            </View>
          </Card>

          <Card containerStyle={styles.card}>
            <Text style={styles.sectionTitle}>Savings Breakdown</Text>
            <PieChart
              data={getChartData()}
              width={width - 60}
              height={220}
              chartConfig={{
                color: (opacity = 1) => `rgba(102, 126, 234, ${opacity})`,
              }}
              accessor="population"
              backgroundColor="transparent"
              paddingLeft="15"
              absolute
            />
          </Card>
        </>
      )}
    </ScrollView>
  );
};

