import React, { useState } from 'react';
import {
  View,
  StyleSheet,
  ScrollView,
  KeyboardAvoidingView,
  Platform,
  Alert,
  TouchableOpacity,
} from 'react-native';
import { Text, Input, Button, Icon } from 'react-native-elements';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../types/navigation';
import { theme } from '../theme/theme';
import { LinearGradient } from 'expo-linear-gradient';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';

type ContactUsScreenProps = {
  navigation: StackNavigationProp<RootStackParamList, 'ContactUs'>;
};

interface SubjectCategory {
  id: string;
  label: string;
  icon: string;
}

const SUBJECT_CATEGORIES: SubjectCategory[] = [
  { id: 'general', label: 'General Inquiry', icon: 'help-outline' },
  { id: 'technical', label: 'Technical Support', icon: 'build' },
  { id: 'account', label: 'Account Issue', icon: 'person' },
  { id: 'billing', label: 'Billing Question', icon: 'payment' },
  { id: 'feature', label: 'Feature Request', icon: 'lightbulb-outline' },
  { id: 'other', label: 'Other', icon: 'more-horiz' },
];

const ContactUsScreen: React.FC<ContactUsScreenProps> = ({ navigation }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [selectedSubject, setSelectedSubject] = useState<SubjectCategory | null>(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const [errors, setErrors] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  const validateForm = (): boolean => {
    const newErrors = {
      name: '',
      email: '',
      subject: '',
      message: '',
    };

    let isValid = true;

    if (!name.trim()) {
      newErrors.name = 'Name is required';
      isValid = false;
    }

    if (!email.trim()) {
      newErrors.email = 'Email is required';
      isValid = false;
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      newErrors.email = 'Invalid email format';
      isValid = false;
    }

    if (!selectedSubject) {
      newErrors.subject = 'Please select a subject';
      isValid = false;
    }

    if (!message.trim()) {
      newErrors.message = 'Message is required';
      isValid = false;
    } else if (message.trim().length < 10) {
      newErrors.message = 'Message must be at least 10 characters';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = async () => {
    if (!validateForm()) {
      return;
    }

    try {
      setLoading(true);
      const token = await AsyncStorage.getItem('authToken');

      await axios.post(
        'http://localhost:3000/api/settings/contact',
        {
          name: name.trim(),
          email: email.trim(),
          subject: selectedSubject!.label,
          message: message.trim(),
        },
        token ? { headers: { Authorization: `Bearer ${token}` } } : {}
      );

      Alert.alert(
        'Message Sent',
        'Thank you for contacting us. We will get back to you within 24-48 hours.',
        [
          {
            text: 'OK',
            onPress: () => navigation.goBack(),
          },
        ]
      );
    } catch (error: any) {
      console.error('Error submitting contact form:', error);
      Alert.alert('Error', error.response?.data?.error || 'Failed to send message. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <KeyboardAvoidingView
      style={styles.container}
      behavior={Platform.OS === 'ios' ? 'padding' : undefined}
    >
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        <LinearGradient
          colors={['#667eea', '#764ba2']}
          start={{ x: 0, y: 0 }}
          end={{ x: 1, y: 1 }}
          style={styles.header}
        >
          <View style={styles.iconContainer}>
            <Icon name="email" type="material" color="white" size={60} />
          </View>
          <Text style={styles.headerTitle}>Contact Us</Text>
          <Text style={styles.headerSubtitle}>We're here to help you</Text>
        </LinearGradient>

        <View style={styles.formContainer}>
          <View style={styles.infoCard}>
            <Icon
              name="info"
              type="material"
              color={theme.colors.primary}
              size={20}
              containerStyle={{ marginRight: 10 }}
            />
            <Text style={styles.infoText}>
              Response time: 24-48 hours
            </Text>
          </View>

          <Input
            label="Your Name"
            placeholder="Enter your full name"
            value={name}
            onChangeText={(text) => {
              setName(text);
              setErrors({ ...errors, name: '' });
            }}
            leftIcon={<Icon name="person" type="material" size={24} color={theme.colors.textSecondary} />}
            errorMessage={errors.name}
            containerStyle={styles.inputContainer}
            labelStyle={styles.inputLabel}
            inputStyle={styles.input}
          />

          <Input
            label="Email Address"
            placeholder="your.email@example.com"
            value={email}
            onChangeText={(text) => {
              setEmail(text);
              setErrors({ ...errors, email: '' });
            }}
            keyboardType="email-address"
            autoCapitalize="none"
            leftIcon={<Icon name="email" type="material" size={24} color={theme.colors.textSecondary} />}
            errorMessage={errors.email}
            containerStyle={styles.inputContainer}
            labelStyle={styles.inputLabel}
            inputStyle={styles.input}
          />

          <View style={styles.subjectContainer}>
            <Text style={styles.subjectLabel}>Subject Category</Text>
            <View style={styles.categoryGrid}>
              {SUBJECT_CATEGORIES.map((category) => (
                <TouchableOpacity
                  key={category.id}
                  style={[
                    styles.categoryCard,
                    selectedSubject?.id === category.id && styles.categoryCardSelected,
                  ]}
                  onPress={() => {
                    setSelectedSubject(category);
                    setErrors({ ...errors, subject: '' });
                  }}
                >
                  <Icon
                    name={category.icon}
                    type="material"
                    size={28}
                    color={
                      selectedSubject?.id === category.id
                        ? theme.colors.primary
                        : theme.colors.textSecondary
                    }
                  />
                  <Text
                    style={[
                      styles.categoryText,
                      selectedSubject?.id === category.id && styles.categoryTextSelected,
                    ]}
                  >
                    {category.label}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
            {errors.subject ? (
              <Text style={styles.errorText}>{errors.subject}</Text>
            ) : null}
          </View>

          <View style={styles.messageContainer}>
            <Text style={styles.messageLabel}>Message</Text>
            <View style={styles.textAreaContainer}>
              <Input
                placeholder="Describe your issue or question in detail..."
                value={message}
                onChangeText={(text) => {
                  setMessage(text);
                  setErrors({ ...errors, message: '' });
                }}
                multiline
                numberOfLines={6}
                textAlignVertical="top"
                errorMessage={errors.message}
                containerStyle={styles.textAreaInputContainer}
                inputContainerStyle={styles.textAreaInput}
                inputStyle={styles.textAreaText}
              />
              <View style={styles.characterCount}>
                <Text style={styles.characterCountText}>
                  {message.length} characters
                </Text>
              </View>
            </View>
          </View>

          <View style={styles.contactMethods}>
            <Text style={styles.contactMethodsTitle}>Other Ways to Reach Us</Text>
            <View style={styles.contactMethod}>
              <Icon name="phone" type="material" size={20} color={theme.colors.primary} />
              <Text style={styles.contactMethodText}>1-800-MEDIMIND</Text>
            </View>
            <View style={styles.contactMethod}>
              <Icon name="email" type="material" size={20} color={theme.colors.primary} />
              <Text style={styles.contactMethodText}>support@medimind.com</Text>
            </View>
            <View style={styles.contactMethod}>
              <Icon name="schedule" type="material" size={20} color={theme.colors.primary} />
              <Text style={styles.contactMethodText}>Mon-Fri: 9AM - 5PM EST</Text>
            </View>
          </View>

          <View style={styles.buttonContainer}>
            <Button
              title={loading ? 'Sending...' : 'Send Message'}
              onPress={handleSubmit}
              disabled={loading}
              loading={loading}
              buttonStyle={styles.sendButton}
              containerStyle={styles.sendButtonContainer}
              icon={
                !loading && (
                  <Icon
                    name="send"
                    type="material"
                    color="white"
                    size={20}
                    containerStyle={{ marginRight: 10 }}
                  />
                )
              }
            />

            <Button
              title="Cancel"
              onPress={() => navigation.goBack()}
              type="outline"
              disabled={loading}
              buttonStyle={styles.cancelButton}
              titleStyle={styles.cancelButtonTitle}
              containerStyle={styles.cancelButtonContainer}
            />
          </View>
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
};

