/**
 * Biological Age Calculator Screen
 *
 * Calculate your biological age using advanced algorithms
 * Part of Longevity Optimization Platform
 * Revenue: $120M Year 1 (high-value demographic)
 */

import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  Dimensions,
  ActivityIndicator,
  Alert,
  Animated,
} from 'react-native';
import { ProgressChart } from 'react-native-chart-kit';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import { useNavigation } from '@react-navigation/native';
import { longevityAPI } from '../services/revolutionaryFeaturesAPI';
import { authService } from '../services/authService';
import { LinearGradient } from 'expo-linear-gradient';
import { theme } from '../theme/theme';

const { width } = Dimensions.get('window');

export default function BiologicalAgeScreen() {
  const navigation = useNavigation();
  const [loading, setLoading] = useState(false);
  const [biologicalAge, setBiologicalAge] = useState<any>(null);
  const [hallmarksData, setHallmarksData] = useState<any>(null);
  const [animatedValue] = useState(new Animated.Value(0));

  useEffect(() => {
    loadBiologicalAge();
  }, []);

  useEffect(() => {
    if (biologicalAge) {
      Animated.spring(animatedValue, {
        toValue: 1,
        useNativeDriver: true,
      }).start();
    }
  }, [biologicalAge]);

  const loadBiologicalAge = async () => {
    try {
      setLoading(true);
      const user = await authService.getCurrentUser();
      const userId = user.id;

      const [ageResponse, hallmarksResponse] = await Promise.all([
        longevityAPI.calculateBiologicalAge(userId),
        longevityAPI.getAgingHallmarks(userId),
      ]);

      setBiologicalAge(ageResponse.data || ageResponse);
      setHallmarksData(hallmarksResponse.data || hallmarksResponse);
    } catch (error) {
      console.error('Error loading biological age:', error);
      Alert.alert('Error', 'Failed to load biological age. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const calculateBiologicalAge = async () => {
    try {
      setLoading(true);
      const user = await authService.getCurrentUser();
      const userId = user.id;

      const response = await longevityAPI.calculateBiologicalAge(userId);

      setBiologicalAge(response.data || response);
      Alert.alert('Success', 'Your biological age has been calculated!');
    } catch (error) {
      console.error('Error calculating biological age:', error);
      Alert.alert('Error', 'Failed to calculate biological age');
    } finally {
      setLoading(false);
    }
  };

  const renderAgeComparison = () => {
    if (!biologicalAge) return null;

    const ageDifference = biologicalAge.chronologicalAge - biologicalAge.biologicalAge;
    const isYounger = ageDifference > 0;
    const percentile = biologicalAge.percentile;

    const scale = animatedValue.interpolate({
      inputRange: [0, 1],
      outputRange: [0.8, 1],
    });

    return (
      <Animated.View style={[styles.ageCard, { transform: [{ scale }] }]}>
        <View style={styles.ageHeader}>
          <Text style={styles.ageCardTitle}>Your Biological Age</Text>
          {isYounger && <Icon name="trophy" size={28} color="#FFD700" />}
        </View>

        <View style={styles.ageComparisonRow}>
          <View style={styles.ageColumn}>
            <Text style={styles.ageLabel}>Chronological</Text>
            <Text style={styles.chronologicalAge}>{biologicalAge.chronologicalAge}</Text>
          </View>

          <View style={styles.ageDivider}>
            <Icon
              name={isYounger ? 'arrow-left-bold' : 'arrow-right-bold'}
              size={40}
              color={isYounger ? '#4CAF50' : '#F44336'}
            />
          </View>

          <View style={styles.ageColumn}>
            <Text style={styles.ageLabel}>Biological</Text>
            <Text style={[
              styles.biologicalAge,
              { color: isYounger ? '#4CAF50' : '#F44336' }
            ]}>
              {biologicalAge.biologicalAge.toFixed(1)}
            </Text>
          </View>
        </View>

        <View style={styles.ageDifferenceCard}>
          {isYounger ? (
            <>
              <Icon name="emoticon-excited" size={32} color="#4CAF50" />
              <Text style={styles.ageDifferenceGood}>
                You're {Math.abs(ageDifference).toFixed(1)} years YOUNGER biologically!
              </Text>
            </>
          ) : (
            <>
              <Icon name="alert-circle" size={32} color="#F44336" />
              <Text style={styles.ageDifferenceBad}>
                You're {Math.abs(ageDifference).toFixed(1)} years OLDER biologically
              </Text>
            </>
          )}
        </View>

        <View style={styles.percentileCard}>
          <Text style={styles.percentileText}>
            You're in the <Text style={styles.percentileBold}>{percentile}th percentile</Text>
          </Text>
          <Text style={styles.percentileSubtext}>
            {percentile > 75
              ? 'Better than most people your age!'
              : percentile > 50
              ? 'Average for your age group'
              : 'Room for improvement with our interventions'}
          </Text>
        </View>

        {/* Aging Pace */}
        <View style={styles.agingPaceCard}>
          <Text style={styles.subsectionTitle}>Aging Pace</Text>
          <View style={styles.agingPaceRow}>
            <View style={styles.agingPaceIndicator}>
              <Text style={styles.agingPaceValue}>
                {biologicalAge.agingPace.toFixed(2)}
              </Text>
              <Text style={styles.agingPaceLabel}>years per year</Text>
            </View>
            <View style={styles.agingPaceInfo}>
              <Text style={styles.agingPaceText}>
                {biologicalAge.agingPace < 1.0
                  ? 'üéâ You\'re aging slower than calendar time!'
                  : biologicalAge.agingPace === 1.0
                  ? '‚è±Ô∏è You\'re aging at normal pace'
                  : '‚ö†Ô∏è You\'re aging faster than calendar time'}
              </Text>
            </View>
          </View>
        </View>

        {/* Lifespan Prediction */}
        <View style={styles.lifespanCard}>
          <Text style={styles.subsectionTitle}>Lifespan Prediction</Text>
          <View style={styles.lifespanRow}>
            <View style={styles.lifespanBox}>
              <Icon name="calendar-range" size={24} color="#2196F3" />
              <Text style={styles.lifespanValue}>
                {biologicalAge.lifespanPrediction.predictedLifespan}
              </Text>
              <Text style={styles.lifespanLabel}>years</Text>
            </View>
            <View style={styles.lifespanBox}>
              <Icon name="heart-pulse" size={24} color="#E91E63" />
              <Text style={styles.lifespanValue}>
                {biologicalAge.lifespanPrediction.healthspan}
              </Text>
              <Text style={styles.lifespanLabel}>healthy years</Text>
            </View>
          </View>
          <Text style={styles.confidenceText}>
            Confidence interval: {biologicalAge.lifespanPrediction.confidenceInterval.lower} - {biologicalAge.lifespanPrediction.confidenceInterval.upper} years
          </Text>
        </View>
      </Animated.View>
    );
  };

  const renderAlgorithms = () => {
    if (!biologicalAge || !biologicalAge.algorithms) return null;

    return (
      <View style={styles.algorithmsCard}>
        <Text style={styles.sectionTitle}>CALCULATION ALGORITHMS</Text>
        <Text style={styles.algorithmsSubtitle}>
          We use 4 state-of-the-art algorithms for maximum accuracy
        </Text>

        {Object.entries(biologicalAge.algorithms).map(([name, data]: [string, any]) => (
          <View key={name} style={styles.algorithmItem}>
            <View style={styles.algorithmHeader}>
              <Text style={styles.algorithmName}>{name.toUpperCase()}</Text>
              <Text style={styles.algorithmAge}>{data.age.toFixed(1)} years</Text>
            </View>
            <Text style={styles.algorithmBasis}>{data.basis}</Text>
            <View style={styles.algorithmBar}>
              <View
                style={[
                  styles.algorithmBarFill,
                  {
                    width: `${(data.age / biologicalAge.chronologicalAge) * 100}%`,
                    backgroundColor: data.age < biologicalAge.chronologicalAge ? '#4CAF50' : '#F44336',
                  },
                ]}
              />
            </View>
          </View>
        ))}
      </View>
    );
  };

  const renderHallmarksOfAging = () => {
    if (!hallmarksData) return null;

    const primaryHallmarks = hallmarksData.hallmarks.filter((h: any) => h.category === 'Primary');
    const antagonisticHallmarks = hallmarksData.hallmarks.filter((h: any) => h.category === 'Antagonistic');
    const integrativeHallmarks = hallmarksData.hallmarks.filter((h: any) => h.category === 'Integrative');

    const renderHallmarkCategory = (title: string, hallmarks: any[], color: string) => (
      <View style={styles.hallmarkCategory}>
        <Text style={[styles.hallmarkCategoryTitle, { color }]}>{title}</Text>
        {hallmarks.map((hallmark: any, index: number) => (
          <TouchableOpacity
            key={index}
            style={styles.hallmarkItem}
            onPress={() => showHallmarkDetails(hallmark)}
          >
            <View style={styles.hallmarkInfo}>
              <Text style={styles.hallmarkName}>{hallmark.name}</Text>
              <Text style={styles.hallmarkMarkers}>
                {hallmark.markers.join(' ‚Ä¢ ')}
              </Text>
            </View>
            <View style={styles.hallmarkScore}>
              <Text style={[
                styles.hallmarkScoreText,
                { color: hallmark.score >= 7 ? '#4CAF50' : hallmark.score >= 5 ? '#FFC107' : '#F44336' }
              ]}>
                {hallmark.score.toFixed(1)}
              </Text>
              <Text style={styles.hallmarkScoreLabel}>/10</Text>
            </View>
          </TouchableOpacity>
        ))}
      </View>
    );

    return (
      <View style={styles.hallmarksCard}>
        <Text style={styles.sectionTitle}>12 HALLMARKS OF AGING</Text>
        <Text style={styles.hallmarksSubtitle}>
          Based on L√≥pez-Ot√≠n et al. Cell 2013 & 2023 update
        </Text>

        <View style={styles.overallScoreCard}>
          <Text style={styles.overallScoreLabel}>Overall Aging Score</Text>
          <Text style={[
            styles.overallScoreValue,
            { color: hallmarksData.overallScore >= 7 ? '#4CAF50' : hallmarksData.overallScore >= 5 ? '#FFC107' : '#F44336' }
          ]}>
            {hallmarksData.overallScore.toFixed(1)}/10
          </Text>
        </View>

        {renderHallmarkCategory('Primary Hallmarks', primaryHallmarks, '#E91E63')}
        {renderHallmarkCategory('Antagonistic Hallmarks', antagonisticHallmarks, '#FF9800')}
        {renderHallmarkCategory('Integrative Hallmarks', integrativeHallmarks, '#9C27B0')}
      </View>
    );
  };

  const showHallmarkDetails = (hallmark: any) => {
    Alert.alert(
      hallmark.name,
      `Markers: ${hallmark.markers.join(', ')}\n\nRecommended Interventions:\n${hallmark.interventions.join('\n‚Ä¢ ')}`,
      [{ text: 'View Interventions', onPress: () => navigation.navigate('LongevityInterventions' as never) }]
    );
  };

  const renderCuttingEdgeTherapies = () => {
    return (
      <View style={styles.therapiesCard}>
        <Text style={styles.sectionTitle}>CUTTING-EDGE THERAPIES</Text>
        <Text style={styles.therapiesSubtitle}>
          Access the latest longevity interventions
        </Text>

        <View style={styles.therapyButtons}>
          <TouchableOpacity
            style={styles.therapyButton}
            onPress={() => navigation.navigate('LongevityTherapies' as never)}
          >
            <Icon name="pill" size={32} color="#2196F3" />
            <Text style={styles.therapyButtonTitle}>Senolytics</Text>
            <Text style={styles.therapyButtonSubtitle}>Clear senescent cells</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.therapyButton}
            onPress={() => navigation.navigate('LongevityTherapies' as never)}
          >
            <Icon name="atom" size={32} color="#4CAF50" />
            <Text style={styles.therapyButtonTitle}>NAD+ Boosters</Text>
            <Text style={styles.therapyButtonSubtitle}>Cellular energy</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.therapyButton}
            onPress={() => navigation.navigate('LongevityTherapies' as never)}
          >
            <Icon name="dna" size={32} color="#9C27B0" />
            <Text style={styles.therapyButtonTitle}>Rapamycin</Text>
            <Text style={styles.therapyButtonSubtitle}>mTOR inhibition</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.therapyButton}
            onPress={() => navigation.navigate('LongevityTherapies' as never)}
          >
            <Icon name="heart-pulse" size={32} color="#E91E63" />
            <Text style={styles.therapyButtonTitle}>Young Plasma</Text>
            <Text style={styles.therapyButtonSubtitle}>Rejuvenation factors</Text>
          </TouchableOpacity>
        </View>

        <TouchableOpacity
          style={styles.viewAllButton}
          onPress={() => navigation.navigate('LongevityTherapies' as never)}
        >
          <Text style={styles.viewAllButtonText}>View All 10+ Therapies</Text>
          <Icon name="arrow-right" size={20} color="#2196F3" />
        </TouchableOpacity>
      </View>
    );
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#2196F3" />
        <Text style={styles.loadingText}>Calculating your biological age...</Text>
      </View>
    );
  }

  if (!biologicalAge) {
    return (
      <View style={styles.container}>
        <ScrollView contentContainerStyle={styles.emptyContainer}>
          <Icon name="chart-line" size={120} color="#CCCCCC" />
          <Text style={styles.emptyTitle}>Discover Your Biological Age</Text>
          <Text style={styles.emptyDescription}>
            Find out how old you really are at the cellular level.
            Our advanced algorithms use 4 different methods to calculate your true biological age.
          </Text>

          <View style={styles.featuresContainer}>
            <View style={styles.featureItem}>
              <Icon name="check-circle" size={24} color="#4CAF50" />
              <Text style={styles.featureText}>4 validation algorithms</Text>
            </View>
            <View style={styles.featureItem}>
              <Icon name="check-circle" size={24} color="#4CAF50" />
              <Text style={styles.featureText}>12 Hallmarks of Aging assessment</Text>
            </View>
            <View style={styles.featureItem}>
              <Icon name="check-circle" size={24} color="#4CAF50" />
              <Text style={styles.featureText}>Lifespan prediction</Text>
            </View>
            <View style={styles.featureItem}>
              <Icon name="check-circle" size={24} color="#4CAF50" />
              <Text style={styles.featureText}>Personalized interventions</Text>
            </View>
          </View>

          <TouchableOpacity style={styles.calculateButton} onPress={calculateBiologicalAge}>
            <Text style={styles.calculateButtonText}>Calculate My Biological Age</Text>
            <Icon name="arrow-right" size={24} color="#fff" />
          </TouchableOpacity>

          <Text style={styles.pricingText}>
            $99 one-time ‚Ä¢ Part of Longevity Optimization ($200/month)
          </Text>
        </ScrollView>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <LinearGradient
        colors={theme.gradients.primary.colors}
        start={theme.gradients.primary.start}
        end={theme.gradients.primary.end}
        style={styles.gradientHeader}
      >
        <Text style={styles.gradientHeaderTitle}>Biological Age Results</Text>
        <Text style={styles.gradientHeaderSubtitle}>Your Cellular Health Analysis</Text>
      </LinearGradient>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        {renderAgeComparison()}
        {renderAlgorithms()}
        {renderHallmarksOfAging()}
        {renderCuttingEdgeTherapies()}

        <View style={styles.actionsCard}>
          <TouchableOpacity
            style={styles.actionButton}
            onPress={() => navigation.navigate('LongevityProfile' as never)}
          >
            <Icon name="account-details" size={24} color="#fff" />
            <Text style={styles.actionButtonText}>Full Longevity Profile</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={[styles.actionButton, styles.secondaryButton]}
            onPress={() => loadBiologicalAge()}
          >
            <Icon name="refresh" size={24} color="#2196F3" />
            <Text style={[styles.actionButtonText, styles.secondaryButtonText]}>
              Recalculate
            </Text>
          </TouchableOpacity>
        </View>
      </ScrollView>
    </View>
  );
}

