/**
 * Billing & Subscription Management Screen
 *
 * Modern UI for managing subscriptions, viewing invoices, and updating payment methods
 * Design: Purple gradient theme (#9333ea â†’ #7c3aed) with glass morphism cards
 */

import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  Dimensions,
  Platform
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { theme } from '../theme/theme';

const { width } = Dimensions.get('window');

interface SubscriptionPlan {
  id: string;
  name: string;
  description: string;
  price: number;
  interval: 'month' | 'year';
  features: string[];
  stripePriceId: string;
}

interface Subscription {
  id: string;
  planId: string;
  status: 'active' | 'trialing' | 'past_due' | 'canceled' | 'incomplete';
  currentPeriodEnd: string;
  cancelAtPeriodEnd: boolean;
}

interface Invoice {
  id: string;
  amount: number;
  status: 'paid' | 'open' | 'draft' | 'void' | 'uncollectible';
  dueDate: string;
  paidAt?: string;
  invoiceUrl: string;
  invoicePdf: string;
}

export default function BillingScreen({ navigation }: any) {
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<'plans' | 'subscription' | 'invoices'>('plans');
  const [plans, setPlans] = useState<SubscriptionPlan[]>([]);
  const [currentSubscription, setCurrentSubscription] = useState<Subscription | null>(null);
  const [invoices, setInvoices] = useState<Invoice[]>([]);
  const [processingPlanId, setProcessingPlanId] = useState<string | null>(null);

  useEffect(() => {
    fetchBillingData();
  }, []);

  const fetchBillingData = async () => {
    try {
      const token = await AsyncStorage.getItem('authToken');
      const API_URL = process.env.EXPO_PUBLIC_API_URL || 'http://localhost:5000';

      // Fetch plans
      const plansResponse = await fetch(`${API_URL}/api/billing/plans`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const plansData = await plansResponse.json();
      if (plansData.success) {
        setPlans(plansData.data);
      }

      // Fetch current subscription (mock for now)
      setCurrentSubscription({
        id: 'sub_123',
        planId: 'basic',
        status: 'active',
        currentPeriodEnd: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString(),
        cancelAtPeriodEnd: false
      });

      // Fetch invoices (mock for now)
      setInvoices([
        {
          id: 'inv_1',
          amount: 2900,
          status: 'paid',
          dueDate: new Date().toISOString(),
          paidAt: new Date().toISOString(),
          invoiceUrl: 'https://stripe.com/invoice/1',
          invoicePdf: 'https://stripe.com/invoice/1.pdf'
        },
        {
          id: 'inv_2',
          amount: 2900,
          status: 'paid',
          dueDate: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString(),
          paidAt: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString(),
          invoiceUrl: 'https://stripe.com/invoice/2',
          invoicePdf: 'https://stripe.com/invoice/2.pdf'
        }
      ]);

      setLoading(false);
    } catch (error) {
      console.error('Error fetching billing data:', error);
      setLoading(false);
    }
  };

  const handleSubscribe = async (planId: string) => {
    try {
      setProcessingPlanId(planId);

      // In production: Show Stripe payment sheet
      Alert.alert(
        'Subscribe to Plan',
        'This will open Stripe payment form. In production, integrate @stripe/stripe-react-native',
        [
          { text: 'Cancel', style: 'cancel' },
          {
            text: 'Continue',
            onPress: async () => {
              // Mock subscription creation
              await new Promise(r => setTimeout(r, 1500));

              setCurrentSubscription({
                id: 'sub_new',
                planId,
                status: 'active',
                currentPeriodEnd: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString(),
                cancelAtPeriodEnd: false
              });

              Alert.alert('Success', 'Subscription activated!');
              setActiveTab('subscription');
            }
          }
        ]
      );
    } catch (error) {
      console.error('Subscription error:', error);
      Alert.alert('Error', 'Failed to process subscription');
    } finally {
      setProcessingPlanId(null);
    }
  };

  const handleChangePlan = async (newPlanId: string) => {
    Alert.alert(
      'Change Plan',
      'Are you sure you want to change your subscription plan? Changes will be prorated.',
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Change Plan',
          onPress: async () => {
            try {
              setProcessingPlanId(newPlanId);
              await new Promise(r => setTimeout(r, 1000));

              if (currentSubscription) {
                setCurrentSubscription({ ...currentSubscription, planId: newPlanId });
              }

              Alert.alert('Success', 'Plan changed successfully!');
            } catch (error) {
              Alert.alert('Error', 'Failed to change plan');
            } finally {
              setProcessingPlanId(null);
            }
          }
        }
      ]
    );
  };

  const handleCancelSubscription = () => {
    Alert.alert(
      'Cancel Subscription',
      'Your subscription will remain active until the end of the current billing period.',
      [
        { text: 'Keep Subscription', style: 'cancel' },
        {
          text: 'Cancel',
          style: 'destructive',
          onPress: async () => {
            if (currentSubscription) {
              setCurrentSubscription({ ...currentSubscription, cancelAtPeriodEnd: true });
            }
            Alert.alert('Subscription Canceled', 'You will retain access until the end of your billing period.');
          }
        }
      ]
    );
  };

  const renderPlanCard = (plan: SubscriptionPlan, isCurrentPlan: boolean) => {
    const isProcessing = processingPlanId === plan.id;

    return (
      <View key={plan.id} style={[styles.planCard, isCurrentPlan && styles.currentPlanCard]}>
        {isCurrentPlan && (
          <View style={styles.currentPlanBadge}>
            <Text style={styles.currentPlanBadgeText}>Current Plan</Text>
          </View>
        )}

        <Text style={styles.planName}>{plan.name}</Text>
        <Text style={styles.planDescription}>{plan.description}</Text>

        <View style={styles.priceContainer}>
          <Text style={styles.priceAmount}>${(plan.price / 100).toFixed(2)}</Text>
          <Text style={styles.priceInterval}>/{plan.interval}</Text>
        </View>

        <View style={styles.featuresContainer}>
          {plan.features.map((feature, index) => (
            <View key={index} style={styles.featureRow}>
              <Ionicons name="checkmark-circle" size={20} color="#10b981" />
              <Text style={styles.featureText}>{feature}</Text>
            </View>
          ))}
        </View>

        <TouchableOpacity
          style={[
            styles.planButton,
            isCurrentPlan && styles.planButtonCurrent,
            isProcessing && styles.planButtonProcessing
          ]}
          onPress={() => {
            if (isCurrentPlan) {
              // Already subscribed
              return;
            } else if (currentSubscription) {
              handleChangePlan(plan.id);
            } else {
              handleSubscribe(plan.id);
            }
          }}
          disabled={isProcessing}
        >
          {isProcessing ? (
            <ActivityIndicator color="#fff" />
          ) : (
            <Text style={styles.planButtonText}>
              {isCurrentPlan ? 'Current Plan' : currentSubscription ? 'Switch Plan' : 'Subscribe'}
            </Text>
          )}
        </TouchableOpacity>
      </View>
    );
  };

  const renderSubscriptionTab = () => {
    if (!currentSubscription) {
      return (
        <View style={styles.emptyContainer}>
          <Ionicons name="card-outline" size={64} color="#9333ea" />
          <Text style={styles.emptyText}>No active subscription</Text>
          <Text style={styles.emptySubtext}>Choose a plan to get started</Text>
          <TouchableOpacity
            style={styles.emptyButton}
            onPress={() => setActiveTab('plans')}
          >
            <Text style={styles.emptyButtonText}>View Plans</Text>
          </TouchableOpacity>
        </View>
      );
    }

    const currentPlan = plans.find(p => p.id === currentSubscription.planId);
    const statusColors: Record<string, string> = {
      active: '#10b981',
      trialing: '#3b82f6',
      past_due: '#f59e0b',
      canceled: '#ef4444',
      incomplete: '#6b7280'
    };

    return (
      <View style={styles.subscriptionContainer}>
        <View style={styles.subscriptionCard}>
          <View style={styles.subscriptionHeader}>
            <Text style={styles.subscriptionTitle}>{currentPlan?.name}</Text>
            <View style={[styles.statusBadge, { backgroundColor: statusColors[currentSubscription.status] }]}>
              <Text style={styles.statusBadgeText}>
                {currentSubscription.status.toUpperCase()}
              </Text>
            </View>
          </View>

          <View style={styles.subscriptionDetail}>
            <Ionicons name="calendar-outline" size={20} color="#6b7280" />
            <Text style={styles.subscriptionDetailText}>
              Renews on {new Date(currentSubscription.currentPeriodEnd).toLocaleDateString()}
            </Text>
          </View>

          <View style={styles.subscriptionDetail}>
            <Ionicons name="cash-outline" size={20} color="#6b7280" />
            <Text style={styles.subscriptionDetailText}>
              ${((currentPlan?.price || 0) / 100).toFixed(2)}/{currentPlan?.interval}
            </Text>
          </View>

          {currentSubscription.cancelAtPeriodEnd && (
            <View style={styles.cancelWarning}>
              <Ionicons name="warning-outline" size={20} color="#f59e0b" />
              <Text style={styles.cancelWarningText}>
                Subscription will cancel on {new Date(currentSubscription.currentPeriodEnd).toLocaleDateString()}
              </Text>
            </View>
          )}

          <View style={styles.subscriptionActions}>
            <TouchableOpacity
              style={[styles.actionButton, styles.actionButtonSecondary]}
              onPress={() => setActiveTab('plans')}
            >
              <Text style={styles.actionButtonTextSecondary}>Change Plan</Text>
            </TouchableOpacity>

            {!currentSubscription.cancelAtPeriodEnd && (
              <TouchableOpacity
                style={[styles.actionButton, styles.actionButtonDanger]}
                onPress={handleCancelSubscription}
              >
                <Text style={styles.actionButtonText}>Cancel</Text>
              </TouchableOpacity>
            )}
          </View>
        </View>
      </View>
    );
  };

  const renderInvoicesTab = () => {
    if (invoices.length === 0) {
      return (
        <View style={styles.emptyContainer}>
          <Ionicons name="document-text-outline" size={64} color="#9333ea" />
          <Text style={styles.emptyText}>No invoices yet</Text>
          <Text style={styles.emptySubtext}>Your billing history will appear here</Text>
        </View>
      );
    }

    return (
      <View style={styles.invoicesContainer}>
        {invoices.map((invoice) => (
          <View key={invoice.id} style={styles.invoiceCard}>
            <View style={styles.invoiceHeader}>
              <View>
                <Text style={styles.invoiceAmount}>
                  ${(invoice.amount / 100).toFixed(2)}
                </Text>
                <Text style={styles.invoiceDate}>
                  {new Date(invoice.dueDate).toLocaleDateString()}
                </Text>
              </View>

              <View style={[
                styles.invoiceStatusBadge,
                { backgroundColor: invoice.status === 'paid' ? '#10b981' : '#6b7280' }
              ]}>
                <Text style={styles.invoiceStatusText}>
                  {invoice.status.toUpperCase()}
                </Text>
              </View>
            </View>

            <TouchableOpacity
              style={styles.downloadButton}
              onPress={() => {
                // In production: Open invoice PDF
                Alert.alert('Download Invoice', 'Opening invoice PDF...');
              }}
            >
              <Ionicons name="download-outline" size={20} color="#9333ea" />
              <Text style={styles.downloadButtonText}>Download PDF</Text>
            </TouchableOpacity>
          </View>
        ))}
      </View>
    );
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#9333ea" />
        <Text style={styles.loadingText}>Loading billing information...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <LinearGradient
        colors={theme.gradients.primary.colors}
        style={styles.header}
      >
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}
        >
          <Ionicons name="arrow-back" size={24} color="#fff" />
        </TouchableOpacity>

        <Text style={styles.headerTitle}>Billing & Subscriptions</Text>
        <Text style={styles.headerSubtitle}>Manage your account and payments</Text>
      </LinearGradient>

      <View style={styles.tabBar}>
        <TouchableOpacity
          style={[styles.tab, activeTab === 'plans' && styles.tabActive]}
          onPress={() => setActiveTab('plans')}
        >
          <Ionicons
            name="pricetag"
            size={20}
            color={activeTab === 'plans' ? '#9333ea' : '#6b7280'}
          />
          <Text style={[styles.tabText, activeTab === 'plans' && styles.tabTextActive]}>
            Plans
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.tab, activeTab === 'subscription' && styles.tabActive]}
          onPress={() => setActiveTab('subscription')}
        >
          <Ionicons
            name="card"
            size={20}
            color={activeTab === 'subscription' ? '#9333ea' : '#6b7280'}
          />
          <Text style={[styles.tabText, activeTab === 'subscription' && styles.tabTextActive]}>
            Subscription
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.tab, activeTab === 'invoices' && styles.tabActive]}
          onPress={() => setActiveTab('invoices')}
        >
          <Ionicons
            name="document-text"
            size={20}
            color={activeTab === 'invoices' ? '#9333ea' : '#6b7280'}
          />
          <Text style={[styles.tabText, activeTab === 'invoices' && styles.tabTextActive]}>
            Invoices
          </Text>
        </TouchableOpacity>
      </View>

      <ScrollView style={styles.content}>
        {activeTab === 'plans' && (
          <View style={styles.plansContainer}>
            {plans.map(plan => renderPlanCard(plan, currentSubscription?.planId === plan.id))}
          </View>
        )}

        {activeTab === 'subscription' && renderSubscriptionTab()}
        {activeTab === 'invoices' && renderInvoicesTab()}
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
