import React, { useState, useEffect } from 'react';
import {
  View,
  ScrollView,
  StyleSheet,
  RefreshControl,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  Modal,
} from 'react-native';
import { Text, Card, Icon, Button, Input } from 'react-native-elements';
import { useTheme } from 'react-native-elements';
import { Picker } from '@react-native-picker/picker';
import providerAPI, { Prescription, PatientSummary } from '../services/providerAPI';

interface PrescriptionManagementScreenProps {
  navigation: any;
  route?: any;
}

const PrescriptionManagementScreen: React.FC<PrescriptionManagementScreenProps> = ({
  navigation,
  route,
}) => {
  const { theme } = useTheme();
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [prescriptions, setPrescriptions] = useState<Prescription[]>([]);
  const [filteredPrescriptions, setFilteredPrescriptions] = useState<Prescription[]>([]);
  const [filter, setFilter] = useState<string>(route?.params?.filter || 'active');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [patients, setPatients] = useState<PatientSummary[]>([]);

  // Create prescription form state
  const [newPrescription, setNewPrescription] = useState<Prescription>({
    patientId: route?.params?.patientId || '',
    medication: '',
    dosage: '',
    frequency: '',
    route: 'oral',
    duration: '',
    quantity: 0,
    refills: 0,
    instructions: '',
    startDate: new Date().toISOString().split('T')[0],
    status: 'active',
  });

  useEffect(() => {
    loadPrescriptions();
    loadPatients();

    if (route?.params?.action === 'create') {
      setShowCreateModal(true);
    }
  }, []);

  useEffect(() => {
    filterPrescriptions();
  }, [prescriptions, filter]);

  const loadPrescriptions = async () => {
    try {
      setLoading(true);
      const data = await providerAPI.getPrescriptions();
      setPrescriptions(data);
    } catch (error) {
      console.error('[PrescriptionManagement] Error loading:', error);
      Alert.alert('Error', 'Failed to load prescriptions');
    } finally {
      setLoading(false);
    }
  };

  const loadPatients = async () => {
    try {
      const data = await providerAPI.getPatients();
      setPatients(data);
    } catch (error) {
      console.error('[PrescriptionManagement] Error loading patients:', error);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadPrescriptions();
    setRefreshing(false);
  };

  const filterPrescriptions = () => {
    let filtered = [...prescriptions];

    if (filter === 'active') {
      filtered = filtered.filter((rx) => rx.status === 'active');
    } else if (filter === 'pending') {
      filtered = filtered.filter((rx) => rx.status === 'active' && !rx.filledDate);
    } else if (filter === 'completed') {
      filtered = filtered.filter((rx) => rx.status === 'completed');
    } else if (filter === 'cancelled') {
      filtered = filtered.filter((rx) => rx.status === 'cancelled');
    }

    // Sort by prescribedDate (most recent first)
    filtered.sort((a, b) => {
      const dateA = a.prescribedDate ? new Date(a.prescribedDate).getTime() : 0;
      const dateB = b.prescribedDate ? new Date(b.prescribedDate).getTime() : 0;
      return dateB - dateA;
    });

    setFilteredPrescriptions(filtered);
  };

  const handleCreatePrescription = async () => {
    try {
      if (!newPrescription.patientId) {
        Alert.alert('Error', 'Please select a patient');
        return;
      }
      if (!newPrescription.medication) {
        Alert.alert('Error', 'Medication name is required');
        return;
      }
      if (!newPrescription.dosage) {
        Alert.alert('Error', 'Dosage is required');
        return;
      }
      if (!newPrescription.frequency) {
        Alert.alert('Error', 'Frequency is required');
        return;
      }

      await providerAPI.createPrescription(newPrescription);
      Alert.alert('Success', 'Prescription created successfully!');
      setShowCreateModal(false);
      resetForm();
      await loadPrescriptions();
    } catch (error) {
      console.error('[PrescriptionManagement] Error creating:', error);
      Alert.alert('Error', 'Failed to create prescription');
    }
  };

  const handleUpdateStatus = async (prescriptionId: string, status: string) => {
    try {
      await providerAPI.updatePrescription(prescriptionId, { status: status as any });
      Alert.alert('Success', 'Prescription status updated');
      await loadPrescriptions();
    } catch (error) {
      console.error('[PrescriptionManagement] Error updating:', error);
      Alert.alert('Error', 'Failed to update prescription');
    }
  };

  const resetForm = () => {
    setNewPrescription({
      patientId: route?.params?.patientId || '',
      medication: '',
      dosage: '',
      frequency: '',
      route: 'oral',
      duration: '',
      quantity: 0,
      refills: 0,
      instructions: '',
      startDate: new Date().toISOString().split('T')[0],
      status: 'active',
    });
  };

  const getStatusColor = (status: string): string => {
    switch (status) {
      case 'active':
        return '#4CAF50';
      case 'completed':
        return '#9E9E9E';
      case 'cancelled':
        return '#F44336';
      case 'expired':
        return '#FF9800';
      default:
        return '#757575';
    }
  };

  if (loading) {
    return (
      <View style={[styles.container, styles.centered]}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>Loading prescriptions...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-back" type="material" color={theme.colors.primary} />
        </TouchableOpacity>
        <Text h3 style={styles.headerTitle}>Prescriptions</Text>
        <TouchableOpacity onPress={() => setShowCreateModal(true)}>
          <Icon name="add-circle" type="material" color={theme.colors.primary} size={28} />
        </TouchableOpacity>
      </View>

      {/* Filter Chips */}
      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
        style={styles.filterScroll}
        contentContainerStyle={styles.filterContainer}
      >
        <TouchableOpacity
          style={[styles.filterChip, filter === 'active' && styles.filterChipActive]}
          onPress={() => setFilter('active')}
        >
          <Text style={[styles.filterChipText, filter === 'active' && styles.filterChipTextActive]}>
            Active
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterChip, filter === 'pending' && styles.filterChipActive]}
          onPress={() => setFilter('pending')}
        >
          <Text style={[styles.filterChipText, filter === 'pending' && styles.filterChipTextActive]}>
            Pending
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterChip, filter === 'completed' && styles.filterChipActive]}
          onPress={() => setFilter('completed')}
        >
          <Text style={[styles.filterChipText, filter === 'completed' && styles.filterChipTextActive]}>
            Completed
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterChip, filter === 'cancelled' && styles.filterChipActive]}
          onPress={() => setFilter('cancelled')}
        >
          <Text style={[styles.filterChipText, filter === 'cancelled' && styles.filterChipTextActive]}>
            Cancelled
          </Text>
        </TouchableOpacity>
      </ScrollView>

      {/* Prescription List */}
      <ScrollView
        style={styles.listContainer}
        refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
      >
        {filteredPrescriptions.length > 0 ? (
          filteredPrescriptions.map((prescription) => (
            <Card key={prescription.id} containerStyle={styles.prescriptionCard}>
              <View style={styles.prescriptionContent}>
                <View style={styles.prescriptionHeader}>
                  <View style={styles.prescriptionInfo}>
                    <View style={styles.medicationRow}>
                      <Icon name="medication" type="material" size={24} color={theme.colors.primary} />
                      <Text style={styles.medicationName}>{prescription.medication}</Text>
                    </View>

                    <Text style={styles.patientName}>{prescription.patientName}</Text>

                    <View style={styles.prescriptionDetails}>
                      <Text style={styles.detailText}>
                        <Text style={styles.detailLabel}>Dosage: </Text>
                        {prescription.dosage}
                      </Text>
                      <Text style={styles.detailText}>
                        <Text style={styles.detailLabel}>Frequency: </Text>
                        {prescription.frequency}
                      </Text>
                      <Text style={styles.detailText}>
                        <Text style={styles.detailLabel}>Route: </Text>
                        {prescription.route}
                      </Text>
                      <Text style={styles.detailText}>
                        <Text style={styles.detailLabel}>Duration: </Text>
                        {prescription.duration}
                      </Text>
                      <Text style={styles.detailText}>
                        <Text style={styles.detailLabel}>Quantity: </Text>
                        {prescription.quantity} • {prescription.refills} refills
                      </Text>
                      <Text style={styles.detailText}>
                        <Text style={styles.detailLabel}>Start Date: </Text>
                        {new Date(prescription.startDate).toLocaleDateString()}
                      </Text>
                    </View>

                    {prescription.instructions && (
                      <View style={styles.instructionsBox}>
                        <Text style={styles.instructionsLabel}>Instructions:</Text>
                        <Text style={styles.instructionsText}>{prescription.instructions}</Text>
                      </View>
                    )}

                    {prescription.filledDate && (
                      <Text style={styles.filledText}>
                        ✓ Filled on {new Date(prescription.filledDate).toLocaleDateString()}
                      </Text>
                    )}
                  </View>

                  <View style={[styles.statusBadge, { backgroundColor: getStatusColor(prescription.status) }]}>
                    <Text style={styles.statusText}>{prescription.status}</Text>
                  </View>
                </View>

                {/* Action Buttons */}
                <View style={styles.actionButtons}>
                  <TouchableOpacity
                    style={styles.actionButton}
                    onPress={() => navigation.navigate('PatientDetails', { patientId: prescription.patientId })}
                  >
                    <Icon name="person" type="material" size={18} color={theme.colors.primary} />
                    <Text style={styles.actionButtonText}>View Patient</Text>
                  </TouchableOpacity>

                  {prescription.status === 'active' && !prescription.filledDate && (
                    <TouchableOpacity
                      style={styles.actionButton}
                      onPress={() =>
                        handleUpdateStatus(prescription.id!, 'active')
                      }
                    >
                      <Icon name="check-circle" type="material" size={18} color="#4CAF50" />
                      <Text style={styles.actionButtonText}>Mark Filled</Text>
                    </TouchableOpacity>
                  )}

                  {prescription.status === 'active' && (
                    <>
                      <TouchableOpacity
                        style={styles.actionButton}
                        onPress={() => handleUpdateStatus(prescription.id!, 'completed')}
                      >
                        <Icon name="done-all" type="material" size={18} color="#4CAF50" />
                        <Text style={styles.actionButtonText}>Complete</Text>
                      </TouchableOpacity>

                      <TouchableOpacity
                        style={styles.actionButton}
                        onPress={() => handleUpdateStatus(prescription.id!, 'cancelled')}
                      >
                        <Icon name="cancel" type="material" size={18} color="#F44336" />
                        <Text style={styles.actionButtonText}>Cancel</Text>
                      </TouchableOpacity>
                    </>
                  )}
                </View>
              </View>
            </Card>
          ))
        ) : (
          <View style={styles.emptyContainer}>
            <Icon name="medication" type="material" size={64} color="#CCC" />
            <Text style={styles.emptyText}>No prescriptions found</Text>
            <Button
              title="Write Prescription"
              onPress={() => setShowCreateModal(true)}
              containerStyle={styles.emptyButton}
            />
          </View>
        )}
      </ScrollView>

      {/* Create Prescription Modal */}
      <Modal
        visible={showCreateModal}
        animationType="slide"
        presentationStyle="pageSheet"
        onRequestClose={() => setShowCreateModal(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <Text h4>Write Prescription</Text>
            <TouchableOpacity onPress={() => setShowCreateModal(false)}>
              <Icon name="close" type="material" size={28} />
            </TouchableOpacity>
          </View>

          <ScrollView style={styles.modalContent}>
            {/* Patient Selection */}
            {!route?.params?.patientId && (
              <>
                <Text style={styles.inputLabel}>Patient *</Text>
                <View style={styles.pickerContainer}>
                  <Picker
                    selectedValue={newPrescription.patientId}
                    onValueChange={(value) => setNewPrescription({ ...newPrescription, patientId: value })}
                    style={styles.picker}
                  >
                    <Picker.Item label="Select a patient" value="" />
                    {patients.map((patient) => (
                      <Picker.Item
                        key={patient.id}
                        label={`${patient.firstName} ${patient.lastName}`}
                        value={patient.id}
                      />
                    ))}
                  </Picker>
                </View>
              </>
            )}

            {/* Medication */}
            <Input
              label="Medication *"
              value={newPrescription.medication}
              onChangeText={(text) => setNewPrescription({ ...newPrescription, medication: text })}
              placeholder="e.g., Lisinopril"
              leftIcon={<Icon name="medication" type="material" size={20} />}
            />

            {/* Dosage */}
            <Input
              label="Dosage *"
              value={newPrescription.dosage}
              onChangeText={(text) => setNewPrescription({ ...newPrescription, dosage: text })}
              placeholder="e.g., 10mg"
            />

            {/* Frequency */}
            <Input
              label="Frequency *"
              value={newPrescription.frequency}
              onChangeText={(text) => setNewPrescription({ ...newPrescription, frequency: text })}
              placeholder="e.g., Once daily"
            />

            {/* Route */}
            <Text style={styles.inputLabel}>Route of Administration</Text>
            <View style={styles.pickerContainer}>
              <Picker
                selectedValue={newPrescription.route}
                onValueChange={(value) => setNewPrescription({ ...newPrescription, route: value as any })}
                style={styles.picker}
              >
                <Picker.Item label="Oral" value="oral" />
                <Picker.Item label="Topical" value="topical" />
                <Picker.Item label="Injection" value="injection" />
                <Picker.Item label="Inhalation" value="inhalation" />
                <Picker.Item label="Other" value="other" />
              </Picker>
            </View>

            {/* Duration */}
            <Input
              label="Duration"
              value={newPrescription.duration}
              onChangeText={(text) => setNewPrescription({ ...newPrescription, duration: text })}
              placeholder="e.g., 30 days"
            />

            {/* Quantity */}
            <Input
              label="Quantity"
              value={newPrescription.quantity.toString()}
              onChangeText={(text) =>
                setNewPrescription({ ...newPrescription, quantity: parseInt(text) || 0 })
              }
              keyboardType="numeric"
              placeholder="e.g., 30"
            />

            {/* Refills */}
            <Input
              label="Refills"
              value={newPrescription.refills.toString()}
              onChangeText={(text) =>
                setNewPrescription({ ...newPrescription, refills: parseInt(text) || 0 })
              }
              keyboardType="numeric"
              placeholder="e.g., 3"
            />

            {/* Start Date */}
            <Input
              label="Start Date"
              value={newPrescription.startDate}
              onChangeText={(text) => setNewPrescription({ ...newPrescription, startDate: text })}
              placeholder="YYYY-MM-DD"
              leftIcon={<Icon name="calendar-today" type="material" size={20} />}
            />

            {/* Instructions */}
            <Input
              label="Instructions"
              value={newPrescription.instructions}
              onChangeText={(text) => setNewPrescription({ ...newPrescription, instructions: text })}
              placeholder="Special instructions for the patient"
              multiline
              numberOfLines={3}
            />

            <Button
              title="Create Prescription"
              onPress={handleCreatePrescription}
              containerStyle={styles.createButton}
              buttonStyle={{ backgroundColor: theme.colors.primary }}
            />
          </ScrollView>
        </View>
      </Modal>
    </View>
  );
};

