import React, { useState, useEffect } from 'react';
import {
  View,
  ScrollView,
  StyleSheet,
  RefreshControl,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  Modal,
} from 'react-native';
import { Text, Card, Icon, Button, Input } from 'react-native-elements';
import { useTheme } from 'react-native-elements';
import { Picker } from '@react-native-picker/picker';
import providerAPI, { ClinicalNote, PatientSummary } from '../services/providerAPI';

interface ClinicalNotesScreenProps {
  navigation: any;
  route?: any;
}

const ClinicalNotesScreen: React.FC<ClinicalNotesScreenProps> = ({ navigation, route }) => {
  const { theme } = useTheme();
  const [loading, setLoading] = useState(false);
  const [refreshing, setRefreshing] = useState(false);
  const [notes, setNotes] = useState<ClinicalNote[]>([]);
  const [patients, setPatients] = useState<PatientSummary[]>([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedPatientId, setSelectedPatientId] = useState<string>(route?.params?.patientId || '');

  // Create note form state
  const [newNote, setNewNote] = useState<ClinicalNote>({
    patientId: route?.params?.patientId || '',
    noteType: 'progress',
    chiefComplaint: '',
    presentIllness: '',
    assessment: '',
    plan: '',
    vitalSigns: {
      bloodPressure: '',
      heartRate: undefined,
      temperature: undefined,
      respiratoryRate: undefined,
      oxygenSaturation: undefined,
      weight: undefined,
      height: undefined,
    },
    diagnosis: [],
    prescriptions: [],
    labOrders: [],
    referrals: [],
    followUpDate: '',
  });

  const [diagnosisInput, setDiagnosisInput] = useState('');
  const [prescriptionInput, setPrescriptionInput] = useState('');
  const [labOrderInput, setLabOrderInput] = useState('');
  const [referralInput, setReferralInput] = useState('');

  useEffect(() => {
    loadPatients();
    if (route?.params?.action === 'create') {
      setShowCreateModal(true);
    }
    if (route?.params?.patientId) {
      loadPatientNotes(route.params.patientId);
    }
  }, []);

  const loadPatients = async () => {
    try {
      const data = await providerAPI.getPatients();
      setPatients(data);
    } catch (error) {
      console.error('[ClinicalNotes] Error loading patients:', error);
    }
  };

  const loadPatientNotes = async (patientId: string) => {
    try {
      setLoading(true);
      const data = await providerAPI.getPatientNotes(patientId);
      setNotes(data);
    } catch (error) {
      console.error('[ClinicalNotes] Error loading notes:', error);
      Alert.alert('Error', 'Failed to load clinical notes');
    } finally {
      setLoading(false);
    }
  };

  const onRefresh = async () => {
    if (selectedPatientId) {
      setRefreshing(true);
      await loadPatientNotes(selectedPatientId);
      setRefreshing(false);
    }
  };

  const handleCreateNote = async () => {
    try {
      if (!newNote.patientId) {
        Alert.alert('Error', 'Please select a patient');
        return;
      }
      if (!newNote.assessment) {
        Alert.alert('Error', 'Assessment is required');
        return;
      }
      if (!newNote.plan) {
        Alert.alert('Error', 'Plan is required');
        return;
      }

      await providerAPI.createClinicalNote(newNote);
      Alert.alert('Success', 'Clinical note created successfully!');
      setShowCreateModal(false);
      resetForm();
      if (selectedPatientId) {
        await loadPatientNotes(selectedPatientId);
      }
    } catch (error) {
      console.error('[ClinicalNotes] Error creating:', error);
      Alert.alert('Error', 'Failed to create clinical note');
    }
  };

  const resetForm = () => {
    setNewNote({
      patientId: route?.params?.patientId || '',
      noteType: 'progress',
      chiefComplaint: '',
      presentIllness: '',
      assessment: '',
      plan: '',
      vitalSigns: {
        bloodPressure: '',
        heartRate: undefined,
        temperature: undefined,
        respiratoryRate: undefined,
        oxygenSaturation: undefined,
        weight: undefined,
        height: undefined,
      },
      diagnosis: [],
      prescriptions: [],
      labOrders: [],
      referrals: [],
      followUpDate: '',
    });
    setDiagnosisInput('');
    setPrescriptionInput('');
    setLabOrderInput('');
    setReferralInput('');
  };

  const addDiagnosis = () => {
    if (diagnosisInput.trim()) {
      setNewNote({
        ...newNote,
        diagnosis: [...(newNote.diagnosis || []), diagnosisInput.trim()],
      });
      setDiagnosisInput('');
    }
  };

  const removeDiagnosis = (index: number) => {
    const updated = [...(newNote.diagnosis || [])];
    updated.splice(index, 1);
    setNewNote({ ...newNote, diagnosis: updated });
  };

  const addPrescription = () => {
    if (prescriptionInput.trim()) {
      setNewNote({
        ...newNote,
        prescriptions: [...(newNote.prescriptions || []), prescriptionInput.trim()],
      });
      setPrescriptionInput('');
    }
  };

  const removePrescription = (index: number) => {
    const updated = [...(newNote.prescriptions || [])];
    updated.splice(index, 1);
    setNewNote({ ...newNote, prescriptions: updated });
  };

  const addLabOrder = () => {
    if (labOrderInput.trim()) {
      setNewNote({
        ...newNote,
        labOrders: [...(newNote.labOrders || []), labOrderInput.trim()],
      });
      setLabOrderInput('');
    }
  };

  const removeLabOrder = (index: number) => {
    const updated = [...(newNote.labOrders || [])];
    updated.splice(index, 1);
    setNewNote({ ...newNote, labOrders: updated });
  };

  const addReferral = () => {
    if (referralInput.trim()) {
      setNewNote({
        ...newNote,
        referrals: [...(newNote.referrals || []), referralInput.trim()],
      });
      setReferralInput('');
    }
  };

  const removeReferral = (index: number) => {
    const updated = [...(newNote.referrals || [])];
    updated.splice(index, 1);
    setNewNote({ ...newNote, referrals: updated });
  };

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-back" type="material" color={theme.colors.primary} />
        </TouchableOpacity>
        <Text h3 style={styles.headerTitle}>Clinical Notes</Text>
        <TouchableOpacity onPress={() => setShowCreateModal(true)}>
          <Icon name="note-add" type="material" color={theme.colors.primary} size={28} />
        </TouchableOpacity>
      </View>

      {/* Patient Selection */}
      {!route?.params?.patientId && (
        <View style={styles.patientSelector}>
          <Text style={styles.selectorLabel}>Select Patient:</Text>
          <View style={styles.pickerContainer}>
            <Picker
              selectedValue={selectedPatientId}
              onValueChange={(value) => {
                setSelectedPatientId(value);
                if (value) loadPatientNotes(value);
              }}
              style={styles.picker}
            >
              <Picker.Item label="Choose a patient..." value="" />
              {patients.map((patient) => (
                <Picker.Item
                  key={patient.id}
                  label={`${patient.firstName} ${patient.lastName}`}
                  value={patient.id}
                />
              ))}
            </Picker>
          </View>
        </View>
      )}

      {/* Notes List */}
      {loading ? (
        <View style={[styles.container, styles.centered]}>
          <ActivityIndicator size="large" color={theme.colors.primary} />
          <Text style={styles.loadingText}>Loading notes...</Text>
        </View>
      ) : (
        <ScrollView
          style={styles.listContainer}
          refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
        >
          {notes.length > 0 ? (
            notes.map((note, index) => (
              <Card key={index} containerStyle={styles.noteCard}>
                <View style={styles.noteHeader}>
                  <View style={styles.noteTypeContainer}>
                    <Icon name="note" type="material" size={20} color={theme.colors.primary} />
                    <Text style={styles.noteType}>{note.noteType}</Text>
                  </View>
                  <Text style={styles.noteDate}>
                    {note.createdAt && new Date(note.createdAt).toLocaleDateString()}
                  </Text>
                </View>

                {note.chiefComplaint && (
                  <View style={styles.noteSection}>
                    <Text style={styles.noteSectionTitle}>Chief Complaint:</Text>
                    <Text style={styles.noteSectionText}>{note.chiefComplaint}</Text>
                  </View>
                )}

                {note.presentIllness && (
                  <View style={styles.noteSection}>
                    <Text style={styles.noteSectionTitle}>Present Illness:</Text>
                    <Text style={styles.noteSectionText}>{note.presentIllness}</Text>
                  </View>
                )}

                {note.vitalSigns && (
                  <View style={styles.noteSection}>
                    <Text style={styles.noteSectionTitle}>Vital Signs:</Text>
                    <View style={styles.vitalSignsGrid}>
                      {note.vitalSigns.bloodPressure && (
                        <Text style={styles.vitalText}>BP: {note.vitalSigns.bloodPressure}</Text>
                      )}
                      {note.vitalSigns.heartRate && (
                        <Text style={styles.vitalText}>HR: {note.vitalSigns.heartRate} bpm</Text>
                      )}
                      {note.vitalSigns.temperature && (
                        <Text style={styles.vitalText}>Temp: {note.vitalSigns.temperature}Â°F</Text>
                      )}
                    </View>
                  </View>
                )}

                <View style={styles.noteSection}>
                  <Text style={styles.noteSectionTitle}>Assessment:</Text>
                  <Text style={styles.noteSectionText}>{note.assessment}</Text>
                </View>

                <View style={styles.noteSection}>
                  <Text style={styles.noteSectionTitle}>Plan:</Text>
                  <Text style={styles.noteSectionText}>{note.plan}</Text>
                </View>

                {note.diagnosis && note.diagnosis.length > 0 && (
                  <View style={styles.noteSection}>
                    <Text style={styles.noteSectionTitle}>Diagnosis:</Text>
                    {note.diagnosis.map((dx, i) => (
                      <Text key={i} style={styles.listItemText}>â¢ {dx}</Text>
                    ))}
                  </View>
                )}

                {note.followUpDate && (
                  <View style={styles.followUpBadge}>
                    <Icon name="event" type="material" size={16} color="#2196F3" />
                    <Text style={styles.followUpText}>
                      Follow-up: {new Date(note.followUpDate).toLocaleDateString()}
                    </Text>
                  </View>
                )}
              </Card>
            ))
          ) : (
            <View style={styles.emptyContainer}>
              <Icon name="note" type="material" size={64} color="#CCC" />
              <Text style={styles.emptyText}>
                {selectedPatientId ? 'No clinical notes recorded' : 'Select a patient to view notes'}
              </Text>
              {selectedPatientId && (
                <Button
                  title="Create First Note"
                  onPress={() => setShowCreateModal(true)}
                  containerStyle={styles.emptyButton}
                />
              )}
            </View>
          )}
        </ScrollView>
      )}

      {/* Create Note Modal */}
      <Modal
        visible={showCreateModal}
        animationType="slide"
        presentationStyle="pageSheet"
        onRequestClose={() => setShowCreateModal(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <Text h4>Create Clinical Note</Text>
            <TouchableOpacity onPress={() => setShowCreateModal(false)}>
              <Icon name="close" type="material" size={28} />
            </TouchableOpacity>
          </View>

          <ScrollView style={styles.modalContent}>
            {/* Patient Selection */}
            {!route?.params?.patientId && (
              <>
                <Text style={styles.inputLabel}>Patient *</Text>
                <View style={styles.pickerContainer}>
                  <Picker
                    selectedValue={newNote.patientId}
                    onValueChange={(value) => setNewNote({ ...newNote, patientId: value })}
                    style={styles.picker}
                  >
                    <Picker.Item label="Select a patient" value="" />
                    {patients.map((patient) => (
                      <Picker.Item
                        key={patient.id}
                        label={`${patient.firstName} ${patient.lastName}`}
                        value={patient.id}
                      />
                    ))}
                  </Picker>
                </View>
              </>
            )}

            {/* Note Type */}
            <Text style={styles.inputLabel}>Note Type</Text>
            <View style={styles.pickerContainer}>
              <Picker
                selectedValue={newNote.noteType}
                onValueChange={(value) => setNewNote({ ...newNote, noteType: value as any })}
                style={styles.picker}
              >
                <Picker.Item label="Progress Note" value="progress" />
                <Picker.Item label="Consultation" value="consultation" />
                <Picker.Item label="Procedure Note" value="procedure" />
                <Picker.Item label="Diagnosis" value="diagnosis" />
                <Picker.Item label="Treatment Plan" value="treatment-plan" />
                <Picker.Item label="Follow-up" value="follow-up" />
              </Picker>
            </View>

            {/* Chief Complaint */}
            <Input
              label="Chief Complaint"
              value={newNote.chiefComplaint}
              onChangeText={(text) => setNewNote({ ...newNote, chiefComplaint: text })}
              placeholder="Patient's main concern or reason for visit"
              multiline
              numberOfLines={2}
            />

            {/* Present Illness */}
            <Input
              label="History of Present Illness"
              value={newNote.presentIllness}
              onChangeText={(text) => setNewNote({ ...newNote, presentIllness: text })}
              placeholder="Detailed history of current symptoms"
              multiline
              numberOfLines={3}
            />

            {/* Vital Signs */}
            <Text h4 style={styles.sectionTitle}>Vital Signs</Text>
            <Input
              label="Blood Pressure"
              value={newNote.vitalSigns?.bloodPressure}
              onChangeText={(text) =>
                setNewNote({
                  ...newNote,
                  vitalSigns: { ...newNote.vitalSigns, bloodPressure: text },
                })
              }
              placeholder="120/80"
            />
            <Input
              label="Heart Rate (bpm)"
              value={newNote.vitalSigns?.heartRate?.toString()}
              onChangeText={(text) =>
                setNewNote({
                  ...newNote,
                  vitalSigns: { ...newNote.vitalSigns, heartRate: text ? parseFloat(text) : undefined },
                })
              }
              keyboardType="numeric"
              placeholder="72"
            />
            <Input
              label="Temperature (Â°F)"
              value={newNote.vitalSigns?.temperature?.toString()}
              onChangeText={(text) =>
                setNewNote({
                  ...newNote,
                  vitalSigns: { ...newNote.vitalSigns, temperature: text ? parseFloat(text) : undefined },
                })
              }
              keyboardType="numeric"
              placeholder="98.6"
            />

            {/* Assessment */}
            <Input
              label="Assessment *"
              value={newNote.assessment}
              onChangeText={(text) => setNewNote({ ...newNote, assessment: text })}
              placeholder="Clinical assessment and findings"
              multiline
              numberOfLines={4}
            />

            {/* Plan */}
            <Input
              label="Plan *"
              value={newNote.plan}
              onChangeText={(text) => setNewNote({ ...newNote, plan: text })}
              placeholder="Treatment plan and next steps"
              multiline
              numberOfLines={4}
            />

            {/* Diagnosis */}
            <Text h4 style={styles.sectionTitle}>Diagnosis</Text>
            <View style={styles.addItemContainer}>
              <Input
                value={diagnosisInput}
                onChangeText={setDiagnosisInput}
                placeholder="Add diagnosis"
                containerStyle={{ flex: 1 }}
              />
              <TouchableOpacity style={styles.addButton} onPress={addDiagnosis}>
                <Icon name="add" type="material" color="#FFFFFF" />
              </TouchableOpacity>
            </View>
            {newNote.diagnosis && newNote.diagnosis.length > 0 && (
              <View style={styles.itemsList}>
                {newNote.diagnosis.map((item, index) => (
                  <View key={index} style={styles.itemChip}>
                    <Text style={styles.itemChipText}>{item}</Text>
                    <TouchableOpacity onPress={() => removeDiagnosis(index)}>
                      <Icon name="close" type="material" size={18} color="#666" />
                    </TouchableOpacity>
                  </View>
                ))}
              </View>
            )}

            {/* Prescriptions */}
            <Text h4 style={styles.sectionTitle}>Prescriptions</Text>
            <View style={styles.addItemContainer}>
              <Input
                value={prescriptionInput}
                onChangeText={setPrescriptionInput}
                placeholder="Add prescription"
                containerStyle={{ flex: 1 }}
              />
              <TouchableOpacity style={styles.addButton} onPress={addPrescription}>
                <Icon name="add" type="material" color="#FFFFFF" />
              </TouchableOpacity>
            </View>
            {newNote.prescriptions && newNote.prescriptions.length > 0 && (
              <View style={styles.itemsList}>
                {newNote.prescriptions.map((item, index) => (
                  <View key={index} style={styles.itemChip}>
                    <Text style={styles.itemChipText}>{item}</Text>
                    <TouchableOpacity onPress={() => removePrescription(index)}>
                      <Icon name="close" type="material" size={18} color="#666" />
                    </TouchableOpacity>
                  </View>
                ))}
              </View>
            )}

            {/* Lab Orders */}
            <Text h4 style={styles.sectionTitle}>Lab Orders</Text>
            <View style={styles.addItemContainer}>
              <Input
                value={labOrderInput}
                onChangeText={setLabOrderInput}
                placeholder="Add lab order"
                containerStyle={{ flex: 1 }}
              />
              <TouchableOpacity style={styles.addButton} onPress={addLabOrder}>
                <Icon name="add" type="material" color="#FFFFFF" />
              </TouchableOpacity>
            </View>
            {newNote.labOrders && newNote.labOrders.length > 0 && (
              <View style={styles.itemsList}>
                {newNote.labOrders.map((item, index) => (
                  <View key={index} style={styles.itemChip}>
                    <Text style={styles.itemChipText}>{item}</Text>
                    <TouchableOpacity onPress={() => removeLabOrder(index)}>
                      <Icon name="close" type="material" size={18} color="#666" />
                    </TouchableOpacity>
                  </View>
                ))}
              </View>
            )}

            {/* Referrals */}
            <Text h4 style={styles.sectionTitle}>Referrals</Text>
            <View style={styles.addItemContainer}>
              <Input
                value={referralInput}
                onChangeText={setReferralInput}
                placeholder="Add referral"
                containerStyle={{ flex: 1 }}
              />
              <TouchableOpacity style={styles.addButton} onPress={addReferral}>
                <Icon name="add" type="material" color="#FFFFFF" />
              </TouchableOpacity>
            </View>
            {newNote.referrals && newNote.referrals.length > 0 && (
              <View style={styles.itemsList}>
                {newNote.referrals.map((item, index) => (
                  <View key={index} style={styles.itemChip}>
                    <Text style={styles.itemChipText}>{item}</Text>
                    <TouchableOpacity onPress={() => removeReferral(index)}>
                      <Icon name="close" type="material" size={18} color="#666" />
                    </TouchableOpacity>
                  </View>
                ))}
              </View>
            )}

            {/* Follow-up Date */}
            <Input
              label="Follow-up Date"
              value={newNote.followUpDate}
              onChangeText={(text) => setNewNote({ ...newNote, followUpDate: text })}
              placeholder="YYYY-MM-DD"
              leftIcon={<Icon name="event" type="material" size={20} />}
            />

            <Button
              title="Create Clinical Note"
              onPress={handleCreateNote}
              containerStyle={styles.createButton}
              buttonStyle={{ backgroundColor: theme.colors.primary }}
            />
          </ScrollView>
        </View>
      </Modal>
    </View>
  );
};

