import React, { useState, useEffect } from 'react';
import { View, StyleSheet, ScrollView, ActivityIndicator } from 'react-native';
import { Text, Button, Icon, Card } from 'react-native-elements';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../types/navigation';
import { theme } from '../theme/theme';
import { LinearGradient } from 'expo-linear-gradient';
import axios from 'axios';

type PrivacyPolicyScreenProps = {
  navigation: StackNavigationProp<RootStackParamList, 'PrivacyPolicy'>;
};

interface PrivacyPolicyData {
  version: string;
  effectiveDate: string;
  content: string;
}

const PrivacyPolicyScreen: React.FC<PrivacyPolicyScreenProps> = ({ navigation }) => {
  const [loading, setLoading] = useState(true);
  const [policy, setPolicy] = useState<PrivacyPolicyData | null>(null);

  useEffect(() => {
    loadPrivacyPolicy();
  }, []);

  const loadPrivacyPolicy = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:3000/api/settings/legal/privacy-policy');
      setPolicy(response.data);
    } catch (error) {
      console.error('Error loading privacy policy:', error);
      // Set fallback content
      setPolicy({
        version: '1.0.0',
        effectiveDate: '2024-01-01',
        content: 'Privacy policy content is currently unavailable. Please try again later.',
      });
    } finally {
      setLoading(false);
    }
  };

  const renderContent = () => {
    if (!policy) return null;

    // Split content by headers (lines starting with #)
    const lines = policy.content.split('\n');
    const sections: { title: string; content: string[] }[] = [];
    let currentSection: { title: string; content: string[] } | null = null;

    lines.forEach((line) => {
      if (line.startsWith('# ')) {
        if (currentSection) {
          sections.push(currentSection);
        }
        currentSection = {
          title: line.replace('# ', '').trim(),
          content: [],
        };
      } else if (line.startsWith('## ')) {
        if (currentSection) {
          currentSection.content.push(line.replace('## ', '').trim());
        }
      } else if (line.trim() && currentSection) {
        currentSection.content.push(line.trim());
      }
    });

    if (currentSection) {
      sections.push(currentSection);
    }

    return sections.map((section, index) => (
      <Card key={index} containerStyle={styles.sectionCard}>
        <Card.Title style={styles.sectionTitle}>{section.title}</Card.Title>
        <Card.Divider />
        {section.content.map((text, i) => (
          <Text key={i} style={styles.sectionText}>
            {text}
          </Text>
        ))}
      </Card>
    ));
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>Loading privacy policy...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <LinearGradient
        colors={theme.gradients.primary.colors}
        start={theme.gradients.primary.start}
        end={theme.gradients.primary.end}
        style={styles.header}
      >
        <View style={styles.iconContainer}>
          <Icon name="privacy-tip" type="material" color="white" size={60} />
        </View>
        <Text style={styles.headerTitle}>Privacy Policy</Text>
        <Text style={styles.headerSubtitle}>How we protect your data</Text>
      </LinearGradient>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        <View style={styles.content}>
          {policy && (
            <View style={styles.metaInfo}>
              <View style={styles.metaRow}>
                <Icon
                  name="update"
                  type="material"
                  size={18}
                  color={theme.colors.textSecondary}
                  containerStyle={styles.metaIcon}
                />
                <Text style={styles.metaText}>
                  Effective Date: {new Date(policy.effectiveDate).toLocaleDateString()}
                </Text>
              </View>
              <View style={styles.metaRow}>
                <Icon
                  name="info-outline"
                  type="material"
                  size={18}
                  color={theme.colors.textSecondary}
                  containerStyle={styles.metaIcon}
                />
                <Text style={styles.metaText}>Version {policy.version}</Text>
              </View>
            </View>
          )}

          <Card containerStyle={styles.hipaaCard}>
            <View style={styles.hipaaHeader}>
              <Icon name="verified-user" type="material" color="#4CAF50" size={28} />
              <Text style={styles.hipaaTitle}>HIPAA Compliant</Text>
            </View>
            <Text style={styles.hipaaText}>
              We are committed to protecting your health information in accordance with HIPAA
              regulations and maintaining the highest standards of data privacy and security.
            </Text>
          </Card>

          {renderContent()}

          <Card containerStyle={styles.contactCard}>
            <Card.Title style={styles.contactTitle}>Questions About Privacy?</Card.Title>
            <Card.Divider />
            <Text style={styles.contactText}>
              If you have any questions about our privacy practices, please contact us:
            </Text>
            <View style={styles.contactMethod}>
              <Icon name="email" type="material" size={20} color={theme.colors.primary} />
              <Text style={styles.contactMethodText}>privacy@medimind.com</Text>
            </View>
            <View style={styles.contactMethod}>
              <Icon name="phone" type="material" size={20} color={theme.colors.primary} />
              <Text style={styles.contactMethodText}>1-800-MEDIMIND</Text>
            </View>
          </Card>

          <View style={styles.buttonContainer}>
            <Button
              title="I Understand"
              onPress={() => navigation.goBack()}
              buttonStyle={styles.backButton}
              icon={
                <Icon
                  name="check"
                  type="material"
                  color="white"
                  size={20}
                  containerStyle={{ marginRight: 10 }}
                />
              }
            />
          </View>
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: theme.colors.background,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: theme.colors.background,
  },
  loadingText: {
    marginTop: 16,
    fontSize: 16,
    color: theme.colors.textSecondary,
  },
  header: {
    alignItems: 'center',
    paddingVertical: 40,
    paddingHorizontal: 20,
  },
  iconContainer: {
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    width: 100,
    height: 100,
    borderRadius: 50,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 20,
  },
  headerTitle: {
    fontSize: 28,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 8,
  },
  headerSubtitle: {
    fontSize: 16,
    color: 'rgba(255, 255, 255, 0.9)',
  },
  scrollView: {
    flex: 1,
  },
  content: {
    padding: 20,
  },
  metaInfo: {
    backgroundColor: 'white',
    borderRadius: 8,
    padding: 16,
    marginBottom: 20,
  },
  metaRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 8,
  },
  metaIcon: {
    marginRight: 8,
  },
  metaText: {
    fontSize: 14,
    color: theme.colors.textSecondary,
  },
  hipaaCard: {
    borderRadius: 12,
    marginBottom: 20,
    borderLeftWidth: 4,
    borderLeftColor: '#4CAF50',
  },
  hipaaHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 12,
  },
  hipaaTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#4CAF50',
    marginLeft: 12,
  },
  hipaaText: {
    fontSize: 14,
    color: theme.colors.textSecondary,
    lineHeight: 20,
  },
  sectionCard: {
    borderRadius: 12,
    marginBottom: 16,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: theme.colors.text,
    textAlign: 'left',
  },
  sectionText: {
    fontSize: 14,
    color: theme.colors.textSecondary,
    lineHeight: 22,
    marginBottom: 12,
  },
  contactCard: {
    borderRadius: 12,
    backgroundColor: '#F3E5F5',
    marginBottom: 20,
  },
  contactTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: theme.colors.text,
  },
  contactText: {
    fontSize: 14,
    color: theme.colors.textSecondary,
    marginBottom: 16,
  },
  contactMethod: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 8,
  },
  contactMethodText: {
    fontSize: 14,
    color: theme.colors.text,
    marginLeft: 12,
    fontWeight: '600',
  },
  buttonContainer: {
    marginTop: 10,
    marginBottom: 40,
  },
  backButton: {
    backgroundColor: theme.colors.primary,
    borderRadius: 8,
    paddingVertical: 14,
  },
});

export default PrivacyPolicyScreen;
