import React, { useState } from 'react';
import {
  View,
  StyleSheet,
  ScrollView,
  KeyboardAvoidingView,
  Platform,
  Alert,
} from 'react-native';
import { Text, Input, Button, Icon } from 'react-native-elements';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../types/navigation';
import { theme } from '../theme/theme';
import { LinearGradient } from 'expo-linear-gradient';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';

type ChangePasswordScreenProps = {
  navigation: StackNavigationProp<RootStackParamList, 'ChangePassword'>;
};

const ChangePasswordScreen: React.FC<ChangePasswordScreenProps> = ({ navigation }) => {
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);

  const [errors, setErrors] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });

  const getPasswordStrength = (password: string): { strength: string; color: string; width: string } => {
    if (password.length === 0) {
      return { strength: '', color: '#E0E0E0', width: '0%' };
    }

    let strength = 0;

    if (password.length >= 8) strength++;
    if (password.length >= 12) strength++;
    if (/[a-z]/.test(password) && /[A-Z]/.test(password)) strength++;
    if (/\d/.test(password)) strength++;
    if (/[^a-zA-Z0-9]/.test(password)) strength++;

    if (strength <= 2) {
      return { strength: 'Weak', color: '#F44336', width: '33%' };
    } else if (strength <= 3) {
      return { strength: 'Medium', color: '#FF9800', width: '66%' };
    } else {
      return { strength: 'Strong', color: '#4CAF50', width: '100%' };
    }
  };

  const passwordStrength = getPasswordStrength(newPassword);

  const validateForm = (): boolean => {
    const newErrors = {
      currentPassword: '',
      newPassword: '',
      confirmPassword: '',
    };

    let isValid = true;

    if (!currentPassword) {
      newErrors.currentPassword = 'Current password is required';
      isValid = false;
    }

    if (!newPassword) {
      newErrors.newPassword = 'New password is required';
      isValid = false;
    } else if (newPassword.length < 8) {
      newErrors.newPassword = 'Password must be at least 8 characters long';
      isValid = false;
    }

    if (!confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your new password';
      isValid = false;
    } else if (newPassword !== confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
      isValid = false;
    }

    if (currentPassword && newPassword && currentPassword === newPassword) {
      newErrors.newPassword = 'New password must be different from current password';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleChangePassword = async () => {
    if (!validateForm()) {
      return;
    }

    try {
      setLoading(true);
      const token = await AsyncStorage.getItem('authToken');

      if (!token) {
        Alert.alert('Error', 'Authentication required');
        navigation.goBack();
        return;
      }

      await axios.post(
        'http://localhost:3000/api/settings/password/change',
        {
          currentPassword,
          newPassword,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      Alert.alert('Success', 'Password changed successfully', [
        {
          text: 'OK',
          onPress: () => navigation.goBack(),
        },
      ]);
    } catch (error: any) {
      console.error('Error changing password:', error);
      const errorMessage = error.response?.data?.error || 'Failed to change password';
      Alert.alert('Error', errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <KeyboardAvoidingView
      style={styles.container}
      behavior={Platform.OS === 'ios' ? 'padding' : undefined}
    >
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        <LinearGradient
          colors={theme.gradients.primary.colors}
          start={theme.gradients.primary.start}
          end={theme.gradients.primary.end}
          style={styles.header}
        >
          <View style={styles.iconContainer}>
            <Icon name="lock" type="material" color="white" size={60} />
          </View>
          <Text style={styles.headerTitle}>Change Password</Text>
          <Text style={styles.headerSubtitle}>Update your account security</Text>
        </LinearGradient>

        <View style={styles.formContainer}>
          <View style={styles.securityTip}>
            <Icon
              name="info"
              type="material"
              color={theme.colors.primary}
              size={20}
              containerStyle={{ marginRight: 10 }}
            />
            <Text style={styles.securityTipText}>
              Use a strong password with letters, numbers, and symbols
            </Text>
          </View>

          <Input
            label="Current Password"
            placeholder="Enter current password"
            value={currentPassword}
            onChangeText={(text) => {
              setCurrentPassword(text);
              setErrors({ ...errors, currentPassword: '' });
            }}
            secureTextEntry={!showCurrentPassword}
            leftIcon={<Icon name="lock-outline" type="material" size={24} color={theme.colors.textSecondary} />}
            rightIcon={
              <Icon
                name={showCurrentPassword ? 'visibility' : 'visibility-off'}
                type="material"
                size={24}
                color={theme.colors.textSecondary}
                onPress={() => setShowCurrentPassword(!showCurrentPassword)}
              />
            }
            errorMessage={errors.currentPassword}
            containerStyle={styles.inputContainer}
            labelStyle={styles.inputLabel}
            inputStyle={styles.input}
          />

          <Input
            label="New Password"
            placeholder="Enter new password"
            value={newPassword}
            onChangeText={(text) => {
              setNewPassword(text);
              setErrors({ ...errors, newPassword: '' });
            }}
            secureTextEntry={!showNewPassword}
            leftIcon={<Icon name="lock" type="material" size={24} color={theme.colors.textSecondary} />}
            rightIcon={
              <Icon
                name={showNewPassword ? 'visibility' : 'visibility-off'}
                type="material"
                size={24}
                color={theme.colors.textSecondary}
                onPress={() => setShowNewPassword(!showNewPassword)}
              />
            }
            errorMessage={errors.newPassword}
            containerStyle={styles.inputContainer}
            labelStyle={styles.inputLabel}
            inputStyle={styles.input}
          />

          {newPassword.length > 0 && (
            <View style={styles.strengthContainer}>
              <View style={styles.strengthBar}>
                <View
                  style={[
                    styles.strengthBarFill,
                    {
                      backgroundColor: passwordStrength.color,
                      width: passwordStrength.width,
                    },
                  ]}
                />
              </View>
              <Text style={[styles.strengthText, { color: passwordStrength.color }]}>
                {passwordStrength.strength}
              </Text>
            </View>
          )}

          <Input
            label="Confirm New Password"
            placeholder="Confirm new password"
            value={confirmPassword}
            onChangeText={(text) => {
              setConfirmPassword(text);
              setErrors({ ...errors, confirmPassword: '' });
            }}
            secureTextEntry={!showConfirmPassword}
            leftIcon={<Icon name="lock-outline" type="material" size={24} color={theme.colors.textSecondary} />}
            rightIcon={
              <Icon
                name={showConfirmPassword ? 'visibility' : 'visibility-off'}
                type="material"
                size={24}
                color={theme.colors.textSecondary}
                onPress={() => setShowConfirmPassword(!showConfirmPassword)}
              />
            }
            errorMessage={errors.confirmPassword}
            containerStyle={styles.inputContainer}
            labelStyle={styles.inputLabel}
            inputStyle={styles.input}
          />

          <View style={styles.passwordRequirements}>
            <Text style={styles.requirementsTitle}>Password Requirements:</Text>
            <View style={styles.requirementRow}>
              <Icon
                name={newPassword.length >= 8 ? 'check-circle' : 'radio-button-unchecked'}
                type="material"
                size={16}
                color={newPassword.length >= 8 ? '#4CAF50' : theme.colors.textSecondary}
              />
              <Text style={styles.requirementText}>At least 8 characters</Text>
            </View>
            <View style={styles.requirementRow}>
              <Icon
                name={/[A-Z]/.test(newPassword) && /[a-z]/.test(newPassword) ? 'check-circle' : 'radio-button-unchecked'}
                type="material"
                size={16}
                color={/[A-Z]/.test(newPassword) && /[a-z]/.test(newPassword) ? '#4CAF50' : theme.colors.textSecondary}
              />
              <Text style={styles.requirementText}>Upper and lowercase letters</Text>
            </View>
            <View style={styles.requirementRow}>
              <Icon
                name={/\d/.test(newPassword) ? 'check-circle' : 'radio-button-unchecked'}
                type="material"
                size={16}
                color={/\d/.test(newPassword) ? '#4CAF50' : theme.colors.textSecondary}
              />
              <Text style={styles.requirementText}>At least one number</Text>
            </View>
            <View style={styles.requirementRow}>
              <Icon
                name={/[^a-zA-Z0-9]/.test(newPassword) ? 'check-circle' : 'radio-button-unchecked'}
                type="material"
                size={16}
                color={/[^a-zA-Z0-9]/.test(newPassword) ? '#4CAF50' : theme.colors.textSecondary}
              />
              <Text style={styles.requirementText}>At least one special character</Text>
            </View>
          </View>

          <View style={styles.buttonContainer}>
            <Button
              title={loading ? 'Changing Password...' : 'Change Password'}
              onPress={handleChangePassword}
              disabled={loading}
              loading={loading}
              buttonStyle={styles.changeButton}
              containerStyle={styles.changeButtonContainer}
              icon={
                !loading && (
                  <Icon
                    name="check"
                    type="material"
                    color="white"
                    size={20}
                    containerStyle={{ marginRight: 10 }}
                  />
                )
              }
            />

            <Button
              title="Cancel"
              onPress={() => navigation.goBack()}
              type="outline"
              disabled={loading}
              buttonStyle={styles.cancelButton}
              titleStyle={styles.cancelButtonTitle}
              containerStyle={styles.cancelButtonContainer}
            />
          </View>
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
};

