import React, { useState, useEffect } from 'react';
import {
  View,
  ScrollView,
  StyleSheet,
  RefreshControl,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  TextInput,
} from 'react-native';
import { Text, Card, Icon, SearchBar } from 'react-native-elements';
import { useTheme } from 'react-native-elements';
import providerAPI, { PatientSummary } from '../services/providerAPI';

interface PatientManagementScreenProps {
  navigation: any;
  route?: any;
}

const PatientManagementScreen: React.FC<PatientManagementScreenProps> = ({ navigation, route }) => {
  const { theme } = useTheme();
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [patients, setPatients] = useState<PatientSummary[]>([]);
  const [filteredPatients, setFilteredPatients] = useState<PatientSummary[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filter, setFilter] = useState<string>(route?.params?.filter || 'all');

  useEffect(() => {
    loadPatients();
  }, []);

  useEffect(() => {
    filterPatients();
  }, [searchQuery, patients, filter]);

  const loadPatients = async () => {
    try {
      setLoading(true);
      const data = await providerAPI.getPatients();
      setPatients(data);
    } catch (error) {
      console.error('[PatientManagement] Error loading:', error);
      Alert.alert('Error', 'Failed to load patients');
    } finally {
      setLoading(false);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadPatients();
    setRefreshing(false);
  };

  const filterPatients = () => {
    let filtered = [...patients];

    // Apply search query
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(
        (patient) =>
          patient.firstName.toLowerCase().includes(query) ||
          patient.lastName.toLowerCase().includes(query) ||
          patient.email.toLowerCase().includes(query)
      );
    }

    // Apply filters
    if (filter === 'critical') {
      filtered = filtered.filter((patient) => patient.riskScore && patient.riskScore > 70);
    } else if (filter === 'recent') {
      filtered = filtered.filter((patient) => patient.lastVisit);
      filtered.sort((a, b) => {
        if (!a.lastVisit) return 1;
        if (!b.lastVisit) return -1;
        return new Date(b.lastVisit).getTime() - new Date(a.lastVisit).getTime();
      });
    } else if (filter === 'upcoming') {
      filtered = filtered.filter((patient) => patient.upcomingAppointment);
    }

    setFilteredPatients(filtered);
  };

  const getRiskColor = (riskScore?: number): string => {
    if (!riskScore) return '#4CAF50';
    if (riskScore >= 70) return '#F44336';
    if (riskScore >= 40) return '#FF9800';
    return '#4CAF50';
  };

  if (loading) {
    return (
      <View style={[styles.container, styles.centered]}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>Loading patients...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-back" type="material" color={theme.colors.primary} />
        </TouchableOpacity>
        <Text h3 style={styles.headerTitle}>Patient Management</Text>
        <View style={{ width: 40 }} />
      </View>

      {/* Search Bar */}
      <View style={styles.searchContainer}>
        <SearchBar
          placeholder="Search patients..."
          onChangeText={setSearchQuery}
          value={searchQuery}
          platform="default"
          containerStyle={styles.searchBarContainer}
          inputContainerStyle={styles.searchInputContainer}
        />
      </View>

      {/* Filter Chips */}
      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
        style={styles.filterScroll}
        contentContainerStyle={styles.filterContainer}
      >
        <TouchableOpacity
          style={[styles.filterChip, filter === 'all' && styles.filterChipActive]}
          onPress={() => setFilter('all')}
        >
          <Text style={[styles.filterChipText, filter === 'all' && styles.filterChipTextActive]}>
            All ({patients.length})
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterChip, filter === 'critical' && styles.filterChipActive]}
          onPress={() => setFilter('critical')}
        >
          <Icon name="warning" type="material" size={16} color={filter === 'critical' ? '#FFFFFF' : '#666'} />
          <Text style={[styles.filterChipText, filter === 'critical' && styles.filterChipTextActive]}>
            Critical
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterChip, filter === 'recent' && styles.filterChipActive]}
          onPress={() => setFilter('recent')}
        >
          <Icon name="history" type="material" size={16} color={filter === 'recent' ? '#FFFFFF' : '#666'} />
          <Text style={[styles.filterChipText, filter === 'recent' && styles.filterChipTextActive]}>
            Recent Visits
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterChip, filter === 'upcoming' && styles.filterChipActive]}
          onPress={() => setFilter('upcoming')}
        >
          <Icon name="event" type="material" size={16} color={filter === 'upcoming' ? '#FFFFFF' : '#666'} />
          <Text style={[styles.filterChipText, filter === 'upcoming' && styles.filterChipTextActive]}>
            Upcoming
          </Text>
        </TouchableOpacity>
      </ScrollView>

      {/* Patient List */}
      <ScrollView
        style={styles.listContainer}
        refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
      >
        {filteredPatients.length > 0 ? (
          filteredPatients.map((patient) => (
            <TouchableOpacity
              key={patient.id}
              onPress={() => navigation.navigate('PatientDetails', { patientId: patient.id })}
            >
              <Card containerStyle={styles.patientCard}>
                <View style={styles.patientCardContent}>
                  {/* Avatar */}
                  <View style={[styles.avatar, { backgroundColor: theme.colors.primary }]}>
                    <Text style={styles.avatarText}>
                      {patient.firstName.charAt(0)}{patient.lastName.charAt(0)}
                    </Text>
                  </View>

                  {/* Patient Info */}
                  <View style={styles.patientInfo}>
                    <Text style={styles.patientName}>
                      {patient.firstName} {patient.lastName}
                    </Text>

                    <View style={styles.patientDetailRow}>
                      <Icon name="cake" type="material" size={14} color="#666" />
                      <Text style={styles.patientDetailText}>
                        {new Date(patient.dateOfBirth).toLocaleDateString()}
                      </Text>
                    </View>

                    {patient.phone && (
                      <View style={styles.patientDetailRow}>
                        <Icon name="phone" type="material" size={14} color="#666" />
                        <Text style={styles.patientDetailText}>{patient.phone}</Text>
                      </View>
                    )}

                    {patient.lastVisit && (
                      <View style={styles.patientDetailRow}>
                        <Icon name="history" type="material" size={14} color="#666" />
                        <Text style={styles.patientDetailText}>
                          Last visit: {new Date(patient.lastVisit).toLocaleDateString()}
                        </Text>
                      </View>
                    )}

                    {patient.upcomingAppointment && (
                      <View style={styles.patientDetailRow}>
                        <Icon name="event" type="material" size={14} color="#4CAF50" />
                        <Text style={[styles.patientDetailText, { color: '#4CAF50' }]}>
                          Next: {new Date(patient.upcomingAppointment).toLocaleDateString()}
                        </Text>
                      </View>
                    )}

                    {/* Conditions */}
                    {patient.conditions && patient.conditions.length > 0 && (
                      <View style={styles.conditionsContainer}>
                        {patient.conditions.slice(0, 3).map((condition, index) => (
                          <View key={index} style={styles.conditionChip}>
                            <Text style={styles.conditionText}>{condition}</Text>
                          </View>
                        ))}
                        {patient.conditions.length > 3 && (
                          <Text style={styles.moreText}>+{patient.conditions.length - 3} more</Text>
                        )}
                      </View>
                    )}
                  </View>

                  {/* Risk Score */}
                  {patient.riskScore !== undefined && (
                    <View style={styles.riskContainer}>
                      <View
                        style={[
                          styles.riskCircle,
                          { backgroundColor: getRiskColor(patient.riskScore) },
                        ]}
                      >
                        <Text style={styles.riskScore}>{patient.riskScore}%</Text>
                      </View>
                      <Text style={styles.riskLabel}>Risk</Text>
                    </View>
                  )}

                  {/* Chevron */}
                  <Icon name="chevron-right" type="material" color={theme.colors.grey3} size={24} />
                </View>
              </Card>
            </TouchableOpacity>
          ))
        ) : (
          <View style={styles.emptyContainer}>
            <Icon name="search-off" type="material" size={64} color="#CCC" />
            <Text style={styles.emptyText}>No patients found</Text>
            <Text style={styles.emptySubtext}>Try adjusting your search or filters</Text>
          </View>
        )}
      </ScrollView>
    </View>
  );
