import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  RefreshControl,
  Dimensions,
  ActivityIndicator,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import { useDispatch, useSelector } from 'react-redux';
import {
  getPatientAnomalies,
  selectAnomalies,
  selectUnresolvedCount,
  selectCriticalCount,
  selectAnomalyIsLoading,
  selectAnomalyError,
} from '../store/slices/anomalySlice';
import type { AppDispatch } from '../store/store';

const { width } = Dimensions.get('window');

const AnomalyDashboardScreen: React.FC = () => {
  const navigation = useNavigation();
  const dispatch = useDispatch<AppDispatch>();

  const anomalies = useSelector(selectAnomalies);
  const unresolvedCount = useSelector(selectUnresolvedCount);
  const criticalCount = useSelector(selectCriticalCount);
  const isLoading = useSelector(selectAnomalyIsLoading);
  const error = useSelector(selectAnomalyError);

  const [refreshing, setRefreshing] = useState(false);
  const [selectedFilter, setSelectedFilter] = useState<'all' | 'unresolved' | 'critical'>('all');

  useEffect(() => {
    loadAnomalies();
  }, []);

  const loadAnomalies = async () => {
    try {
      // Using 'me' as placeholder - backend should resolve to actual patient DID
      await dispatch(getPatientAnomalies('me')).unwrap();
    } catch (err) {
      console.error('Error loading anomalies:', err);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadAnomalies();
    setRefreshing(false);
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical':
        return '#dc2626';
      case 'high':
        return '#ef4444';
      case 'medium':
        return '#f59e0b';
      case 'low':
        return '#10b981';
      default:
        return '#64748b';
    }
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'critical':
      case 'high':
        return 'alert-circle';
      case 'medium':
        return 'warning';
      case 'low':
        return 'information-circle';
      default:
        return 'help-circle';
    }
  };

  const getAnomalyTypeIcon = (type: string) => {
    const lowerType = type.toLowerCase();
    if (lowerType.includes('heart') || lowerType.includes('cardiac')) return 'heart';
    if (lowerType.includes('blood') || lowerType.includes('pressure')) return 'water';
    if (lowerType.includes('temperature') || lowerType.includes('fever')) return 'thermometer';
    if (lowerType.includes('oxygen') || lowerType.includes('spo2')) return 'pulse';
    if (lowerType.includes('glucose') || lowerType.includes('sugar')) return 'nutrition';
    if (lowerType.includes('activity') || lowerType.includes('movement')) return 'walk';
    return 'fitness';
  };

  const filteredAnomalies = anomalies.filter((anomaly) => {
    if (selectedFilter === 'unresolved') return !anomaly.resolved;
    if (selectedFilter === 'critical') return anomaly.severity === 'critical';
    return true;
  });

  const getTimeAgo = (timestamp: string) => {
    const now = new Date();
    const detected = new Date(timestamp);
    const diffMs = now.getTime() - detected.getTime();
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMins / 60);
    const diffDays = Math.floor(diffHours / 24);

    if (diffMins < 1) return 'Just now';
    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;
    return `${diffDays}d ago`;
  };

  return (
    <View style={styles.container}>
      {/* Header */}
      <LinearGradient
        colors={theme.gradients.primary.colors}
        style={styles.header}
        start={theme.gradients.primary.start}
        end={theme.gradients.primary.end}
      >
        <View style={styles.headerContent}>
          <TouchableOpacity
            onPress={() => navigation.goBack()}
            style={styles.backButton}
          >
            <Ionicons name="arrow-back" size={24} color="#fff" />
          </TouchableOpacity>
          <View style={styles.headerTitleContainer}>
            <Text style={styles.headerTitle}>Anomaly Monitor</Text>
            <Text style={styles.headerSubtitle}>
              Real-time Health Monitoring
            </Text>
          </View>
          <TouchableOpacity style={styles.settingsButton}>
            <Ionicons name="settings" size={24} color="#fff" />
          </TouchableOpacity>
        </View>

        {/* Stats Cards */}
        <View style={styles.statsContainer}>
          <View style={styles.statCard}>
            <Text style={styles.statValue}>{anomalies.length}</Text>
            <Text style={styles.statLabel}>Total</Text>
          </View>
          <View style={styles.statCard}>
            <Text style={[styles.statValue, { color: '#fbbf24' }]}>
              {unresolvedCount}
            </Text>
            <Text style={styles.statLabel}>Unresolved</Text>
          </View>
          <View style={styles.statCard}>
            <Text style={[styles.statValue, { color: '#ef4444' }]}>
              {criticalCount}
            </Text>
            <Text style={styles.statLabel}>Critical</Text>
          </View>
        </View>
      </LinearGradient>

      {/* Filter Tabs */}
      <View style={styles.filterContainer}>
        <TouchableOpacity
          style={[styles.filterTab, selectedFilter === 'all' && styles.filterTabActive]}
          onPress={() => setSelectedFilter('all')}
        >
          <Text style={[
            styles.filterText,
            selectedFilter === 'all' && styles.filterTextActive
          ]}>
            All Anomalies
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.filterTab, selectedFilter === 'unresolved' && styles.filterTabActive]}
          onPress={() => setSelectedFilter('unresolved')}
        >
          <Text style={[
            styles.filterText,
            selectedFilter === 'unresolved' && styles.filterTextActive
          ]}>
            Unresolved
          </Text>
          {unresolvedCount > 0 && (
            <View style={styles.badge}>
              <Text style={styles.badgeText}>{unresolvedCount}</Text>
            </View>
          )}
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.filterTab, selectedFilter === 'critical' && styles.filterTabActive]}
          onPress={() => setSelectedFilter('critical')}
        >
          <Text style={[
            styles.filterText,
            selectedFilter === 'critical' && styles.filterTextActive
          ]}>
            Critical
          </Text>
          {criticalCount > 0 && (
            <View style={[styles.badge, styles.badgeCritical]}>
              <Text style={styles.badgeText}>{criticalCount}</Text>
            </View>
          )}
        </TouchableOpacity>
      </View>

      <ScrollView
        style={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        {isLoading && anomalies.length === 0 ? (
          <View style={styles.loadingContainer}>
            <ActivityIndicator size="large" color="#667eea" />
            <Text style={styles.loadingText}>Loading anomalies...</Text>
          </View>
        ) : filteredAnomalies.length === 0 ? (
          <View style={styles.emptyState}>
            <View style={styles.emptyIconContainer}>
              <Ionicons name="checkmark-circle" size={80} color="#10b981" />
            </View>
            <Text style={styles.emptyStateTitle}>All Clear!</Text>
            <Text style={styles.emptyStateText}>
              {selectedFilter === 'all'
                ? 'No anomalies detected yet'
                : selectedFilter === 'unresolved'
                ? 'All anomalies have been resolved'
                : 'No critical anomalies detected'}
            </Text>
          </View>
        ) : (
          <View style={styles.anomaliesContainer}>
            {filteredAnomalies.map((anomaly) => {
              const severityColor = getSeverityColor(anomaly.severity);
              const severityIcon = getSeverityIcon(anomaly.severity);
              const typeIcon = getAnomalyTypeIcon(anomaly.anomaly_type);

              return (
                <TouchableOpacity
                  key={anomaly.id}
                  style={styles.anomalyCard}
                  activeOpacity={0.7}
                >
                  <View style={styles.anomalyHeader}>
                    <View style={[styles.anomalyIconContainer, { backgroundColor: severityColor + '20' }]}>
                      <Ionicons name={typeIcon as any} size={28} color={severityColor} />
                    </View>
                    <View style={styles.anomalyTitleContainer}>
                      <Text style={styles.anomalyType}>
                        {anomaly.anomaly_type.replace(/_/g, ' ')}
                      </Text>
                      <Text style={styles.anomalyTime}>
                        {getTimeAgo(anomaly.detected_at)}
                      </Text>
                    </View>
                    <View style={[styles.severityBadge, { backgroundColor: severityColor + '20' }]}>
                      <Ionicons name={severityIcon as any} size={16} color={severityColor} />
                      <Text style={[styles.severityText, { color: severityColor }]}>
                        {anomaly.severity.toUpperCase()}
                      </Text>
                    </View>
                  </View>

                  {/* Sensor Data */}
                  <View style={styles.sensorDataContainer}>
                    <Text style={styles.sensorDataTitle}>Sensor Readings:</Text>
                    <View style={styles.sensorDataGrid}>
                      {Object.entries(anomaly.sensor_data || {}).map(([key, value]) => (
                        <View key={key} style={styles.sensorDataItem}>
                          <Text style={styles.sensorDataKey}>
                            {key.replace(/_/g, ' ')}:
                          </Text>
                          <Text style={styles.sensorDataValue}>
                            {typeof value === 'object' ? JSON.stringify(value) : String(value)}
                          </Text>
                        </View>
                      ))}
                    </View>
                  </View>

                  {/* AI Confidence */}
                  <View style={styles.confidenceContainer}>
                    <Text style={styles.confidenceLabel}>AI Confidence:</Text>
                    <View style={styles.confidenceBarBg}>
                      <LinearGradient
                        colors={theme.gradients.primary.colors}
                        style={[
                          styles.confidenceBar,
                          { width: `${anomaly.ai_confidence * 100}%` },
                        ]}
                        start={theme.gradients.primary.start}
                        end={{ x: 1, y: 0 }}
                      />
                    </View>
                    <Text style={styles.confidenceValue}>
                      {(anomaly.ai_confidence * 100).toFixed(1)}%
                    </Text>
                  </View>

                  {/* Action Taken */}
                  {anomaly.action_taken && (
                    <View style={styles.actionContainer}>
                      <Ionicons name="medical" size={16} color="#10b981" />
                      <Text style={styles.actionText}>{anomaly.action_taken}</Text>
                    </View>
                  )}

                  {/* Footer */}
                  <View style={styles.anomalyFooter}>
                    <View style={styles.deviceInfo}>
                      <Ionicons name="watch" size={16} color="#64748b" />
                      <Text style={styles.deviceText}>
                        Device: {anomaly.device_id.substring(0, 8)}...
                      </Text>
                    </View>
                    <View style={styles.blockchainBadge}>
                      <Ionicons name="shield-checkmark" size={14} color="#10b981" />
                      <Text style={styles.blockchainText}>Verified</Text>
                    </View>
                    {anomaly.resolved ? (
                      <View style={styles.resolvedBadge}>
                        <Ionicons name="checkmark-circle" size={14} color="#10b981" />
                        <Text style={styles.resolvedText}>Resolved</Text>
                      </View>
                    ) : (
                      <TouchableOpacity style={styles.resolveButton}>
                        <Text style={styles.resolveButtonText}>Mark Resolved</Text>
                      </TouchableOpacity>
                    )}
                  </View>
                </TouchableOpacity>
              );
            })}
          </View>
        )}

        {error && (
          <View style={styles.errorContainer}>
            <Ionicons name="alert-circle" size={24} color="#ef4444" />
            <Text style={styles.errorText}>{error}</Text>
          </View>
        )}
      </ScrollView>
    </View>
  );
};

