import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  ActivityIndicator,
  Platform,
  Alert,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import axios from 'axios';

interface CancerType {
  id: string;
  name: string;
  riskScore: number;
  riskCategory: 'low' | 'moderate' | 'high' | 'very_high';
  icon: keyof typeof Ionicons.glyphMap;
  color: string;
  biomarkers: Biomarker[];
  screeningStatus: {
    lastScreening: string | null;
    nextDue: string;
    overdue: boolean;
  };
  riskFactors: RiskFactor[];
}

interface Biomarker {
  name: string;
  value: number;
  unit: string;
  normalRange: string;
  status: 'normal' | 'borderline' | 'elevated';
}

interface RiskFactor {
  name: string;
  present: boolean;
  impact: 'low' | 'moderate' | 'high';
}

interface GeneticRisk {
  tested: boolean;
  mutations: {
    gene: string;
    status: 'positive' | 'negative' | 'vus';
    description: string;
  }[];
}

const CancerDetectionSystemScreen: React.FC = () => {
  const navigation = useNavigation();
  const [cancerTypes, setCancerTypes] = useState<CancerType[]>([]);
  const [geneticRisk, setGeneticRisk] = useState<GeneticRisk | null>(null);
  const [loading, setLoading] = useState(true);
  const [selectedCancer, setSelectedCancer] = useState<CancerType | null>(null);

  const API_URL = process.env.EXPO_PUBLIC_API_URL || 'http://localhost:3000';

  useEffect(() => {
    fetchCancerData();
  }, []);

  const fetchCancerData = async () => {
    try {
      const [cancerResponse, geneticResponse] = await Promise.all([
        axios.get(`${API_URL}/api/cancer/screening`),
        axios.get(`${API_URL}/api/cancer/genetic-risk`),
      ]);

      setCancerTypes(cancerResponse.data.cancers);
      setGeneticRisk(geneticResponse.data);
    } catch (error) {
      console.error('Failed to fetch cancer data:', error);

      // Use mock data for demo
      if (__DEV__) {
        setCancerTypes(mockCancerData);
        setGeneticRisk(mockGeneticData);
      }
    } finally {
      setLoading(false);
    }
  };

  const getRiskColor = (category: string) => {
    switch (category) {
      case 'low':
        return '#4CAF50';
      case 'moderate':
        return '#FFC107';
      case 'high':
        return '#FF9800';
      case 'very_high':
        return '#F44336';
      default:
        return '#999';
    }
  };

  const getBiomarkerStatusColor = (status: string) => {
    switch (status) {
      case 'normal':
        return '#4CAF50';
      case 'borderline':
        return '#FFC107';
      case 'elevated':
        return '#F44336';
      default:
        return '#999';
    }
  };

  const scheduleScreening = (cancerType: string) => {
    Alert.alert(
      'Schedule Screening',
      `Would you like to schedule a ${cancerType} screening?`,
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Schedule',
          onPress: () => {
            // Navigate to appointment scheduling
            Alert.alert('Success', 'Screening appointment request sent to your provider.');
          },
        },
      ]
    );
  };

  const renderCancerCard = (cancer: CancerType) => (
    <TouchableOpacity
      key={cancer.id}
      style={[styles.cancerCard, { borderLeftColor: cancer.color }]}
      onPress={() => setSelectedCancer(cancer)}
    >
      <View style={styles.cancerHeader}>
        <View style={[styles.cancerIconContainer, { backgroundColor: cancer.color + '20' }]}>
          <Ionicons name={cancer.icon} size={32} color={cancer.color} />
        </View>
        <View style={styles.cancerInfo}>
          <Text style={styles.cancerName}>{cancer.name}</Text>
          <Text
            style={[styles.riskCategory, { color: getRiskColor(cancer.riskCategory) }]}
          >
            {cancer.riskCategory.replace('_', ' ').toUpperCase()} RISK
          </Text>
        </View>
        <Text style={[styles.riskScore, { color: getRiskColor(cancer.riskCategory) }]}>
          {cancer.riskScore}%
        </Text>
      </View>

      <View style={styles.screeningStatus}>
        <View style={styles.statusItem}>
          <Text style={styles.statusLabel}>Last Screening:</Text>
          <Text style={styles.statusValue}>
            {cancer.screeningStatus.lastScreening
              ? new Date(cancer.screeningStatus.lastScreening).toLocaleDateString()
              : 'Never'}
          </Text>
        </View>
        <View style={styles.statusItem}>
          <Text style={styles.statusLabel}>Next Due:</Text>
          <Text
            style={[
              styles.statusValue,
              cancer.screeningStatus.overdue && { color: '#F44336' },
            ]}
          >
            {new Date(cancer.screeningStatus.nextDue).toLocaleDateString()}
            {cancer.screeningStatus.overdue && ' (Overdue)'}
          </Text>
        </View>
      </View>

      {cancer.screeningStatus.overdue && (
        <TouchableOpacity
          style={styles.scheduleButton}
          onPress={() => scheduleScreening(cancer.name)}
        >
          <Ionicons name="calendar" size={16} color="#FFFFFF" />
          <Text style={styles.scheduleButtonText}>Schedule Screening</Text>
        </TouchableOpacity>
      )}
    </TouchableOpacity>
  );

  const renderCancerDetails = () => {
    if (!selectedCancer) return null;

    return (
      <View style={styles.detailsOverlay}>
        <View style={styles.detailsContainer}>
          <View style={styles.detailsHeader}>
            <Text style={styles.detailsTitle}>{selectedCancer.name}</Text>
            <TouchableOpacity onPress={() => setSelectedCancer(null)}>
              <Ionicons name="close" size={28} color="#333" />
            </TouchableOpacity>
          </View>

          <ScrollView style={styles.detailsContent} showsVerticalScrollIndicator={false}>
            {/* Risk Score */}
            <View style={styles.detailsSection}>
              <Text style={styles.sectionTitle}>Risk Assessment</Text>
              <View style={styles.riskScoreCard}>
                <Text
                  style={[
                    styles.detailsRiskScore,
                    { color: getRiskColor(selectedCancer.riskCategory) },
                  ]}
                >
                  {selectedCancer.riskScore}%
                </Text>
                <Text
                  style={[
                    styles.detailsRiskCategory,
                    { color: getRiskColor(selectedCancer.riskCategory) },
                  ]}
                >
                  {selectedCancer.riskCategory.replace('_', ' ').toUpperCase()} RISK
                </Text>
                <View style={styles.riskBar}>
                  <View
                    style={[
                      styles.riskBarFill,
                      {
                        width: `${selectedCancer.riskScore}%`,
                        backgroundColor: getRiskColor(selectedCancer.riskCategory),
                      },
                    ]}
                  />
                </View>
              </View>
            </View>

            {/* Biomarkers */}
            <View style={styles.detailsSection}>
              <Text style={styles.sectionTitle}>Biomarkers</Text>
              {selectedCancer.biomarkers.map((biomarker, index) => (
                <View key={index} style={styles.biomarkerCard}>
                  <View style={styles.biomarkerHeader}>
                    <Text style={styles.biomarkerName}>{biomarker.name}</Text>
                    <View
                      style={[
                        styles.biomarkerStatus,
                        { backgroundColor: getBiomarkerStatusColor(biomarker.status) },
                      ]}
                    >
                      <Text style={styles.biomarkerStatusText}>
                        {biomarker.status.toUpperCase()}
                      </Text>
                    </View>
                  </View>
                  <View style={styles.biomarkerInfo}>
                    <Text style={styles.biomarkerValue}>
                      {biomarker.value} {biomarker.unit}
                    </Text>
                    <Text style={styles.biomarkerRange}>
                      Normal: {biomarker.normalRange}
                    </Text>
                  </View>
                </View>
              ))}
            </View>

            {/* Risk Factors */}
            <View style={styles.detailsSection}>
              <Text style={styles.sectionTitle}>Risk Factors</Text>
              {selectedCancer.riskFactors.map((factor, index) => (
                <View key={index} style={styles.riskFactorItem}>
                  <Ionicons
                    name={factor.present ? 'checkbox' : 'square-outline'}
                    size={20}
                    color={factor.present ? '#F44336' : '#999'}
                  />
                  <Text
                    style={[
                      styles.riskFactorText,
                      factor.present && { color: '#F44336', fontWeight: '600' },
                    ]}
                  >
                    {factor.name}
                  </Text>
                  {factor.present && (
                    <View
                      style={[
                        styles.impactBadge,
                        {
                          backgroundColor:
                            factor.impact === 'high'
                              ? '#F44336'
                              : factor.impact === 'moderate'
                              ? '#FFC107'
                              : '#4CAF50',
                        },
                      ]}
                    >
                      <Text style={styles.impactText}>{factor.impact}</Text>
                    </View>
                  )}
                </View>
              ))}
            </View>

            {/* Prevention Strategies */}
            <View style={styles.detailsSection}>
              <Text style={styles.sectionTitle}>Prevention Strategies</Text>
              <View style={styles.preventionList}>
                <Text style={styles.preventionItem}>
                  • Regular screening as recommended
                </Text>
                <Text style={styles.preventionItem}>• Maintain healthy weight (BMI 18.5-24.9)</Text>
                <Text style={styles.preventionItem}>• Exercise 150+ minutes per week</Text>
                <Text style={styles.preventionItem}>• Limit alcohol consumption</Text>
                <Text style={styles.preventionItem}>• Avoid tobacco and smoking</Text>
                <Text style={styles.preventionItem}>• Eat a balanced diet rich in fruits/vegetables</Text>
                <Text style={styles.preventionItem}>• Manage stress levels</Text>
                <Text style={styles.preventionItem}>• Get adequate sleep (7-9 hours)</Text>
              </View>
            </View>

            <TouchableOpacity
              style={styles.detailsButton}
              onPress={() => scheduleScreening(selectedCancer.name)}
            >
              <Text style={styles.detailsButtonText}>Schedule Screening</Text>
            </TouchableOpacity>
          </ScrollView>
        </View>
      </View>
    );
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#9C27B0" />
        <Text style={styles.loadingText}>Loading cancer screening data...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Ionicons name="arrow-back" size={24} color="#333" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Cancer Detection</Text>
        <TouchableOpacity onPress={fetchCancerData}>
          <Ionicons name="refresh" size={24} color="#333" />
        </TouchableOpacity>
      </View>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Overview Stats */}
        <View style={styles.overviewCard}>
          <Text style={styles.overviewTitle}>Screening Overview</Text>
          <View style={styles.overviewStats}>
            <View style={styles.overviewStat}>
              <Text style={styles.overviewValue}>{cancerTypes.length}</Text>
              <Text style={styles.overviewLabel}>Cancer Types</Text>
            </View>
            <View style={styles.overviewStat}>
              <Text style={[styles.overviewValue, { color: '#F44336' }]}>
                {cancerTypes.filter((c) => c.screeningStatus.overdue).length}
              </Text>
              <Text style={styles.overviewLabel}>Overdue</Text>
            </View>
            <View style={styles.overviewStat}>
              <Text style={[styles.overviewValue, { color: '#4CAF50' }]}>
                {cancerTypes.filter((c) => !c.screeningStatus.overdue).length}
              </Text>
              <Text style={styles.overviewLabel}>Up to Date</Text>
            </View>
          </View>
        </View>

        {/* Cancer Types */}
        <Text style={styles.sectionHeader}>Cancer Screening Status</Text>
        {cancerTypes.map(renderCancerCard)}

        {/* Genetic Risk */}
        {geneticRisk && (
          <View style={styles.geneticCard}>
            <Text style={styles.sectionHeader}>Genetic Risk Assessment</Text>
            {!geneticRisk.tested ? (
              <View style={styles.geneticNotTested}>
                <Ionicons name="dna" size={48} color="#9C27B0" />
                <Text style={styles.geneticNotTestedText}>
                  No genetic testing on file
                </Text>
                <Text style={styles.geneticNotTestedDesc}>
                  Genetic testing can identify inherited mutations that increase cancer risk
                </Text>
                <TouchableOpacity
                  style={styles.geneticTestButton}
                  onPress={() =>
                    Alert.alert(
                      'Genetic Testing',
                      'Contact your healthcare provider to discuss genetic testing options.'
                    )
                  }
                >
                  <Text style={styles.geneticTestButtonText}>Learn About Genetic Testing</Text>
                </TouchableOpacity>
              </View>
            ) : (
              <View style={styles.geneticTested}>
                {geneticRisk.mutations.map((mutation, index) => (
                  <View key={index} style={styles.mutationCard}>
                    <View style={styles.mutationHeader}>
                      <Text style={styles.mutationGene}>{mutation.gene}</Text>
                      <View
                        style={[
                          styles.mutationStatus,
                          {
                            backgroundColor:
                              mutation.status === 'positive'
                                ? '#F44336'
                                : mutation.status === 'negative'
                                ? '#4CAF50'
                                : '#FFC107',
                          },
                        ]}
                      >
                        <Text style={styles.mutationStatusText}>
                          {mutation.status.toUpperCase()}
                        </Text>
                      </View>
                    </View>
                    <Text style={styles.mutationDesc}>{mutation.description}</Text>
                  </View>
                ))}
              </View>
            )}
          </View>
        )}

        {/* Multi-Cancer Blood Test Info */}
        <View style={styles.infoCard}>
          <Text style={styles.infoTitle}>
            <Ionicons name="flask" size={20} color="#2196F3" /> Multi-Cancer Early Detection
          </Text>
          <Text style={styles.infoText}>
            New blood tests like Galleri can detect signals from 50+ types of cancer, often
            before symptoms appear. Ask your doctor if this test is right for you.
          </Text>
          <View style={styles.infoFeatures}>
            <Text style={styles.infoFeature}>✓ Single blood draw</Text>
            <Text style={styles.infoFeature}>✓ Detects 50+ cancer types</Text>
            <Text style={styles.infoFeature}>✓ Identifies tissue of origin</Text>
            <Text style={styles.infoFeature}>✓ 99.5% specificity</Text>
          </View>
          <TouchableOpacity style={styles.infoButton}>
            <Text style={styles.infoButtonText}>Learn More</Text>
          </TouchableOpacity>
        </View>

        <View style={{ height: 30 }} />
      </ScrollView>

      {/* Cancer Details Modal */}
      {selectedCancer && renderCancerDetails()}
    </View>
  );
};

// Mock data
const mockCancerData: CancerType[] = [
