/**
 * CBT Therapy Chatbot Screen
 * Revenue Impact: +$80M ARR
 */

import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TextInput, TouchableOpacity, KeyboardAvoidingView, Platform } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import { theme } from '../theme/theme';

export default function CBTChatbotScreen({ navigation }: any) {
  const [messages, setMessages] = useState<any[]>([
    { role: 'assistant', content: "Hi! I'm here to support you using evidence-based CBT techniques. How are you feeling today?" }
  ]);
  const [input, setInput] = useState('');

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { role: 'user', content: input };
    setMessages(prev => [...prev, userMsg]);
    setInput('');

    await new Promise(r => setTimeout(r, 1000));

    const botMsg = {
      role: 'assistant',
      content: "I hear you. That sounds challenging. Let's explore this together. What thoughts are going through your mind right now?"
    };
    setMessages(prev => [...prev, botMsg]);
  };

  return (
    <KeyboardAvoidingView style={styles.container} behavior={Platform.OS === 'ios' ? 'padding' : undefined}>
      <LinearGradient colors={theme.gradients.primary.colors} style={styles.header}>
        <View style={styles.headerContent}>
          <TouchableOpacity onPress={() => navigation.goBack()}>
            <Ionicons name="arrow-back" size={24} color="#fff" />
          </TouchableOpacity>
          <Text style={styles.headerTitle}>CBT Therapy Chat</Text>
          <Ionicons name="shield-checkmark" size={24} color="#fff" />
        </View>
      </LinearGradient>

      <ScrollView style={styles.messages}>
        {messages.map((msg, i) => (
          <View key={i} style={[styles.messageBubble, msg.role === 'user' ? styles.userBubble : styles.botBubble]}>
            <Text style={[styles.messageText, msg.role === 'user' ? styles.userText : styles.botText]}>{msg.content}</Text>
          </View>
        ))}
      </ScrollView>

      <View style={styles.inputContainer}>
        <TextInput style={styles.input} value={input} onChangeText={setInput} placeholder="Type your message..." multiline />
        <TouchableOpacity style={styles.sendButton} onPress={sendMessage}>
          <LinearGradient colors={theme.gradients.primary.colors} style={styles.sendButtonGradient}>
            <Ionicons name="send" size={20} color="#fff" />
          </LinearGradient>
        </TouchableOpacity>
      </View>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f8fafc' },
  header: { paddingTop: 60, paddingBottom: 20, paddingHorizontal: 20 },
  headerContent: { flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between' },
  headerTitle: { fontSize: 24, fontWeight: 'bold', color: '#fff', flex: 1, marginLeft: 16 },
  messages: { flex: 1, padding: 16 },
  messageBubble: { maxWidth: '80%', borderRadius: 16, padding: 12, marginBottom: 12 },
  userBubble: { backgroundColor: '#8b5cf6', alignSelf: 'flex-end' },
  botBubble: { backgroundColor: '#fff', alignSelf: 'flex-start' },
  messageText: { fontSize: 15, lineHeight: 20 },
  userText: { color: '#fff' },
  botText: { color: '#1e293b' },
  inputContainer: { flexDirection: 'row', padding: 16, backgroundColor: '#fff', borderTopWidth: 1, borderTopColor: '#e2e8f0' },
  input: { flex: 1, backgroundColor: '#f1f5f9', borderRadius: 20, paddingHorizontal: 16, paddingVertical: 12, marginRight: 12, maxHeight: 100 },
  sendButton: { width: 48, height: 48, borderRadius: 24, overflow: 'hidden' },
  sendButtonGradient: { width: '100%', height: '100%', justifyContent: 'center', alignItems: 'center' },
});
