import React, { useState, useEffect } from 'react';
import {
  View,
  ScrollView,
  StyleSheet,
  RefreshControl,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  TextInput,
  Modal,
} from 'react-native';
import { Text, Card, Icon, Button, Input } from 'react-native-elements';
import { useTheme } from 'react-native-elements';
import { Picker } from '@react-native-picker/picker';
import providerAPI, { Appointment, PatientSummary } from '../services/providerAPI';
import { LinearGradient } from 'expo-linear-gradient';
import { theme as appTheme } from '../theme/theme';

interface AppointmentManagementScreenProps {
  navigation: any;
  route?: any;
}

const AppointmentManagementScreen: React.FC<AppointmentManagementScreenProps> = ({ navigation, route }) => {
  const { theme } = useTheme();
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [filteredAppointments, setFilteredAppointments] = useState<Appointment[]>([]);
  const [filter, setFilter] = useState<string>(route?.params?.filter || 'upcoming');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [patients, setPatients] = useState<PatientSummary[]>([]);

  // Create appointment form state
  const [newAppointment, setNewAppointment] = useState<Appointment>({
    patientId: route?.params?.patientId || '',
    appointmentDate: new Date().toISOString().split('T')[0],
    appointmentTime: '09:00',
    duration: 30,
    type: 'checkup',
    status: 'scheduled',
    reason: '',
    notes: '',
  });

  useEffect(() => {
    loadAppointments();
    loadPatients();

    if (route?.params?.action === 'create') {
      setShowCreateModal(true);
    }
  }, []);

  useEffect(() => {
    filterAppointments();
  }, [appointments, filter]);

  const loadAppointments = async () => {
    try {
      setLoading(true);
      const data = await providerAPI.getAppointments();
      setAppointments(data);
    } catch (error) {
      console.error('[AppointmentManagement] Error loading:', error);
      Alert.alert('Error', 'Failed to load appointments');
    } finally {
      setLoading(false);
    }
  };

  const loadPatients = async () => {
    try {
      const data = await providerAPI.getPatients();
      setPatients(data);
    } catch (error) {
      console.error('[AppointmentManagement] Error loading patients:', error);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadAppointments();
    setRefreshing(false);
  };

  const filterAppointments = () => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());

    let filtered = [...appointments];

    if (filter === 'today') {
      filtered = filtered.filter((apt) => {
        const aptDate = new Date(apt.appointmentDate);
        return aptDate.toDateString() === today.toDateString();
      });
    } else if (filter === 'upcoming') {
      filtered = filtered.filter((apt) => {
        const aptDate = new Date(apt.appointmentDate);
        return aptDate >= today && apt.status !== 'cancelled' && apt.status !== 'completed';
      });
      filtered.sort((a, b) => new Date(a.appointmentDate).getTime() - new Date(b.appointmentDate).getTime());
    } else if (filter === 'past') {
      filtered = filtered.filter((apt) => {
        const aptDate = new Date(apt.appointmentDate);
        return aptDate < today || apt.status === 'completed';
      });
      filtered.sort((a, b) => new Date(b.appointmentDate).getTime() - new Date(a.appointmentDate).getTime());
    } else if (filter === 'cancelled') {
      filtered = filtered.filter((apt) => apt.status === 'cancelled');
    }

    setFilteredAppointments(filtered);
  };

  const handleCreateAppointment = async () => {
    try {
      if (!newAppointment.patientId) {
        Alert.alert('Error', 'Please select a patient');
        return;
      }

      await providerAPI.createAppointment(newAppointment);
      Alert.alert('Success', 'Appointment created successfully!');
      setShowCreateModal(false);
      resetForm();
      await loadAppointments();
    } catch (error) {
      console.error('[AppointmentManagement] Error creating:', error);
      Alert.alert('Error', 'Failed to create appointment');
    }
  };

  const handleUpdateStatus = async (appointmentId: string, status: string) => {
    try {
      await providerAPI.updateAppointment(appointmentId, { status: status as any });
      Alert.alert('Success', 'Appointment status updated');
      await loadAppointments();
    } catch (error) {
      console.error('[AppointmentManagement] Error updating:', error);
      Alert.alert('Error', 'Failed to update appointment');
    }
  };

  const handleCancelAppointment = async (appointmentId: string) => {
    Alert.alert(
      'Cancel Appointment',
      'Are you sure you want to cancel this appointment?',
      [
        { text: 'No', style: 'cancel' },
        {
          text: 'Yes',
          style: 'destructive',
          onPress: async () => {
            try {
              await providerAPI.cancelAppointment(appointmentId);
              Alert.alert('Success', 'Appointment cancelled');
              await loadAppointments();
            } catch (error) {
              console.error('[AppointmentManagement] Error cancelling:', error);
              Alert.alert('Error', 'Failed to cancel appointment');
            }
          },
        },
      ]
    );
  };

  const resetForm = () => {
    setNewAppointment({
      patientId: '',
      appointmentDate: new Date().toISOString().split('T')[0],
      appointmentTime: '09:00',
      duration: 30,
      type: 'checkup',
      status: 'scheduled',
      reason: '',
      notes: '',
    });
  };

  const getStatusColor = (status: string): string => {
    switch (status) {
      case 'confirmed': return '#4CAF50';
      case 'scheduled': return '#2196F3';
      case 'in-progress': return '#FF9800';
      case 'completed': return '#9E9E9E';
      case 'cancelled': return '#F44336';
      default: return '#757575';
    }
  };

  if (loading) {
    return (
      <View style={[styles.container, styles.centered]}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>Loading appointments...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      {/* Header */}
      <LinearGradient
        colors={appTheme.gradients.primary}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
        style={styles.header}
      >
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-back" type="material" color="#fff" />
        </TouchableOpacity>
        <Text h3 style={styles.headerTitle}>Appointments</Text>
        <TouchableOpacity onPress={() => setShowCreateModal(true)}>
          <Icon name="add-circle" type="material" color="#fff" size={28} />
        </TouchableOpacity>
      </LinearGradient>

      {/* Filter Chips */}
      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
        style={styles.filterScroll}
        contentContainerStyle={styles.filterContainer}
      >
        <TouchableOpacity
          style={[styles.filterChip, filter === 'today' && styles.filterChipActive]}
          onPress={() => setFilter('today')}
        >
          <Text style={[styles.filterChipText, filter === 'today' && styles.filterChipTextActive]}>
            Today
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterChip, filter === 'upcoming' && styles.filterChipActive]}
          onPress={() => setFilter('upcoming')}
        >
          <Text style={[styles.filterChipText, filter === 'upcoming' && styles.filterChipTextActive]}>
            Upcoming
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterChip, filter === 'past' && styles.filterChipActive]}
          onPress={() => setFilter('past')}
        >
          <Text style={[styles.filterChipText, filter === 'past' && styles.filterChipTextActive]}>
            Past
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.filterChip, filter === 'cancelled' && styles.filterChipActive]}
          onPress={() => setFilter('cancelled')}
        >
          <Text style={[styles.filterChipText, filter === 'cancelled' && styles.filterChipTextActive]}>
            Cancelled
          </Text>
        </TouchableOpacity>
      </ScrollView>

      {/* Appointment List */}
      <ScrollView
        style={styles.listContainer}
        refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
      >
        {filteredAppointments.length > 0 ? (
          filteredAppointments.map((appointment) => (
            <Card key={appointment.id} containerStyle={styles.appointmentCard}>
              <View style={styles.appointmentContent}>
                <View style={styles.appointmentHeader}>
                  <View style={styles.appointmentInfo}>
                    <Text style={styles.patientName}>{appointment.patientName}</Text>
                    <Text style={styles.appointmentDateTime}>
                      {new Date(appointment.appointmentDate).toLocaleDateString()} at {appointment.appointmentTime}
                    </Text>
                    <View style={styles.appointmentMeta}>
                      <Icon name="schedule" type="material" size={16} color="#666" />
                      <Text style={styles.appointmentDuration}>{appointment.duration} min</Text>
                      <Text style={styles.appointmentType}> â€¢ {appointment.type}</Text>
                    </View>
                    {appointment.reason && (
                      <Text style={styles.appointmentReason}>Reason: {appointment.reason}</Text>
                    )}
                  </View>

                  <View style={[styles.statusBadge, { backgroundColor: getStatusColor(appointment.status) }]}>
                    <Text style={styles.statusText}>{appointment.status}</Text>
                  </View>
                </View>

                {/* Action Buttons */}
                <View style={styles.actionButtons}>
                  <TouchableOpacity
                    style={styles.actionButton}
                    onPress={() => navigation.navigate('PatientDetails', { patientId: appointment.patientId })}
                  >
                    <Icon name="person" type="material" size={18} color={theme.colors.primary} />
                    <Text style={styles.actionButtonText}>View Patient</Text>
                  </TouchableOpacity>

                  {appointment.status === 'scheduled' && (
                    <TouchableOpacity
                      style={styles.actionButton}
                      onPress={() => handleUpdateStatus(appointment.id!, 'confirmed')}
                    >
                      <Icon name="check-circle" type="material" size={18} color="#4CAF50" />
                      <Text style={styles.actionButtonText}>Confirm</Text>
                    </TouchableOpacity>
                  )}

                  {(appointment.status === 'scheduled' || appointment.status === 'confirmed') && (
                    <>
                      <TouchableOpacity
                        style={styles.actionButton}
                        onPress={() => handleUpdateStatus(appointment.id!, 'in-progress')}
                      >
                        <Icon name="play-circle" type="material" size={18} color="#FF9800" />
                        <Text style={styles.actionButtonText}>Start</Text>
                      </TouchableOpacity>

                      <TouchableOpacity
                        style={styles.actionButton}
                        onPress={() => handleCancelAppointment(appointment.id!)}
                      >
                        <Icon name="cancel" type="material" size={18} color="#F44336" />
                        <Text style={styles.actionButtonText}>Cancel</Text>
                      </TouchableOpacity>
                    </>
                  )}

                  {appointment.status === 'in-progress' && (
                    <TouchableOpacity
                      style={styles.actionButton}
                      onPress={() => handleUpdateStatus(appointment.id!, 'completed')}
                    >
                      <Icon name="check-circle" type="material" size={18} color="#4CAF50" />
                      <Text style={styles.actionButtonText}>Complete</Text>
                    </TouchableOpacity>
                  )}
                </View>
              </View>
            </Card>
          ))
        ) : (
          <View style={styles.emptyContainer}>
            <Icon name="event-busy" type="material" size={64} color="#CCC" />
            <Text style={styles.emptyText}>No appointments found</Text>
            <Button
              title="Create Appointment"
              onPress={() => setShowCreateModal(true)}
              containerStyle={styles.emptyButton}
            />
          </View>
        )}
      </ScrollView>

      {/* Create Appointment Modal */}
      <Modal
        visible={showCreateModal}
        animationType="slide"
        presentationStyle="pageSheet"
        onRequestClose={() => setShowCreateModal(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <Text h4>Create Appointment</Text>
            <TouchableOpacity onPress={() => setShowCreateModal(false)}>
              <Icon name="close" type="material" size={28} />
            </TouchableOpacity>
          </View>

          <ScrollView style={styles.modalContent}>
            {/* Patient Selection */}
            <Text style={styles.inputLabel}>Patient *</Text>
            <View style={styles.pickerContainer}>
              <Picker
                selectedValue={newAppointment.patientId}
                onValueChange={(value) => setNewAppointment({ ...newAppointment, patientId: value })}
                style={styles.picker}
              >
                <Picker.Item label="Select a patient" value="" />
                {patients.map((patient) => (
                  <Picker.Item
                    key={patient.id}
                    label={`${patient.firstName} ${patient.lastName}`}
                    value={patient.id}
                  />
                ))}
              </Picker>
            </View>

            {/* Date */}
            <Input
              label="Date *"
              value={newAppointment.appointmentDate}
              onChangeText={(text) => setNewAppointment({ ...newAppointment, appointmentDate: text })}
              placeholder="YYYY-MM-DD"
              leftIcon={<Icon name="calendar-today" type="material" size={20} />}
            />

            {/* Time */}
            <Input
              label="Time *"
              value={newAppointment.appointmentTime}
              onChangeText={(text) => setNewAppointment({ ...newAppointment, appointmentTime: text })}
              placeholder="HH:MM"
              leftIcon={<Icon name="access-time" type="material" size={20} />}
            />

            {/* Duration */}
            <Text style={styles.inputLabel}>Duration (minutes)</Text>
            <View style={styles.pickerContainer}>
              <Picker
                selectedValue={newAppointment.duration}
                onValueChange={(value) => setNewAppointment({ ...newAppointment, duration: value })}
                style={styles.picker}
              >
                <Picker.Item label="15 minutes" value={15} />
                <Picker.Item label="30 minutes" value={30} />
                <Picker.Item label="45 minutes" value={45} />
                <Picker.Item label="60 minutes" value={60} />
                <Picker.Item label="90 minutes" value={90} />
              </Picker>
            </View>

            {/* Type */}
            <Text style={styles.inputLabel}>Appointment Type</Text>
            <View style={styles.pickerContainer}>
              <Picker
                selectedValue={newAppointment.type}
                onValueChange={(value) => setNewAppointment({ ...newAppointment, type: value as any })}
                style={styles.picker}
              >
                <Picker.Item label="Check-up" value="checkup" />
                <Picker.Item label="Follow-up" value="follow-up" />
                <Picker.Item label="Urgent" value="urgent" />
                <Picker.Item label="Consultation" value="consultation" />
                <Picker.Item label="Procedure" value="procedure" />
              </Picker>
            </View>

            {/* Reason */}
            <Input
              label="Reason"
              value={newAppointment.reason}
              onChangeText={(text) => setNewAppointment({ ...newAppointment, reason: text })}
              placeholder="Chief complaint or reason for visit"
              multiline
              numberOfLines={2}
            />

            {/* Notes */}
            <Input
              label="Notes"
              value={newAppointment.notes}
              onChangeText={(text) => setNewAppointment({ ...newAppointment, notes: text })}
              placeholder="Additional notes"
              multiline
              numberOfLines={3}
            />

            <Button
              title="Create Appointment"
              onPress={handleCreateAppointment}
              containerStyle={styles.createButton}
              buttonStyle={{ backgroundColor: theme.colors.primary }}
            />
          </ScrollView>
        </View>
      </Modal>
    </View>
  );
};

