/**
 * Payment Success Screen for MediMindPlus
 * Displays success message after successful payment
 */

import React, { useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  ScrollView,
  Animated,
} from 'react-native';
import { useSelector, useDispatch } from 'react-redux';
import { Ionicons } from '@expo/vector-icons';
import { RootState, AppDispatch } from '../store/store';
import { getConsultationById } from '../store/slices/consultationSlice';

interface RouteParams {
  consultationId: string;
}

interface Props {
  route: { params: RouteParams };
  navigation: any;
}

const PaymentSuccessScreen: React.FC<Props> = ({ route, navigation }) => {
  const dispatch = useDispatch<AppDispatch>();
  const { consultationId } = route.params;

  const { currentConsultation } = useSelector(
    (state: RootState) => state.consultation
  );

  const scaleAnim = new Animated.Value(0);

  useEffect(() => {
    // Load consultation details
    dispatch(getConsultationById(consultationId));

    // Animate success icon
    Animated.spring(scaleAnim, {
      toValue: 1,
      tension: 50,
      friction: 7,
      useNativeDriver: true,
    }).start();
  }, [consultationId]);

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const formatTime = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const handleViewConsultation = () => {
    navigation.navigate('ConsultationDetails', { consultationId });
  };

  const handleGoHome = () => {
    navigation.navigate('Home');
  };

  const handleViewReceipt = () => {
    navigation.navigate('PaymentHistory');
  };

  return (
    <ScrollView style={styles.container} contentContainerStyle={styles.content}>
      {/* Success Animation */}
      <Animated.View
        style={[
          styles.successIconContainer,
          { transform: [{ scale: scaleAnim }] },
        ]}
      >
        <Ionicons name="checkmark-circle" size={120} color="#34C759" />
      </Animated.View>

      {/* Success Message */}
      <View style={styles.messageContainer}>
        <Text style={styles.successTitle}>Payment Successful!</Text>
        <Text style={styles.successSubtitle}>
          Your consultation has been confirmed
        </Text>
      </View>

      {/* Consultation Summary */}
      {currentConsultation && (
        <View style={styles.card}>
          <Text style={styles.cardTitle}>Consultation Details</Text>

          <View style={styles.detailRow}>
            <Ionicons name="calendar-outline" size={24} color="#007AFF" />
            <View style={styles.detailContent}>
              <Text style={styles.detailLabel}>Date</Text>
              <Text style={styles.detailValue}>
                {formatDate(currentConsultation.scheduledStart)}
              </Text>
            </View>
          </View>

          <View style={styles.detailRow}>
            <Ionicons name="time-outline" size={24} color="#007AFF" />
            <View style={styles.detailContent}>
              <Text style={styles.detailLabel}>Time</Text>
              <Text style={styles.detailValue}>
                {formatTime(currentConsultation.scheduledStart)}
              </Text>
            </View>
          </View>

          {currentConsultation.provider && (
            <View style={styles.detailRow}>
              <Ionicons name="person-outline" size={24} color="#007AFF" />
              <View style={styles.detailContent}>
                <Text style={styles.detailLabel}>Provider</Text>
                <Text style={styles.detailValue}>
                  Dr. {currentConsultation.provider.firstName}{' '}
                  {currentConsultation.provider.lastName}
                </Text>
              </View>
            </View>
          )}

          <View style={styles.detailRow}>
            <Ionicons name="cash-outline" size={24} color="#007AFF" />
            <View style={styles.detailContent}>
              <Text style={styles.detailLabel}>Amount Paid</Text>
              <Text style={styles.detailValue}>
                ${(currentConsultation.consultationFee / 100).toFixed(2)}
              </Text>
            </View>
          </View>
        </View>
      )}

      {/* Next Steps Card */}
      <View style={styles.card}>
        <Text style={styles.cardTitle}>What's Next?</Text>

        <View style={styles.stepRow}>
          <View style={styles.stepNumber}>
            <Text style={styles.stepNumberText}>1</Text>
          </View>
          <View style={styles.stepContent}>
            <Text style={styles.stepTitle}>Check Your Email</Text>
            <Text style={styles.stepText}>
              We've sent a confirmation email with consultation details
            </Text>
          </View>
        </View>

        <View style={styles.stepRow}>
          <View style={styles.stepNumber}>
            <Text style={styles.stepNumberText}>2</Text>
          </View>
          <View style={styles.stepContent}>
            <Text style={styles.stepTitle}>Join the Video Call</Text>
            <Text style={styles.stepText}>
              You'll receive a notification when it's time to join
            </Text>
          </View>
        </View>

        <View style={styles.stepRow}>
          <View style={styles.stepNumber}>
            <Text style={styles.stepNumberText}>3</Text>
          </View>
          <View style={styles.stepContent}>
            <Text style={styles.stepTitle}>Share Your Health Data</Text>
            <Text style={styles.stepText}>
              Optionally share your vitals during the consultation
            </Text>
          </View>
        </View>
      </View>

      {/* Action Buttons */}
      <View style={styles.actionContainer}>
        <TouchableOpacity
          style={styles.primaryButton}
          onPress={handleViewConsultation}
        >
          <Text style={styles.primaryButtonText}>View Consultation</Text>
          <Ionicons name="arrow-forward" size={20} color="#FFFFFF" />
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.secondaryButton}
          onPress={handleViewReceipt}
        >
          <Ionicons name="receipt-outline" size={20} color="#007AFF" />
          <Text style={styles.secondaryButtonText}>View Receipt</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.textButton} onPress={handleGoHome}>
          <Text style={styles.textButtonText}>Go to Home</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F5',
  },
  content: {
    padding: 20,
    alignItems: 'center',
  },
  successIconContainer: {
    marginTop: 40,
    marginBottom: 24,
  },
  messageContainer: {
    alignItems: 'center',
    marginBottom: 32,
  },
  successTitle: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#000',
    marginBottom: 8,
  },
  successSubtitle: {
    fontSize: 16,
    color: '#666',
    textAlign: 'center',
  },
  card: {
    width: '100%',
    backgroundColor: '#FFFFFF',
    borderRadius: 12,
    padding: 20,
    marginBottom: 16,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  cardTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#000',
    marginBottom: 16,
  },
  detailRow: {
    flexDirection: 'row',
    alignItems: 'flex-start',
    marginBottom: 16,
  },
  detailContent: {
    flex: 1,
    marginLeft: 12,
  },
  detailLabel: {
    fontSize: 14,
    color: '#666',
    marginBottom: 4,
  },
  detailValue: {
    fontSize: 16,
    fontWeight: '600',
    color: '#000',
  },
  stepRow: {
    flexDirection: 'row',
    alignItems: 'flex-start',
    marginBottom: 16,
  },
  stepNumber: {
    width: 32,
    height: 32,
    borderRadius: 16,
    backgroundColor: '#007AFF',
    justifyContent: 'center',
    alignItems: 'center',
  },
  stepNumberText: {
    color: '#FFFFFF',
    fontSize: 16,
    fontWeight: 'bold',
  },
  stepContent: {
    flex: 1,
    marginLeft: 12,
  },
  stepTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: '#000',
    marginBottom: 4,
  },
  stepText: {
    fontSize: 14,
    color: '#666',
    lineHeight: 20,
  },
  actionContainer: {
    width: '100%',
    marginTop: 8,
  },
  primaryButton: {
    backgroundColor: '#007AFF',
    paddingVertical: 16,
    borderRadius: 12,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 12,
  },
  primaryButtonText: {
    color: '#FFFFFF',
    fontSize: 18,
    fontWeight: 'bold',
    marginRight: 8,
  },
  secondaryButton: {
    backgroundColor: '#FFFFFF',
    paddingVertical: 16,
    borderRadius: 12,
    borderWidth: 1,
    borderColor: '#007AFF',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 12,
  },
  secondaryButtonText: {
    color: '#007AFF',
    fontSize: 16,
    fontWeight: '600',
    marginLeft: 8,
  },
  textButton: {
    paddingVertical: 16,
    justifyContent: 'center',
    alignItems: 'center',
  },
  textButtonText: {
    color: '#007AFF',
    fontSize: 16,
    fontWeight: '500',
  },
});

export default PaymentSuccessScreen;
