import React, { useState, useEffect } from 'react';
import {
  View,
  StyleSheet,
  ScrollView,
  ActivityIndicator,
  TouchableOpacity,
  Alert,
} from 'react-native';
import { Text, Input, Button, Icon, Card } from 'react-native-elements';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../types/navigation';
import { theme } from '../theme/theme';
import { LinearGradient } from 'expo-linear-gradient';
import axios from 'axios';

type HelpCenterScreenProps = {
  navigation: StackNavigationProp<RootStackParamList, 'HelpCenter'>;
};

interface HelpArticle {
  id: string;
  title: string;
  category: string;
  summary: string;
  icon?: string;
}

const HelpCenterScreen: React.FC<HelpCenterScreenProps> = ({ navigation }) => {
  const [loading, setLoading] = useState(true);
  const [searching, setSearching] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
  const [articles, setArticles] = useState<HelpArticle[]>([]);
  const [categories, setCategories] = useState<string[]>([]);
  const [expandedArticle, setExpandedArticle] = useState<string | null>(null);

  useEffect(() => {
    loadHelpData();
  }, []);

  const loadHelpData = async () => {
    try {
      setLoading(true);
      const [articlesRes, categoriesRes] = await Promise.all([
        axios.get('http://localhost:3000/api/settings/help/articles'),
        axios.get('http://localhost:3000/api/settings/help/categories'),
      ]);

      setArticles(articlesRes.data);
      setCategories(categoriesRes.data);
    } catch (error) {
      console.error('Error loading help data:', error);
      Alert.alert('Error', 'Failed to load help articles');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      loadHelpData();
      return;
    }

    try {
      setSearching(true);
      const response = await axios.get('http://localhost:3000/api/settings/help/search', {
        params: { q: searchQuery },
      });
      setArticles(response.data);
      setSelectedCategory(null);
    } catch (error) {
      console.error('Error searching:', error);
      Alert.alert('Error', 'Search failed');
    } finally {
      setSearching(false);
    }
  };

  const handleCategoryFilter = async (category: string) => {
    if (selectedCategory === category) {
      setSelectedCategory(null);
      loadHelpData();
      return;
    }

    try {
      setLoading(true);
      setSelectedCategory(category);
      const response = await axios.get('http://localhost:3000/api/settings/help/articles', {
        params: { category },
      });
      setArticles(response.data);
      setSearchQuery('');
    } catch (error) {
      console.error('Error filtering by category:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleArticlePress = async (articleId: string) => {
    if (expandedArticle === articleId) {
      setExpandedArticle(null);
      return;
    }

    try {
      const response = await axios.get(`http://localhost:3000/api/settings/help/article/${articleId}`);
      setExpandedArticle(articleId);
    } catch (error) {
      console.error('Error loading article:', error);
    }
  };

  const getIconForCategory = (category: string): string => {
    const iconMap: { [key: string]: string } = {
      'Getting Started': 'rocket',
      'Account Management': 'person',
      'Health Features': 'fitness',
      'Privacy & Security': 'lock',
      'Troubleshooting': 'build',
    };
    return iconMap[category] || 'help-outline';
  };

  if (loading && articles.length === 0) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>Loading help articles...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <LinearGradient
        colors={['#667eea', '#764ba2']}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
        style={styles.header}
      >
        <View style={styles.iconContainer}>
          <Icon name="help" type="material" color="white" size={60} />
        </View>
        <Text style={styles.headerTitle}>Help Center</Text>
        <Text style={styles.headerSubtitle}>Find answers to your questions</Text>
      </LinearGradient>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        <View style={styles.content}>
          <View style={styles.searchContainer}>
            <Input
              placeholder="Search for help..."
              value={searchQuery}
              onChangeText={setSearchQuery}
              onSubmitEditing={handleSearch}
              leftIcon={<Icon name="search" type="material" size={24} color={theme.colors.textSecondary} />}
              rightIcon={
                searchQuery ? (
                  <TouchableOpacity
                    onPress={() => {
                      setSearchQuery('');
                      loadHelpData();
                    }}
                  >
                    <Icon name="close" type="material" size={20} color={theme.colors.textSecondary} />
                  </TouchableOpacity>
                ) : undefined
              }
              containerStyle={styles.searchInputContainer}
              inputContainerStyle={styles.searchInput}
              inputStyle={styles.searchText}
            />
            <Button
              title={searching ? 'Searching...' : 'Search'}
              onPress={handleSearch}
              disabled={searching || !searchQuery.trim()}
              loading={searching}
              buttonStyle={styles.searchButton}
              containerStyle={styles.searchButtonContainer}
            />
          </View>

          <View style={styles.categoriesContainer}>
            <Text style={styles.categoriesTitle}>Browse by Category</Text>
            <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.categoriesScroll}>
              {categories.map((category) => (
                <TouchableOpacity
                  key={category}
                  style={[
                    styles.categoryChip,
                    selectedCategory === category && styles.categoryChipSelected,
                  ]}
                  onPress={() => handleCategoryFilter(category)}
                >
                  <Icon
                    name={getIconForCategory(category)}
                    type="material"
                    size={20}
                    color={selectedCategory === category ? 'white' : theme.colors.primary}
                    containerStyle={{ marginRight: 6 }}
                  />
                  <Text
                    style={[
                      styles.categoryChipText,
                      selectedCategory === category && styles.categoryChipTextSelected,
                    ]}
                  >
                    {category}
                  </Text>
                </TouchableOpacity>
              ))}
            </ScrollView>
          </View>

          {articles.length === 0 ? (
            <Card containerStyle={styles.noResultsCard}>
              <Icon name="search-off" type="material" size={64} color={theme.colors.textSecondary} />
              <Text style={styles.noResultsText}>No articles found</Text>
              <Text style={styles.noResultsSubtext}>
                Try adjusting your search or browse by category
              </Text>
            </Card>
          ) : (
            <View style={styles.articlesContainer}>
              <Text style={styles.articlesTitle}>
                {selectedCategory
                  ? `${selectedCategory} (${articles.length})`
                  : searchQuery
                  ? `Search Results (${articles.length})`
                  : `All Articles (${articles.length})`}
              </Text>

              {articles.map((article) => (
                <TouchableOpacity
                  key={article.id}
                  onPress={() => handleArticlePress(article.id)}
                  activeOpacity={0.7}
                >
                  <Card containerStyle={styles.articleCard}>
                    <View style={styles.articleHeader}>
                      <View style={styles.articleIconContainer}>
                        <Icon
                          name={article.icon || getIconForCategory(article.category)}
                          type="material"
                          size={28}
                          color={theme.colors.primary}
                        />
                      </View>
                      <View style={styles.articleInfo}>
                        <Text style={styles.articleTitle}>{article.title}</Text>
                        <Text style={styles.articleCategory}>{article.category}</Text>
                      </View>
                      <Icon
                        name={expandedArticle === article.id ? 'keyboard-arrow-up' : 'keyboard-arrow-down'}
                        type="material"
                        size={24}
                        color={theme.colors.textSecondary}
