import React, { useState, useEffect } from 'react';
import {
  View,
  ScrollView,
  StyleSheet,
  RefreshControl,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
} from 'react-native';
import { Text, Card, Icon } from 'react-native-elements';
import { useTheme } from 'react-native-elements';
import providerAPI, { ProviderDashboardStats } from '../services/providerAPI';

interface ProviderDashboardScreenProps {
  navigation: any;
}

const ProviderDashboardScreen: React.FC<ProviderDashboardScreenProps> = ({ navigation }) => {
  const { theme } = useTheme();
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [stats, setStats] = useState<ProviderDashboardStats | null>(null);

  useEffect(() => {
    loadDashboard();
  }, []);

  const loadDashboard = async () => {
    try {
      setLoading(true);
      const data = await providerAPI.getDashboardStats();
      setStats(data);
    } catch (error) {
      console.error('[ProviderDashboard] Error loading:', error);
      Alert.alert('Error', 'Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadDashboard();
    setRefreshing(false);
  };

  if (loading) {
    return (
      <View style={[styles.container, styles.centered]}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>Loading dashboard...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      {/* Navigation Header */}
      <View style={styles.navigationHeader}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-back" type="material" color="#FFFFFF" size={24} />
          <Text style={styles.backButtonText}>Back</Text>
        </TouchableOpacity>
      </View>

      <ScrollView
        style={styles.scrollContainer}
        contentContainerStyle={styles.contentContainer}
        refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
      >
        {/* Header */}
        <View style={styles.header}>
          <Text h3 style={styles.headerTitle}>Provider Portal</Text>
          <Text style={styles.headerSubtitle}>Welcome back, Doctor</Text>
        </View>

      {/* Statistics Cards */}
      <View style={styles.statsGrid}>
        <TouchableOpacity
          style={[styles.statCard, { backgroundColor: theme.colors.primary }]}
          onPress={() => navigation.navigate('PatientManagement')}
        >
          <Icon name="people" type="material" color="#FFFFFF" size={32} />
          <Text h2 style={styles.statNumber}>{stats?.totalPatients || 0}</Text>
          <Text style={styles.statLabel}>Total Patients</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.statCard, { backgroundColor: '#4CAF50' }]}
          onPress={() => navigation.navigate('AppointmentManagement', { filter: 'today' })}
        >
          <Icon name="event" type="material" color="#FFFFFF" size={32} />
          <Text h2 style={styles.statNumber}>{stats?.todayAppointments || 0}</Text>
          <Text style={styles.statLabel}>Today's Appointments</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.statCard, { backgroundColor: '#FF9800' }]}
          onPress={() => navigation.navigate('PrescriptionManagement', { filter: 'pending' })}
        >
          <Icon name="medication" type="material" color="#FFFFFF" size={32} />
          <Text h2 style={styles.statNumber}>{stats?.pendingPrescriptions || 0}</Text>
          <Text style={styles.statLabel}>Pending Prescriptions</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.statCard, { backgroundColor: '#F44336' }]}
          onPress={() => navigation.navigate('PatientManagement', { filter: 'critical' })}
        >
          <Icon name="warning" type="material" color="#FFFFFF" size={32} />
          <Text h2 style={styles.statNumber}>{stats?.criticalAlerts || 0}</Text>
          <Text style={styles.statLabel}>Critical Alerts</Text>
        </TouchableOpacity>
      </View>

      {/* Upcoming Appointments */}
      <Card containerStyle={styles.card}>
        <View style={styles.cardHeader}>
          <View style={styles.cardTitleRow}>
            <Icon name="event" type="material" color={theme.colors.primary} size={24} />
            <Text h4 style={styles.cardTitle}>Upcoming Appointments</Text>
          </View>
          <TouchableOpacity onPress={() => navigation.navigate('AppointmentManagement')}>
            <Text style={[styles.linkText, { color: theme.colors.primary }]}>View All</Text>
          </TouchableOpacity>
        </View>
        {stats?.upcomingAppointments && stats.upcomingAppointments.length > 0 ? (
          stats.upcomingAppointments.slice(0, 5).map((appointment, index) => (
            <TouchableOpacity
              key={appointment.id || index}
              style={styles.appointmentItem}
              onPress={() => navigation.navigate('PatientDetails', { patientId: appointment.patientId })}
            >
              <View style={styles.appointmentInfo}>
                <Text style={styles.appointmentName}>{appointment.patientName}</Text>
                <Text style={styles.appointmentTime}>
                  {new Date(appointment.appointmentDate).toLocaleDateString()} at {appointment.appointmentTime}
                </Text>
                <Text style={styles.appointmentType}>{appointment.type}</Text>
              </View>
              <View style={[styles.statusBadge, { backgroundColor: getStatusColor(appointment.status) }]}>
                <Text style={styles.statusText}>{appointment.status}</Text>
              </View>
            </TouchableOpacity>
          ))
        ) : (
          <Text style={styles.emptyText}>No upcoming appointments</Text>
        )}
      </Card>

      {/* Recent Patients */}
      <Card containerStyle={styles.card}>
        <View style={styles.cardHeader}>
          <View style={styles.cardTitleRow}>
            <Icon name="people" type="material" color={theme.colors.primary} size={24} />
            <Text h4 style={styles.cardTitle}>Recent Patients</Text>
          </View>
          <TouchableOpacity onPress={() => navigation.navigate('PatientManagement')}>
            <Text style={[styles.linkText, { color: theme.colors.primary }]}>View All</Text>
          </TouchableOpacity>
        </View>
        {stats?.recentPatients && stats.recentPatients.length > 0 ? (
          stats.recentPatients.slice(0, 5).map((patient, index) => (
            <TouchableOpacity
              key={patient.id || index}
              style={styles.patientItem}
              onPress={() => navigation.navigate('PatientDetails', { patientId: patient.id })}
            >
              <View style={styles.patientAvatar}>
                <Text style={styles.avatarText}>
                  {patient.firstName.charAt(0)}{patient.lastName.charAt(0)}
                </Text>
              </View>
              <View style={styles.patientInfo}>
                <Text style={styles.patientName}>{patient.firstName} {patient.lastName}</Text>
                <Text style={styles.patientDetail}>
                  Last visit: {patient.lastVisit ? new Date(patient.lastVisit).toLocaleDateString() : 'N/A'}
                </Text>
                {patient.riskScore && (
                  <View style={styles.riskBadge}>
                    <Text style={styles.riskText}>Risk: {patient.riskScore}%</Text>
                  </View>
                )}
              </View>
              <Icon name="chevron-right" type="material" color={theme.colors.grey3} />
            </TouchableOpacity>
          ))
        ) : (
