const getStatusColor = (status: string): string => {
  switch (status) {
    case 'confirmed':
      return '#4CAF50';
    case 'scheduled':
      return '#2196F3';
    case 'in-progress':
      return '#FF9800';
    case 'completed':
      return '#9E9E9E';
    case 'cancelled':
      return '#F44336';
    default:
      return '#757575';
  }
};

