import React from 'react';
import { View, StyleSheet, ScrollView } from 'react-native';
import { Text, Button, Icon } from 'react-native-elements';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../types/navigation';
import { theme } from '../theme/theme';

type ChangePasswordScreenProps = {
  navigation: StackNavigationProp<RootStackParamList, 'ChangePassword'>;
};

const ChangePasswordScreen: React.FC<ChangePasswordScreenProps> = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Icon
          name="lock"
          type="material"
          color={theme.colors.primary}
          size={80}
          containerStyle={styles.iconContainer}
        />
        <Text style={styles.title}>Change Password</Text>
        <Text style={styles.subtitle}>Update your account password</Text>
      </View>

      <ScrollView style={styles.content}>
        <Text style={styles.infoText}>
          Password change functionality will be available soon.
        </Text>
        <Text style={styles.descriptionText}>
          You'll be able to securely update your password and enhance your account security.
        </Text>
      </ScrollView>

      <View style={styles.footer}>
        <Button
          title="Go Back"
          onPress={() => navigation.goBack()}
          buttonStyle={styles.backButton}
          icon={
            <Icon
              name="arrow-back"
              type="material"
              color="white"
              size={20}
              containerStyle={{ marginRight: 10 }}
            />
          }
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: theme.colors.background,
  },
  header: {
    alignItems: 'center',
    paddingVertical: 40,
    backgroundColor: 'white',
  },
  iconContainer: {
    marginBottom: 20,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: theme.colors.text,
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 16,
    color: theme.colors.textSecondary,
  },
  content: {
    flex: 1,
    padding: 20,
  },
  infoText: {
    fontSize: 18,
    fontWeight: '600',
    color: theme.colors.text,
    marginBottom: 12,
    textAlign: 'center',
  },
  descriptionText: {
    fontSize: 16,
    color: theme.colors.textSecondary,
    lineHeight: 24,
    textAlign: 'center',
  },
  footer: {
    padding: 20,
    backgroundColor: 'white',
  },
  backButton: {
    backgroundColor: theme.colors.primary,
    borderRadius: 8,
    paddingVertical: 12,
  },
});

export default ChangePasswordScreen;
