import React, { useState, useEffect } from 'react';
import {
  View,
  StyleSheet,
  ScrollView,
  ActivityIndicator,
  Switch,
  Alert,
  Platform,
} from 'react-native';
import { Text, Button, Icon, Card } from 'react-native-elements';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../types/navigation';
import { theme } from '../theme/theme';
import { LinearGradient } from 'expo-linear-gradient';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';

type PrivacySettingsScreenProps = {
  navigation: StackNavigationProp<RootStackParamList, 'PrivacySettings'>;
};

interface PrivacySettings {
  share_health_data: boolean;
  share_with_providers: boolean;
  marketing_emails: boolean;
  research_participation: boolean;
  data_retention_days: number;
}

const PrivacySettingsScreen: React.FC<PrivacySettingsScreenProps> = ({ navigation }) => {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [settings, setSettings] = useState<PrivacySettings | null>(null);
  const [hasChanges, setHasChanges] = useState(false);

  useEffect(() => {
    loadPrivacySettings();
  }, []);

  const loadPrivacySettings = async () => {
    try {
      setLoading(true);
      const token = await AsyncStorage.getItem('authToken');

      if (!token) {
        Alert.alert('Error', 'Authentication required');
        navigation.goBack();
        return;
      }

      const response = await axios.get('http://localhost:3000/api/settings/privacy/settings', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setSettings(response.data);
    } catch (error) {
      console.error('Error loading privacy settings:', error);
      Alert.alert('Error', 'Failed to load privacy settings');
    } finally {
      setLoading(false);
    }
  };

  const handleToggle = (key: keyof PrivacySettings) => {
    if (settings) {
      setSettings({ ...settings, [key]: !settings[key] });
      setHasChanges(true);
    }
  };

  const handleSaveSettings = async () => {
    if (!settings) return;

    try {
      setSaving(true);
      const token = await AsyncStorage.getItem('authToken');

      if (!token) {
        Alert.alert('Error', 'Authentication required');
        return;
      }

      await axios.put(
        'http://localhost:3000/api/settings/privacy/settings',
        settings,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      Alert.alert('Success', 'Privacy settings updated successfully');
      setHasChanges(false);
    } catch (error) {
      console.error('Error saving privacy settings:', error);
      Alert.alert('Error', 'Failed to save privacy settings');
    } finally {
      setSaving(false);
    }
  };

  const handleExportData = async () => {
    try {
      const token = await AsyncStorage.getItem('authToken');

      if (!token) {
        Alert.alert('Error', 'Authentication required');
        return;
      }

      Alert.alert(
        'Export Data',
        'Your data export request has been received. You will receive an email with a download link within 24 hours.',
        [
          {
            text: 'Cancel',
            style: 'cancel',
          },
          {
            text: 'Confirm',
            onPress: async () => {
              try {
                await axios.get('http://localhost:3000/api/settings/privacy/export', {
                  headers: { Authorization: `Bearer ${token}` },
                });
                Alert.alert('Success', 'Data export request submitted successfully');
              } catch (error) {
                console.error('Error exporting data:', error);
                Alert.alert('Error', 'Failed to export data');
              }
            },
          },
        ]
      );
    } catch (error) {
      console.error('Error in export data:', error);
    }
  };

  const handleDeleteData = async () => {
    Alert.alert(
      'Delete All Data',
      'This will permanently delete all your health data, appointments, and account information. This action cannot be undone. Are you sure you want to continue?',
      [
        {
          text: 'Cancel',
          style: 'cancel',
        },
        {
          text: 'Delete',
          style: 'destructive',
          onPress: async () => {
            try {
              const token = await AsyncStorage.getItem('authToken');

              if (!token) {
                Alert.alert('Error', 'Authentication required');
                return;
              }

              await axios.delete('http://localhost:3000/api/settings/privacy/data', {
                headers: { Authorization: `Bearer ${token}` },
              });

              Alert.alert(
                'Account Deleted',
                'Your account and all data have been permanently deleted.',
                [
                  {
                    text: 'OK',
                    onPress: () => {
                      AsyncStorage.clear();
                      navigation.reset({
                        index: 0,
                        routes: [{ name: 'Login' as any }],
                      });
                    },
                  },
                ]
              );
            } catch (error) {
              console.error('Error deleting data:', error);
              Alert.alert('Error', 'Failed to delete data');
            }
          },
        },
      ]
    );
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>Loading privacy settings...</Text>
      </View>
    );
  }

  if (!settings) {
    return (
      <View style={styles.loadingContainer}>
        <Icon name="error-outline" type="material" size={64} color={theme.colors.textSecondary} />
        <Text style={styles.errorText}>Failed to load privacy settings</Text>
        <Button
          title="Try Again"
          onPress={loadPrivacySettings}
          buttonStyle={styles.retryButton}
        />
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <LinearGradient
        colors={theme.gradients.primary.colors}
        start={theme.gradients.primary.start}
        end={theme.gradients.primary.end}
        style={styles.header}
      >
        <View style={styles.iconContainer}>
          <Icon name="privacy-tip" type="material" color="white" size={60} />
        </View>
        <Text style={styles.headerTitle}>Privacy Settings</Text>
        <Text style={styles.headerSubtitle}>Control your data and privacy</Text>
      </LinearGradient>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        <View style={styles.content}>
          <Card containerStyle={styles.hipaaCard}>
            <View style={styles.hipaaHeader}>
              <Icon name="verified-user" type="material" color="#4CAF50" size={28} />
              <Text style={styles.hipaaTitle}>HIPAA Protected</Text>
            </View>
            <Text style={styles.hipaaText}>
              Your health information is protected under HIPAA regulations. We maintain the
              highest standards of data privacy and security.
            </Text>
          </Card>

          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Data Sharing Preferences</Text>

            <Card containerStyle={styles.settingCard}>
              <View style={styles.settingRow}>
                <View style={styles.settingInfo}>
                  <View style={styles.settingHeader}>
                    <Icon
                      name="share"
                      type="material"
                      size={24}
                      color={theme.colors.primary}
                      containerStyle={styles.settingIcon}
                    />
                    <Text style={styles.settingLabel}>Share Health Data</Text>
                  </View>
                  <Text style={styles.settingDescription}>
                    Allow sharing health data with authorized healthcare providers
                  </Text>
                </View>
                <Switch
                  value={settings.share_health_data}
                  onValueChange={() => handleToggle('share_health_data')}
                  trackColor={{ false: '#E0E0E0', true: theme.colors.primary }}
                  thumbColor={Platform.OS === 'ios' ? undefined : 'white'}
                />
              </View>
            </Card>

            <Card containerStyle={styles.settingCard}>
              <View style={styles.settingRow}>
                <View style={styles.settingInfo}>
                  <View style={styles.settingHeader}>
                    <Icon
                      name="local-hospital"
                      type="material"
                      size={24}
                      color={theme.colors.primary}
                      containerStyle={styles.settingIcon}
                    />
                    <Text style={styles.settingLabel}>Share With Providers</Text>
                  </View>
                  <Text style={styles.settingDescription}>
                    Allow your healthcare providers to access your medical records
                  </Text>
                </View>
                <Switch
                  value={settings.share_with_providers}
                  onValueChange={() => handleToggle('share_with_providers')}
                  trackColor={{ false: '#E0E0E0', true: theme.colors.primary }}
                  thumbColor={Platform.OS === 'ios' ? undefined : 'white'}
                />
              </View>
            </Card>
          </View>

          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Communication Preferences</Text>

            <Card containerStyle={styles.settingCard}>
              <View style={styles.settingRow}>
                <View style={styles.settingInfo}>
                  <View style={styles.settingHeader}>
                    <Icon
                      name="mail"
                      type="material"
                      size={24}
                      color={theme.colors.primary}
                      containerStyle={styles.settingIcon}
                    />
                    <Text style={styles.settingLabel}>Marketing Emails</Text>
                  </View>
                  <Text style={styles.settingDescription}>
                    Receive emails about new features and health tips
                  </Text>
                </View>
                <Switch
                  value={settings.marketing_emails}
                  onValueChange={() => handleToggle('marketing_emails')}
                  trackColor={{ false: '#E0E0E0', true: theme.colors.primary }}
                  thumbColor={Platform.OS === 'ios' ? undefined : 'white'}
                />
              </View>
            </Card>

            <Card containerStyle={styles.settingCard}>
              <View style={styles.settingRow}>
                <View style={styles.settingInfo}>
                  <View style={styles.settingHeader}>
                    <Icon
                      name="science"
                      type="material"
                      size={24}
                      color={theme.colors.primary}
                      containerStyle={styles.settingIcon}
                    />
                    <Text style={styles.settingLabel}>Research Participation</Text>
                  </View>
                  <Text style={styles.settingDescription}>
                    Allow anonymized data to be used for medical research
                  </Text>
                </View>
                <Switch
                  value={settings.research_participation}
                  onValueChange={() => handleToggle('research_participation')}
                  trackColor={{ false: '#E0E0E0', true: theme.colors.primary }}
                  thumbColor={Platform.OS === 'ios' ? undefined : 'white'}
                />
              </View>
            </Card>
          </View>

          {hasChanges && (
            <Card containerStyle={styles.saveCard}>
              <Icon name="warning" type="material" size={24} color="#FF9800" />
              <Text style={styles.saveText}>You have unsaved changes</Text>
              <Button
                title={saving ? 'Saving...' : 'Save Changes'}
                onPress={handleSaveSettings}
                disabled={saving}
                loading={saving}
                buttonStyle={styles.saveButton}
                containerStyle={styles.saveButtonContainer}
                icon={
                  !saving && (
                    <Icon
                      name="save"
                      type="material"
                      color="white"
                      size={20}
                      containerStyle={{ marginRight: 10 }}
                    />
                  )
                }
              />
            </Card>
          )}

          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Data Management</Text>

            <Card containerStyle={styles.actionCard}>
              <View style={styles.actionHeader}>
                <Icon name="download" type="material" size={28} color={theme.colors.primary} />
                <Text style={styles.actionTitle}>Export Your Data</Text>
              </View>
              <Text style={styles.actionDescription}>
                Download a copy of all your health data, appointments, and account information
                in JSON format.
              </Text>
              <Button
                title="Request Data Export"
                onPress={handleExportData}
                buttonStyle={styles.exportButton}
                titleStyle={styles.exportButtonTitle}
                type="outline"
                icon={
                  <Icon
                    name="file-download"
                    type="material"
                    color={theme.colors.primary}
                    size={20}
                    containerStyle={{ marginRight: 10 }}
                  />
                }
              />
            </Card>

            <Card containerStyle={styles.deleteCard}>
              <View style={styles.deleteHeader}>
                <Icon name="delete-forever" type="material" size={28} color="#F44336" />
                <Text style={styles.deleteTitle}>Delete All Data</Text>
              </View>
              <Text style={styles.deleteDescription}>
                Permanently delete your account and all associated data. This action cannot be
                undone and you will need to create a new account to use our services again.
              </Text>
              <Button
                title="Delete My Account"
                onPress={handleDeleteData}
                buttonStyle={styles.deleteButton}
                titleStyle={styles.deleteButtonTitle}
                icon={
                  <Icon
                    name="warning"
                    type="material"
                    color="white"
                    size={20}
                    containerStyle={{ marginRight: 10 }}
                  />
                }
              />
            </Card>
          </View>

          <Card containerStyle={styles.infoCard}>
            <Card.Title style={styles.infoTitle}>Need Help?</Card.Title>
            <Card.Divider />
            <Text style={styles.infoText}>
              If you have questions about your privacy settings or data management, please
              contact our privacy team.
            </Text>
            <View style={styles.contactMethod}>
              <Icon name="email" type="material" size={18} color={theme.colors.primary} />
              <Text style={styles.contactText}>privacy@medimind.com</Text>
            </View>
            <View style={styles.contactMethod}>
              <Icon name="phone" type="material" size={18} color={theme.colors.primary} />
              <Text style={styles.contactText}>1-800-MEDIMIND</Text>
            </View>
          </Card>
        </View>
      </ScrollView>
    </View>
  );
};

