/**
 * Voice Analysis Screen
 * Modern UI for voice biomarker analysis with real-time waveform visualization
 */

import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Animated,
  Dimensions,
  ActivityIndicator,
  Alert
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import { Audio } from 'expo-av';
import voiceBiomarkerML, { VoiceAnalysisResult } from '../services/ai/voiceBiomarkerML';

const { width } = Dimensions.get('window');

const VoiceAnalysisScreen: React.FC = () => {
  const [recording, setRecording] = useState<Audio.Recording | null>(null);
  const [isRecording, setIsRecording] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState<VoiceAnalysisResult | null>(null);
  const [recordingDuration, setRecordingDuration] = useState(0);
  const [audioLevel, setAudioLevel] = useState(0);

  // Animation values
  const pulseAnim = useRef(new Animated.Value(1)).current;
  const waveformAnim = useRef(new Animated.Value(0)).current;
  const fadeAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    initializeAudio();
    return () => {
      if (recording) {
        recording.stopAndUnloadAsync();
      }
    };
  }, []);

  useEffect(() => {
    if (isRecording) {
      // Pulse animation for recording indicator
      Animated.loop(
        Animated.sequence([
          Animated.timing(pulseAnim, {
            toValue: 1.2,
            duration: 800,
            useNativeDriver: true,
          }),
          Animated.timing(pulseAnim, {
            toValue: 1,
            duration: 800,
            useNativeDriver: true,
          }),
        ])
      ).start();

      // Waveform animation
      Animated.loop(
        Animated.timing(waveformAnim, {
          toValue: 1,
          duration: 2000,
          useNativeDriver: true,
        })
      ).start();
    } else {
      pulseAnim.stopAnimation();
      waveformAnim.stopAnimation();
    }
  }, [isRecording]);

  const initializeAudio = async () => {
    try {
      await Audio.requestPermissionsAsync();
      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
      });
    } catch (error) {
      console.error('Failed to initialize audio:', error);
    }
  };

  const startRecording = async () => {
    try {
      const { recording: newRecording } = await Audio.Recording.createAsync(
        Audio.RecordingOptionsPresets.HIGH_QUALITY
      );

      setRecording(newRecording);
      setIsRecording(true);
      setRecordingDuration(0);

      // Monitor recording duration
      const durationInterval = setInterval(() => {
        setRecordingDuration((prev) => prev + 1);
      }, 1000);

      // Monitor audio level
      newRecording.setOnRecordingStatusUpdate((status) => {
        if (status.isRecording && status.metering !== undefined) {
          setAudioLevel(Math.abs(status.metering) / 160); // Normalize to 0-1
        }
      });

      // Auto-stop after 30 seconds
      setTimeout(() => {
        stopRecording();
        clearInterval(durationInterval);
      }, 30000);

    } catch (error) {
      console.error('Failed to start recording:', error);
      Alert.alert('Error', 'Failed to start recording. Please check microphone permissions.');
    }
  };

  const stopRecording = async () => {
    if (!recording) return;

    try {
      setIsRecording(false);
      await recording.stopAndUnloadAsync();
      const uri = recording.getURI();

      if (uri) {
        analyzeVoice(uri);
      }

      setRecording(null);
    } catch (error) {
      console.error('Failed to stop recording:', error);
    }
  };

  const analyzeVoice = async (audioUri: string) => {
    setIsAnalyzing(true);

    try {
      // Load ML model if not loaded
      await voiceBiomarkerML.loadModel();

      // Analyze voice
      const result = await voiceBiomarkerML.analyzeVoice(audioUri);

      setAnalysisResult(result);

      // Fade in results
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 500,
        useNativeDriver: true,
      }).start();

      setIsAnalyzing(false);
    } catch (error) {
      console.error('Voice analysis failed:', error);
      setIsAnalyzing(false);
      Alert.alert('Error', 'Voice analysis failed. Please try again.');
    }
  };

  const getStressColor = (level: number): string => {
    if (level < 0.3) return '#10b981'; // Green - calm
    if (level < 0.6) return '#f59e0b'; // Orange - moderate
    return '#ef4444'; // Red - high stress
  };

  const getEmotionalStateIcon = (state: string): string => {
    switch (state) {
      case 'calm': return 'happy-outline';
      case 'stressed': return 'warning-outline';
      case 'anxious': return 'alert-circle-outline';
      case 'depressed': return 'sad-outline';
      case 'elevated': return 'trending-up-outline';
      default: return 'help-outline';
    }
  };

  const formatDuration = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <View style={styles.container}>
      {/* Header with Gradient */}
      <LinearGradient
        colors={theme.gradients.primary.colors}
        start={theme.gradients.primary.start}
        end={theme.gradients.primary.end}
        style={styles.header}
      >
        <Text style={styles.headerTitle}>Voice Biomarker Analysis</Text>
        <Text style={styles.headerSubtitle}>AI-Powered Health Insights from Your Voice</Text>
      </LinearGradient>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Recording Control Card */}
        <View style={styles.card}>
          <View style={styles.recordingContainer}>
            {/* Waveform Visualization */}
            {isRecording && (
              <View style={styles.waveformContainer}>
                {[...Array(20)].map((_, index) => (
                  <Animated.View
                    key={index}
                    style={[
                      styles.waveformBar,
                      {
                        height: waveformAnim.interpolate({
                          inputRange: [0, 1],
                          outputRange: [
                            10 + Math.sin(index) * 20,
                            30 + Math.sin(index + Date.now() / 200) * 40 + audioLevel * 50
                          ]
                        }),
                        opacity: audioLevel > 0.1 ? 0.8 : 0.3
                      }
                    ]}
                  />
                ))}
              </View>
            )}

            {/* Recording Button */}
            <TouchableOpacity
              onPress={isRecording ? stopRecording : startRecording}
              disabled={isAnalyzing}
              activeOpacity={0.8}
            >
              <Animated.View
                style={[
                  styles.recordButton,
                  { transform: [{ scale: isRecording ? pulseAnim : 1 }] }
                ]}
              >
                <LinearGradient
                  colors={isRecording ? ['#ef4444', '#dc2626'] : ['#8b5cf6', '#7c3aed']}
                  style={styles.recordButtonGradient}
                >
                  <Ionicons
                    name={isRecording ? 'stop' : 'mic'}
                    size={40}
                    color="#fff"
                  />
                </LinearGradient>
              </Animated.View>
            </TouchableOpacity>

            {/* Recording Status */}
            <Text style={styles.recordingStatus}>
              {isRecording
                ? `Recording... ${formatDuration(recordingDuration)}`
                : isAnalyzing
                ? 'Analyzing voice...'
                : 'Tap to record (5-30 seconds)'}
            </Text>

            {/* Instructions */}
            {!isRecording && !isAnalyzing && !analysisResult && (
              <View style={styles.instructionsCard}>
                <Ionicons name="information-circle" size={20} color="#8b5cf6" />
                <Text style={styles.instructionsText}>
                  Speak naturally for 5-30 seconds. Our AI will analyze stress, emotions, and respiratory health from your voice.
                </Text>
              </View>
            )}
          </View>
        </View>

        {/* Loading Indicator */}
        {isAnalyzing && (
          <View style={styles.loadingCard}>
            <ActivityIndicator size="large" color="#8b5cf6" />
            <Text style={styles.loadingText}>Analyzing voice biomarkers...</Text>
            <Text style={styles.loadingSubtext}>Extracting acoustic features and running ML inference</Text>
          </View>
        )}

        {/* Analysis Results */}
        {analysisResult && !isAnalyzing && (
          <Animated.View style={[styles.resultsContainer, { opacity: fadeAnim }]}>
            {/* Overall Health Score */}
            <View style={styles.card}>
              <Text style={styles.sectionTitle}>Overall Voice Health</Text>

              <View style={styles.scoreContainer}>
                <View style={styles.scoreCircle}>
                  <LinearGradient
                    colors={[getStressColor(analysisResult.stressLevel), getStressColor(analysisResult.stressLevel) + 'CC']}
                    style={styles.scoreGradient}
                  >
                    <Text style={styles.scoreValue}>
                      {Math.round((1 - analysisResult.stressLevel) * 100)}
                    </Text>
                    <Text style={styles.scoreUnit}>/ 100</Text>
                  </LinearGradient>
                </View>

                <View style={styles.scoreDetails}>
                  <View style={styles.scoreRow}>
                    <Ionicons name="heart" size={20} color="#ef4444" />
                    <Text style={styles.scoreLabel}>Stress Level:</Text>
                    <Text style={[styles.scoreText, { color: getStressColor(analysisResult.stressLevel) }]}>
                      {Math.round(analysisResult.stressLevel * 100)}%
                    </Text>
                  </View>

                  <View style={styles.scoreRow}>
                    <Ionicons name={getEmotionalStateIcon(analysisResult.emotionalState)} size={20} color="#8b5cf6" />
                    <Text style={styles.scoreLabel}>Emotional State:</Text>
                    <Text style={styles.scoreText}>
                      {analysisResult.emotionalState.charAt(0).toUpperCase() + analysisResult.emotionalState.slice(1)}
                    </Text>
                  </View>

                  <View style={styles.scoreRow}>
                    <Ionicons name="fitness" size={20} color="#10b981" />
                    <Text style={styles.scoreLabel}>Voice Quality:</Text>
                    <Text style={styles.scoreText}>
                      {Math.round(analysisResult.voiceQuality * 100)}%
                    </Text>
                  </View>
                </View>
              </View>
            </View>

            {/* Detailed Metrics */}
            <View style={styles.card}>
              <Text style={styles.sectionTitle}>Health Biomarkers</Text>

              {/* Respiratory Rate */}
              <View style={styles.metricCard}>
                <View style={styles.metricHeader}>
                  <View style={styles.metricIconContainer}>
                    <Ionicons name="water" size={24} color="#3b82f6" />
                  </View>
                  <View style={styles.metricInfo}>
                    <Text style={styles.metricLabel}>Respiratory Rate</Text>
                    <Text style={styles.metricValue}>{Math.round(analysisResult.respiratoryRate)} bpm</Text>
                  </View>
                  <View style={[
                    styles.metricBadge,
                    {
                      backgroundColor: analysisResult.respiratoryRate >= 12 && analysisResult.respiratoryRate <= 20
                        ? '#d1fae5'
                        : '#fef3c7'
                    }
                  ]}>
                    <Text style={[
                      styles.metricBadgeText,
                      {
                        color: analysisResult.respiratoryRate >= 12 && analysisResult.respiratoryRate <= 20
                          ? '#065f46'
                          : '#92400e'
                      }
                    ]}>
                      {analysisResult.respiratoryRate >= 12 && analysisResult.respiratoryRate <= 20 ? 'Normal' : 'Elevated'}
                    </Text>
                  </View>
                </View>
                <View style={styles.metricBar}>
                  <View
                    style={[
                      styles.metricBarFill,
                      {
                        width: `${Math.min(100, (analysisResult.respiratoryRate / 25) * 100)}%`,
                        backgroundColor: '#3b82f6'
                      }
                    ]}
                  />
                </View>
              </View>

              {/* Cognitive Load */}
              <View style={styles.metricCard}>
                <View style={styles.metricHeader}>
                  <View style={styles.metricIconContainer}>
                    <Ionicons name="brain" size={24} color="#ec4899" />
                  </View>
                  <View style={styles.metricInfo}>
                    <Text style={styles.metricLabel}>Cognitive Load</Text>
                    <Text style={styles.metricValue}>{Math.round(analysisResult.cognitiveLoad * 100)}%</Text>
                  </View>
                  <View style={[
                    styles.metricBadge,
                    {
                      backgroundColor: analysisResult.cognitiveLoad < 0.6 ? '#d1fae5' : '#fef3c7'
                    }
                  ]}>
                    <Text style={[
                      styles.metricBadgeText,
                      {
                        color: analysisResult.cognitiveLoad < 0.6 ? '#065f46' : '#92400e'
                      }
                    ]}>
                      {analysisResult.cognitiveLoad < 0.6 ? 'Low' : 'Moderate'}
                    </Text>
                  </View>
                </View>
                <View style={styles.metricBar}>
                  <View
                    style={[
                      styles.metricBarFill,
                      {
                        width: `${analysisResult.cognitiveLoad * 100}%`,
                        backgroundColor: '#ec4899'
                      }
                    ]}
                  />
                </View>
              </View>
            </View>

            {/* AI Predictions */}
            <View style={styles.card}>
              <Text style={styles.sectionTitle}>AI Health Predictions</Text>

              {Object.entries(analysisResult.rawPredictions).map(([key, value]) => (
                <View key={key} style={styles.predictionRow}>
                  <Text style={styles.predictionLabel}>
                    {key.replace(/([A-Z])/g, ' $1').replace(/^./, (str) => str.toUpperCase())}
                  </Text>
                  <View style={styles.predictionBar}>
                    <View
                      style={[
                        styles.predictionBarFill,
                        {
                          width: `${value * 100}%`,
                          backgroundColor: value < 0.3 ? '#10b981' : value < 0.6 ? '#f59e0b' : '#ef4444'
                        }
                      ]}
                    />
                  </View>
                  <Text style={[
                    styles.predictionValue,
                    { color: value < 0.3 ? '#10b981' : value < 0.6 ? '#f59e0b' : '#ef4444' }
                  ]}>
                    {Math.round(value * 100)}%
                  </Text>
                </View>
              ))}
            </View>

            {/* Voice Features */}
            <View style={styles.card}>
              <Text style={styles.sectionTitle}>Voice Characteristics</Text>

              <View style={styles.featuresGrid}>
                <View style={styles.featureCard}>
                  <Ionicons name="musical-notes" size={24} color="#8b5cf6" />
                  <Text style={styles.featureLabel}>Pitch (Hz)</Text>
                  <Text style={styles.featureValue}>{Math.round(analysisResult.features.pitch.mean)}</Text>
                </View>

                <View style={styles.featureCard}>
                  <Ionicons name="pulse" size={24} color="#3b82f6" />
                  <Text style={styles.featureLabel}>Speaking Rate</Text>
                  <Text style={styles.featureValue}>{analysisResult.features.speakingRate.toFixed(1)} syl/s</Text>
                </View>

                <View style={styles.featureCard}>
                  <Ionicons name="timer" size={24} color="#f59e0b" />
                  <Text style={styles.featureLabel}>Pause Duration</Text>
                  <Text style={styles.featureValue}>{analysisResult.features.pauseDuration.toFixed(2)}s</Text>
                </View>

                <View style={styles.featureCard}>
                  <Ionicons name="radio" size={24} color="#10b981" />
                  <Text style={styles.featureLabel}>HNR (dB)</Text>
                  <Text style={styles.featureValue}>{analysisResult.features.harmonicToNoiseRatio.toFixed(1)}</Text>
                </View>
              </View>
            </View>

            {/* Confidence & Timestamp */}
            <View style={styles.metadataCard}>
              <View style={styles.metadataRow}>
                <Ionicons name="checkmark-circle" size={20} color="#10b981" />
                <Text style={styles.metadataText}>
                  Analysis Confidence: {Math.round(analysisResult.confidence * 100)}%
                </Text>
              </View>
              <View style={styles.metadataRow}>
                <Ionicons name="time" size={20} color="#64748b" />
                <Text style={styles.metadataText}>
                  {new Date(analysisResult.timestamp).toLocaleString()}
                </Text>
              </View>
            </View>

            {/* Analyze Again Button */}
            <TouchableOpacity
              style={styles.analyzeAgainButton}
              onPress={() => {
                setAnalysisResult(null);
                setRecordingDuration(0);
                fadeAnim.setValue(0);
              }}
            >
              <LinearGradient
                colors={theme.gradients.primary.colors}
                style={styles.analyzeAgainGradient}
              >
                <Ionicons name="refresh" size={20} color="#fff" />
                <Text style={styles.analyzeAgainText}>Analyze Again</Text>
              </LinearGradient>
            </TouchableOpacity>
          </Animated.View>
        )}
      </ScrollView>
    </View>
  );
};

