import React, { useState, useEffect } from 'react';
import {
  View,
  ScrollView,
  StyleSheet,
  RefreshControl,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
} from 'react-native';
import { Text, Card, Icon, Button, Tab, TabView } from 'react-native-elements';
import { useTheme } from 'react-native-elements';
import providerAPI, { PatientDetails } from '../services/providerAPI';

interface PatientDetailsScreenProps {
  navigation: any;
  route: {
    params: {
      patientId: string;
    };
  };
}

const PatientDetailsScreen: React.FC<PatientDetailsScreenProps> = ({ navigation, route }) => {
  const { theme } = useTheme();
  const { patientId } = route.params;
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [patient, setPatient] = useState<PatientDetails | null>(null);
  const [tabIndex, setTabIndex] = useState(0);

  useEffect(() => {
    loadPatientDetails();
  }, [patientId]);

  const loadPatientDetails = async () => {
    try {
      setLoading(true);
      const data = await providerAPI.getPatientDetails(patientId);
      setPatient(data);
    } catch (error) {
      console.error('[PatientDetails] Error loading:', error);
      Alert.alert('Error', 'Failed to load patient details');
    } finally {
      setLoading(false);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadPatientDetails();
    setRefreshing(false);
  };

  if (loading) {
    return (
      <View style={[styles.container, styles.centered]}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>Loading patient details...</Text>
      </View>
    );
  }

  if (!patient) {
    return (
      <View style={[styles.container, styles.centered]}>
        <Icon name="error-outline" type="material" size={64} color="#CCC" />
        <Text style={styles.errorText}>Patient not found</Text>
        <Button title="Go Back" onPress={() => navigation.goBack()} />
      </View>
    );
  }

  const calculateAge = (dateOfBirth: string): number => {
    const today = new Date();
    const birthDate = new Date(dateOfBirth);
    let age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-back" type="material" color={theme.colors.primary} />
        </TouchableOpacity>
        <Text h4 style={styles.headerTitle}>Patient Details</Text>
        <TouchableOpacity onPress={() => Alert.alert('Edit', 'Edit patient info (coming soon)')}>
          <Icon name="edit" type="material" color={theme.colors.primary} />
        </TouchableOpacity>
      </View>

      <ScrollView
        style={styles.scrollView}
        refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
      >
        {/* Patient Header Card */}
        <Card containerStyle={styles.headerCard}>
          <View style={styles.patientHeaderContent}>
            <View style={[styles.largeAvatar, { backgroundColor: theme.colors.primary }]}>
              <Text style={styles.largeAvatarText}>
                {patient.firstName.charAt(0)}{patient.lastName.charAt(0)}
              </Text>
            </View>
            <View style={styles.patientHeaderInfo}>
              <Text h3 style={styles.patientName}>
                {patient.firstName} {patient.lastName}
              </Text>
              <Text style={styles.patientAge}>
                {calculateAge(patient.dateOfBirth)} years old • {patient.bloodType || 'Blood type N/A'}
              </Text>
              {patient.riskScore !== undefined && (
                <View style={[styles.riskBadge, { backgroundColor: getRiskColor(patient.riskScore) }]}>
                  <Text style={styles.riskBadgeText}>Risk Score: {patient.riskScore}%</Text>
                </View>
              )}
            </View>
          </View>

          {/* Contact Information */}
          <View style={styles.contactSection}>
            <View style={styles.contactRow}>
              <Icon name="email" type="material" size={18} color="#666" />
              <Text style={styles.contactText}>{patient.email}</Text>
            </View>
            {patient.phone && (
              <View style={styles.contactRow}>
                <Icon name="phone" type="material" size={18} color="#666" />
                <Text style={styles.contactText}>{patient.phone}</Text>
              </View>
            )}
            {patient.address && (
              <View style={styles.contactRow}>
                <Icon name="location-on" type="material" size={18} color="#666" />
                <Text style={styles.contactText}>{patient.address}</Text>
              </View>
            )}
          </View>

          {/* Emergency Contact */}
          {patient.emergencyContact && (
            <View style={styles.emergencySection}>
              <Text style={styles.sectionSubtitle}>Emergency Contact</Text>
              <Text style={styles.emergencyText}>
                {patient.emergencyContact}
                {patient.emergencyPhone && ` • ${patient.emergencyPhone}`}
              </Text>
            </View>
          )}
        </Card>

        {/* Quick Actions */}
        <View style={styles.quickActionsRow}>
          <TouchableOpacity
            style={styles.quickActionBtn}
            onPress={() => navigation.navigate('AppointmentManagement', { patientId, action: 'create' })}
          >
            <Icon name="event" type="material" color={theme.colors.primary} size={24} />
            <Text style={styles.quickActionText}>Schedule</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.quickActionBtn}
            onPress={() => navigation.navigate('ClinicalNotes', { patientId, action: 'create' })}
          >
            <Icon name="note-add" type="material" color={theme.colors.primary} size={24} />
            <Text style={styles.quickActionText}>Add Note</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.quickActionBtn}
            onPress={() => navigation.navigate('PrescriptionManagement', { patientId, action: 'create' })}
          >
            <Icon name="medication" type="material" color={theme.colors.primary} size={24} />
            <Text style={styles.quickActionText}>Prescribe</Text>
          </TouchableOpacity>
        </View>

        {/* Tabs */}
        <Tab value={tabIndex} onChange={setTabIndex} indicatorStyle={{ backgroundColor: theme.colors.primary }}>
          <Tab.Item title="Overview" titleStyle={styles.tabTitle} />
          <Tab.Item title="Medical" titleStyle={styles.tabTitle} />
          <Tab.Item title="Appointments" titleStyle={styles.tabTitle} />
          <Tab.Item title="Notes" titleStyle={styles.tabTitle} />
        </Tab>

        <TabView value={tabIndex} onChange={setTabIndex}>
          {/* Overview Tab */}
          <TabView.Item style={styles.tabContent}>
            {/* Vital Signs */}
            {patient.vitalSigns && patient.vitalSigns.length > 0 && (
              <Card containerStyle={styles.card}>
                <Text h4 style={styles.cardTitle}>Recent Vital Signs</Text>
                {patient.vitalSigns.slice(0, 1).map((vital, index) => (
                  <View key={index} style={styles.vitalContainer}>
                    <View style={styles.vitalRow}>
                      <Text style={styles.vitalLabel}>Blood Pressure:</Text>
                      <Text style={styles.vitalValue}>
                        {vital.bloodPressureSystolic}/{vital.bloodPressureDiastolic} mmHg
                      </Text>
                    </View>
                    <View style={styles.vitalRow}>
                      <Text style={styles.vitalLabel}>Heart Rate:</Text>
                      <Text style={styles.vitalValue}>{vital.heartRate} bpm</Text>
                    </View>
                    <View style={styles.vitalRow}>
                      <Text style={styles.vitalLabel}>Temperature:</Text>
                      <Text style={styles.vitalValue}>{vital.temperature}°F</Text>
                    </View>
                    <View style={styles.vitalRow}>
                      <Text style={styles.vitalLabel}>Weight:</Text>
                      <Text style={styles.vitalValue}>{vital.weight} lbs</Text>
                    </View>
                    <Text style={styles.vitalDate}>
                      Recorded: {new Date(vital.recordedDate).toLocaleDateString()}
                    </Text>
                  </View>
                ))}
              </Card>
            )}

            {/* Medications */}
            {patient.medications && patient.medications.length > 0 && (
              <Card containerStyle={styles.card}>
                <Text h4 style={styles.cardTitle}>Current Medications</Text>
                {patient.medications.map((med, index) => (
                  <View key={index} style={styles.listItem}>
                    <Icon name="medication" type="material" size={20} color={theme.colors.primary} />
                    <View style={styles.listItemContent}>
                      <Text style={styles.listItemTitle}>{med.name}</Text>
                      <Text style={styles.listItemSubtitle}>{med.dosage}</Text>
                    </View>
                  </View>
                ))}
              </Card>
            )}

            {/* Allergies */}
            {patient.allergies && patient.allergies.length > 0 && (
              <Card containerStyle={styles.card}>
                <Text h4 style={styles.cardTitle}>Allergies</Text>
                <View style={styles.chipsContainer}>
                  {patient.allergies.map((allergy, index) => (
                    <View key={index} style={styles.allergyChip}>
                      <Text style={styles.allergyText}>{allergy}</Text>
                    </View>
                  ))}
                </View>
              </Card>
            )}
          </TabView.Item>

          {/* Medical Tab */}
          <TabView.Item style={styles.tabContent}>
            {/* Conditions */}
            {patient.conditions && patient.conditions.length > 0 && (
              <Card containerStyle={styles.card}>
                <Text h4 style={styles.cardTitle}>Medical Conditions</Text>
                {patient.conditions.map((condition, index) => (
                  <View key={index} style={styles.listItem}>
                    <Icon name="local-hospital" type="material" size={20} color="#F44336" />
                    <View style={styles.listItemContent}>
                      <Text style={styles.listItemTitle}>{condition.name}</Text>
                      {condition.diagnosedDate && (
                        <Text style={styles.listItemSubtitle}>
                          Diagnosed: {new Date(condition.diagnosedDate).toLocaleDateString()}
                        </Text>
                      )}
                    </View>
                  </View>
                ))}
              </Card>
            )}

            {/* Surgeries */}
            {patient.surgeries && patient.surgeries.length > 0 && (
              <Card containerStyle={styles.card}>
                <Text h4 style={styles.cardTitle}>Past Surgeries</Text>
                {patient.surgeries.map((surgery, index) => (
                  <View key={index} style={styles.listItem}>
                    <Icon name="healing" type="material" size={20} color="#FF9800" />
                    <View style={styles.listItemContent}>
                      <Text style={styles.listItemTitle}>{surgery.name}</Text>
                      <Text style={styles.listItemSubtitle}>
                        {new Date(surgery.date).toLocaleDateString()}
                      </Text>
                      {surgery.notes && <Text style={styles.listItemNotes}>{surgery.notes}</Text>}
                    </View>
                  </View>
                ))}
              </Card>
            )}

            {/* Family History */}
            {patient.familyHistory && patient.familyHistory.length > 0 && (
              <Card containerStyle={styles.card}>
                <Text h4 style={styles.cardTitle}>Family History</Text>
                {patient.familyHistory.map((history, index) => (
                  <View key={index} style={styles.listItem}>
                    <Icon name="family-restroom" type="material" size={20} color="#9C27B0" />
                    <View style={styles.listItemContent}>
                      <Text style={styles.listItemTitle}>{history.condition}</Text>
                      <Text style={styles.listItemSubtitle}>{history.relationship}</Text>
                      {history.notes && <Text style={styles.listItemNotes}>{history.notes}</Text>}
                    </View>
                  </View>
                ))}
              </Card>
            )}

            {/* Lifestyle */}
            {patient.lifestyle && (
              <Card containerStyle={styles.card}>
                <Text h4 style={styles.cardTitle}>Lifestyle Information</Text>
                <View style={styles.lifestyleGrid}>
                  {patient.lifestyle.smokingStatus && (
                    <View style={styles.lifestyleItem}>
                      <Text style={styles.lifestyleLabel}>Smoking:</Text>
                      <Text style={styles.lifestyleValue}>{patient.lifestyle.smokingStatus}</Text>
                    </View>
                  )}
                  {patient.lifestyle.alcoholConsumption && (
                    <View style={styles.lifestyleItem}>
                      <Text style={styles.lifestyleLabel}>Alcohol:</Text>
                      <Text style={styles.lifestyleValue}>{patient.lifestyle.alcoholConsumption}</Text>
                    </View>
                  )}
                  {patient.lifestyle.exerciseFrequency && (
                    <View style={styles.lifestyleItem}>
                      <Text style={styles.lifestyleLabel}>Exercise:</Text>
                      <Text style={styles.lifestyleValue}>{patient.lifestyle.exerciseFrequency}</Text>
                    </View>
                  )}
                  {patient.lifestyle.diet && (
                    <View style={styles.lifestyleItem}>
                      <Text style={styles.lifestyleLabel}>Diet:</Text>
                      <Text style={styles.lifestyleValue}>{patient.lifestyle.diet}</Text>
                    </View>
                  )}
                  {patient.lifestyle.sleepHours && (
                    <View style={styles.lifestyleItem}>
                      <Text style={styles.lifestyleLabel}>Sleep:</Text>
                      <Text style={styles.lifestyleValue}>{patient.lifestyle.sleepHours} hrs/night</Text>
                    </View>
                  )}
                </View>
              </Card>
            )}
          </TabView.Item>

          {/* Appointments Tab */}
          <TabView.Item style={styles.tabContent}>
            {patient.appointments && patient.appointments.length > 0 ? (
              patient.appointments.map((appointment, index) => (
                <Card key={index} containerStyle={styles.card}>
                  <View style={styles.appointmentHeader}>
                    <Text style={styles.appointmentDate}>
                      {new Date(appointment.appointmentDate).toLocaleDateString()} at {appointment.appointmentTime}
                    </Text>
                    <View style={[styles.statusBadge, { backgroundColor: getStatusColor(appointment.status) }]}>
                      <Text style={styles.statusText}>{appointment.status}</Text>
                    </View>
                  </View>
                  <Text style={styles.appointmentType}>{appointment.type}</Text>
                  {appointment.reason && <Text style={styles.appointmentReason}>{appointment.reason}</Text>}
                  {appointment.notes && <Text style={styles.appointmentNotes}>{appointment.notes}</Text>}
                </Card>
              ))
            ) : (
              <Text style={styles.emptyText}>No appointments recorded</Text>
            )}
          </TabView.Item>

          {/* Clinical Notes Tab */}
          <TabView.Item style={styles.tabContent}>
            {patient.clinicalNotes && patient.clinicalNotes.length > 0 ? (
              patient.clinicalNotes.map((note, index) => (
                <Card key={index} containerStyle={styles.card}>
                  <View style={styles.noteHeader}>
                    <Text style={styles.noteType}>{note.noteType}</Text>
                    <Text style={styles.noteDate}>
                      {note.createdAt && new Date(note.createdAt).toLocaleDateString()}
                    </Text>
                  </View>
                  {note.chiefComplaint && (
                    <View style={styles.noteSection}>
                      <Text style={styles.noteSectionTitle}>Chief Complaint:</Text>
                      <Text style={styles.noteSectionText}>{note.chiefComplaint}</Text>
                    </View>
                  )}
                  <View style={styles.noteSection}>
                    <Text style={styles.noteSectionTitle}>Assessment:</Text>
                    <Text style={styles.noteSectionText}>{note.assessment}</Text>
                  </View>
                  <View style={styles.noteSection}>
                    <Text style={styles.noteSectionTitle}>Plan:</Text>
                    <Text style={styles.noteSectionText}>{note.plan}</Text>
                  </View>
                </Card>
              ))
            ) : (
              <Text style={styles.emptyText}>No clinical notes recorded</Text>
            )}
          </TabView.Item>
        </TabView>
      </ScrollView>
    </View>
  );
};

const getRiskColor = (riskScore: number): string => {
  if (riskScore >= 70) return '#F44336';
  if (riskScore >= 40) return '#FF9800';
  return '#4CAF50';
};

const getStatusColor = (status: string): string => {
  switch (status) {
    case 'confirmed': return '#4CAF50';
    case 'scheduled': return '#2196F3';
    case 'in-progress': return '#FF9800';
    case 'completed': return '#9E9E9E';
    case 'cancelled': return '#F44336';
