              onPress={() => setIsLogoutVisible(false)}
              type="outline"
              buttonStyle={styles.overlayButton}
              titleStyle={styles.overlayButtonText}
              containerStyle={[styles.overlayButtonContainer, { marginRight: 10 }]}
