import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  TextInput,
  Platform,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import Icon from 'react-native-vector-icons/MaterialIcons';
import { useNavigation } from '@react-navigation/native';
import DateTimePicker from '@react-native-community/datetimepicker';

interface HealthMetric {
  id: string;
  name: string;
  icon: string;
  unit: string;
  category: 'vitals' | 'activity' | 'nutrition' | 'mental';
}

const healthMetrics: HealthMetric[] = [
  { id: 'weight', name: 'Weight', icon: 'monitor-weight', unit: 'kg', category: 'vitals' },
  { id: 'blood_pressure', name: 'Blood Pressure', icon: 'favorite', unit: 'mmHg', category: 'vitals' },
  { id: 'heart_rate', name: 'Heart Rate', icon: 'favorite-border', unit: 'bpm', category: 'vitals' },
  { id: 'blood_glucose', name: 'Blood Glucose', icon: 'opacity', unit: 'mg/dL', category: 'vitals' },
  { id: 'temperature', name: 'Temperature', icon: 'thermostat', unit: '°C', category: 'vitals' },
  { id: 'steps', name: 'Steps', icon: 'directions-walk', unit: 'steps', category: 'activity' },
  { id: 'exercise', name: 'Exercise Duration', icon: 'fitness-center', unit: 'min', category: 'activity' },
  { id: 'sleep', name: 'Sleep Duration', icon: 'bedtime', unit: 'hours', category: 'activity' },
  { id: 'water', name: 'Water Intake', icon: 'local-drink', unit: 'ml', category: 'nutrition' },
  { id: 'calories', name: 'Calories', icon: 'restaurant', unit: 'kcal', category: 'nutrition' },
  { id: 'mood', name: 'Mood Rating', icon: 'sentiment-satisfied', unit: '/10', category: 'mental' },
  { id: 'stress', name: 'Stress Level', icon: 'psychology', unit: '/10', category: 'mental' },
];

const LogHealthDataScreen: React.FC = () => {
  const navigation = useNavigation();
  const [selectedCategory, setSelectedCategory] = useState<'vitals' | 'activity' | 'nutrition' | 'mental'>('vitals');
  const [selectedMetric, setSelectedMetric] = useState<HealthMetric | null>(null);
  const [value, setValue] = useState('');
  const [systolic, setSystolic] = useState('');
  const [diastolic, setDiastolic] = useState('');
  const [date, setDate] = useState(new Date());
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [notes, setNotes] = useState('');

  const categories = [
    { id: 'vitals' as const, name: 'Vitals', icon: 'favorite' },
    { id: 'activity' as const, name: 'Activity', icon: 'directions-run' },
    { id: 'nutrition' as const, name: 'Nutrition', icon: 'restaurant' },
    { id: 'mental' as const, name: 'Mental', icon: 'psychology' },
  ];

  const filteredMetrics = healthMetrics.filter(m => m.category === selectedCategory);

  const handleSave = () => {
    if (!selectedMetric || (!value && selectedMetric.id !== 'blood_pressure')) {
      alert('Please select a metric and enter a value');
      return;
    }

    if (selectedMetric.id === 'blood_pressure' && (!systolic || !diastolic)) {
      alert('Please enter both systolic and diastolic values');
      return;
    }

    // TODO: Save to backend
    const dataToSave = {
      metric: selectedMetric.id,
      value: selectedMetric.id === 'blood_pressure' ? `${systolic}/${diastolic}` : value,
      date: date.toISOString(),
      notes,
    };

    console.log('Saving health data:', dataToSave);
    alert('Health data saved successfully!');

    // Reset form
    setValue('');
    setSystolic('');
    setDiastolic('');
    setNotes('');
    setSelectedMetric(null);
  };

  return (
    <SafeAreaView style={styles.container} edges={['top']}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-back" size={24} color="#333" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Log Health Data</Text>
        <View style={{ width: 40 }} />
      </View>

      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        {/* Category Selection */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Select Category</Text>
          <View style={styles.categoriesContainer}>
            {categories.map((category) => (
              <TouchableOpacity
                key={category.id}
                style={[
                  styles.categoryCard,
                  selectedCategory === category.id && styles.categoryCardActive,
                ]}
                onPress={() => {
                  setSelectedCategory(category.id);
                  setSelectedMetric(null);
                }}
              >
                <Icon
                  name={category.icon}
                  size={32}
                  color={selectedCategory === category.id ? '#667eea' : '#8E8E93'}
                />
                <Text
                  style={[
                    styles.categoryName,
                    selectedCategory === category.id && styles.categoryNameActive,
                  ]}
                >
                  {category.name}
                </Text>
              </TouchableOpacity>
            ))}
          </View>
        </View>

        {/* Metric Selection */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Select Metric</Text>
          <View style={styles.metricsContainer}>
            {filteredMetrics.map((metric) => (
              <TouchableOpacity
                key={metric.id}
                style={[
                  styles.metricCard,
                  selectedMetric?.id === metric.id && styles.metricCardActive,
                ]}
                onPress={() => setSelectedMetric(metric)}
              >
                <Icon
                  name={metric.icon}
                  size={24}
                  color={selectedMetric?.id === metric.id ? '#667eea' : '#8E8E93'}
                />
                <Text
                  style={[
                    styles.metricName,
                    selectedMetric?.id === metric.id && styles.metricNameActive,
                  ]}
                >
                  {metric.name}
                </Text>
                <Text style={styles.metricUnit}>{metric.unit}</Text>
              </TouchableOpacity>
            ))}
          </View>
        </View>

        {/* Data Entry Form */}
        {selectedMetric && (
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Enter Value</Text>

            {selectedMetric.id === 'blood_pressure' ? (
              <View style={styles.bloodPressureContainer}>
                <View style={styles.bpInputContainer}>
                  <Text style={styles.bpLabel}>Systolic</Text>
                  <TextInput
                    style={styles.bpInput}
                    value={systolic}
                    onChangeText={setSystolic}
                    placeholder="120"
                    keyboardType="numeric"
                    placeholderTextColor="#C7C7CC"
                  />
                </View>
                <Text style={styles.bpSeparator}>/</Text>
                <View style={styles.bpInputContainer}>
                  <Text style={styles.bpLabel}>Diastolic</Text>
                  <TextInput
                    style={styles.bpInput}
                    value={diastolic}
                    onChangeText={setDiastolic}
                    placeholder="80"
                    keyboardType="numeric"
                    placeholderTextColor="#C7C7CC"
                  />
                </View>
              </View>
            ) : (
              <View style={styles.inputContainer}>
                <TextInput
                  style={styles.input}
                  value={value}
                  onChangeText={setValue}
                  placeholder={`Enter ${selectedMetric.name.toLowerCase()}`}
                  keyboardType="numeric"
                  placeholderTextColor="#C7C7CC"
                />
                <Text style={styles.inputUnit}>{selectedMetric.unit}</Text>
              </View>
            )}

            {/* Date/Time Picker */}
            <TouchableOpacity
              style={styles.dateButton}
              onPress={() => setShowDatePicker(true)}
            >
              <Icon name="event" size={20} color="#667eea" />
              <Text style={styles.dateText}>
                {date.toLocaleDateString()} {date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </Text>
            </TouchableOpacity>

            {showDatePicker && (
              <DateTimePicker
                value={date}
                mode="datetime"
                display={Platform.OS === 'ios' ? 'spinner' : 'default'}
                onChange={(event, selectedDate) => {
                  setShowDatePicker(Platform.OS === 'ios');
                  if (selectedDate) {
                    setDate(selectedDate);
                  }
                }}
              />
            )}

            {/* Notes */}
            <TextInput
              style={styles.notesInput}
              value={notes}
              onChangeText={setNotes}
              placeholder="Add notes (optional)"
              placeholderTextColor="#C7C7CC"
              multiline
              numberOfLines={4}
              textAlignVertical="top"
            />

            {/* Save Button */}
            <TouchableOpacity style={styles.saveButton} onPress={handleSave}>
              <Icon name="check" size={20} color="white" />
              <Text style={styles.saveButtonText}>Save Entry</Text>
            </TouchableOpacity>
          </View>
        )}
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F7',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: 16,
    paddingVertical: 12,
    backgroundColor: 'white',
    borderBottomWidth: 1,
    borderBottomColor: '#E5E5EA',
  },
  backButton: {
    width: 40,
    height: 40,
    alignItems: 'center',
    justifyContent: 'center',
  },
  headerTitle: {
